css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("lupuslive.yooco.de") {

#tokstaAds a, #tokstaAds span,#ads_textlinks,#ads_right,#head_left img{visibility:hidden!important;}

#head{
background-color:yellowgreen!important;
background-image: url(http://static.yooco.de/s3/images/profile/small/39/49/5a09ae8502fcbbf3b8fbbf203ea0c62c.png)!important;
background-repeat:no-repeat;
}

body,#content{background-color:yellowgreen!important}

body{
background-image: url(http://www.blumen-possienke.de/images/header7.jpg);
background-repeat:no-repeat;}

#main_container{background-color:transparent!important}

#container,#breadcrumb{background-color:gainsboro!important}

.left,strong,h2 a,#breadcrumb a,#ads_textlinks a,.forum_postbody_small a,.small{color:saddlebrown!important}

#mainbar{
background-image: none!important;
background-color:burlywood!important;
}

#mainbar li a:hover { background-color: gainsboro!important}
#mainbar li a:active { background-color: red!important}
.active{ background-color: #B28BB3!important}


.highlight{background-color:red!important}
.intro,.imCenter{color:black!important}
}
