css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("4chan.org") {

__________________________________________


html,body {
font-size:20px !important;
background-color:#000000 !important;
background-image:url('http://oi54.tinypic.com/okszro.jpg') !important;
background-position: right bottom !important;
background-repeat:no-repeat !important;
background-attachment:fixed !important;
color:#FFFFFF !important;
}

#recent-images li{ background: #1c1c1c !important; }
#header { width: 100% !important; }
#navtop { font: 12px Calibri !important; border-bottom: 1px solid #1c1c1c; top: 0;
padding: 0; width: 100% !important; padding: 5px !important; background: #101010 !important;
 color: #8899AA !important; text-align: center !important; z-index: 9 }

.logo font b span{color:#FFFFFF !important;font-size: 200% !important;}

SPAN[style="left: 5px; position: absolute;"] {display:none !important;}
center font[color="red"]{display:none !important;}
TABLE[align="right"] {display:none !important;}
BODY TABLE[width="100%"] { padding: 0 !important; padding-left: 10px !important; padding-right: 10px !important; margin-bottom: -1px;}
BODY TABLE[width="100%"] TH { padding: 8px !important; !important;  }

.postarea { padding: 0 !important; opacity: .90 !important; }
.postarea form div {border: none !important;}
.postarea input{ }
.postarea form { border-bottom: 1px solid #212121; padding-bottom: 18px !important;}
.postarea table { margin-top: 0px; margin-bottom: -10px; }
.postblock { border: 0 !important; background: transparent !important; color: #aaa !important; }
a { color: #efefef !important; !important; text-decoration: none !important; border: 0 !important; }
a:hover { color: #bb0000 !important; background: #222 !important; }

th {background-color:#333333 !important;opacity:.90 !important;-moz-border-radius:10px !important;}
td.reply {-moz-border-radius:0px !important;opacity:.90 !important;background-color:#333333 !important;color:#FFFFFF !important;
border:solid .5px #FFFFFF !important;padding: 0 20px 16px 0 !important}

td.replyhl {-moz-border-radius:0px !important;opacity:.90 !important;background-color:#333333 !important;color:#FFFFFF !important;
border:solid .5px #FFFFFF !important;padding: 0 20px 16px 0 !important}
.pages { background: #222 !important; border: 0 !important;  width: 100% !important; text-align: center !important; }
.pages td, .pages td b {font: 12px Calibri !important; color: #666 !important; center !important;  }

.postername, .abbr { color: #599 !important; }
.commentpostername { color: #999 !important; }
.postertrip { color: #688 !important; }
.filetitle { color: #6666ff !important; }
a img { clear: both !important; margin: 0 10px 10px 10px !important; border: 1px solid #AAA !important; }
.deletebuttons input:not([type="checkbox"]) { width: 130px !important; display: inline !important; }
.deletebuttons input[type="checkbox"] { position: relative; top: 3px; }
.quotejs { visibility: visible;   }

.replytitle{ color: #6666ff !important; }

.deletebuttons{ visibility: hidden; }
/*body form input {display: none !important; }*/

body form { font: 12px Calibri !important; }
td {font: 12px Calibri !important;} 

.omittedposts {color: #777 !important; visibility: visible !important; margin-left: 10px;}
.filesize {  padding-left: 2px; display: inline-block; padding-top: 3px; padding-bottom: 6px; }

/* TR BL */
body form blockquote { background: transparent ; padding: 10px; margin: 8px 2px 8px 20px;}
td blockquote { background: transparent !important; margin: 0 !important; padding: 5px !important;  }
form td{padding: 2px 6px !important}



 #navtopr, #hd, #ft, hr, td small, .doubledash, #footer, .rules, center font h3, center font small, center small font { display: none !important; }


/*4chan extensions*/
img[class*="4chan_ext"]{
display: inline;
margin: 0 !important; 
border: 0 !important;
}

img.thumb,img[src*="/thumb/"] {
background:transparent !important;
border:solid .5px #FFFFFF !important;
-moz-border-radius:0px !important;
opacity:.90 !important;
z-index:3 !important;
}

div[id*="twi_table"]{


}

.reply{ 
background: transparent !important;

border: 1px solid #444 !important;
color: #EEE !important;
font: 12px Calibri !important;
}

.postername , .postertrip, .filetitle, .commentpostername{
font: 12px Calibri !important;
}

.reply[id*="ext"]{ 
padding: 1px !important;
}

div[id*="twi_table"] .postblock{
font: 12px Calibri !important;
padding: 3px !important;
}

form[id*="4chan_qr_form"]{
width: auto !important;
padding: 5px;

}

span[class*="4chan_win_header_buttons"] img{
display: inline;
padding: 1px;
}
/*stuff*/

/*Center Images When you look at them*/
img{ display: block; margin: 0 auto;}

/*Remove Ads*/
img[src*="http://static.4chan.org/"]{
display:none !important;
}

}

/*even more stuff*/
/*** FRONTPAGE ***/
@-moz-document url(http://www.4chan.org/){

HTML > BODY {padding: 10px 0px !important; color: #dddddd; }

#hd, #close-announce
  { display: none !important;
background: #222222 !important; color: #dddddd;
}

.boxbar, #preview-tooltip-nws H2, #preview-tooltip-ws H2
  {  background: 101010 !important; color: #dddddd;
    text-align: center !important;
    color: #bb0000 !important;
    border-bottom: 1px solid rgba(0,0,0,.2) !important; }
#announce .boxcontent
  { text-align: center !important; background: #222222 !important; color: #dddddd; }
.box-outer, #preview-tooltip-nws, #preview-tooltip-ws
  {  background: #222222 !important; color: #dddddd;
    border-width: 12px !important;
}
.menubutton { background-image: none !important; background: #222222 !important; color: #dddddd; }
#filter-button, #option-button
  { letter-spacing: 0px !important;
    padding : 0px !important;
    margin: 0 !important; 
 }
#filter-button:hover, #option-button:hover
  { opacity: .7 !important;
 }
.yui-menu-shadow
  { display: none !important; }

#recent-images LI
  { background: none !important;  background: #222222 !important; color: #dddddd; }
#boards .column
  { 
width: 12em !important;
    text-transform: lowercase !important;
 background: #222222 !important; color: #dddddd;
 }
.column > H3, .p_postername
  { color: #bb0000 !important; }

.box-outer.right-box, .box-outer.left-box,.box-outer.top-box
  {  background: #222222 !important; color: #dddddd; }

.box-outer, #preview-tooltip-nws, #preview-tooltip-ws
  {  background: #222222 !important; color: #dddddd;
    border: 5px solid transparent !important;
}

#preview-tooltip-nws, #preview-tooltip-ws{
 background: #222222 !important; color: #dddddd;
    padding: 5px !important;
}

.boxcontent img{
 background: #222222 !important; color: #dddddd;
    margin: 0 auto !important; 
}

}

@-moz-document url-prefix(http://www.4chan.org/framesnav){

BODY
  { 
font: 11px verdana !important;
 }
#logo
  { background: none !important; 
height: 0 !important;
padding-bottom: 5px !important;
}
.plus
  { background: #DDD !important;
    border: none !important; }
H2
  { background: #101010 !important;
    color: #FFF !important; }
LI:hover
  { text-decoration: none !important;
}
li{
color: #6666ff !important;
padding-left: 5px !important;
padding-right: 5px !important;
}

}
