css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

/** Category Background Color **/

#styles-container #styles [anonid='heading']{
background: #efefef !important;}

/** END Category Background Color **/

/*******************************************************************************************************/

/** Category Text **/

/** Sort Enabled **/
/** Enabled **/
#styles [anonid='heading'] [label='Enabled'] {
font-weight: bold !important;
font-size: 14px!important;
color: green !important;
}

/** Disabled **/
#styles [anonid='heading'] [label='Disabled'] {
font-weight: bold !important;
font-size:14px!important;
color: #ff0000 !important
}
/** END Sort Enabled **/


/** Sort Type **/
/** Application **/
#styles [anonid='heading'] [label='Application'] {
font-weight: bold !important;
font-size:14px!important;
color: #00ee00 !important;
}


/** Global **/
#styles [anonid='heading'] [label='Global'] {
font-weight: bold !important;
font-size:14px!important;
color: #0000ee !important;
}

/** Site **/
#styles [anonid='heading'] [label='Site'] {
font-weight: bold !important;
font-size:14px!important;
color: #ee0000 !important;
}
/** END Sort Type **/


/** Sort Tag **/
/** No tags **/
#styles [anonid='heading'] [label='No tags'] {
font-weight: bold !important;
font-size:14px!important;
color: purple !important;
}
/** END Sort Tag **/

/** If you create tags for any of your styles you can change them aswell just copy the following code and paste it in the "Sort Tags" section
Remember to change YOURTAG to your tag and yes it is CaSe SenSitive!

#styles [anonid='heading'] [label='YOURTAG'] {
font-weight: bold !important;
font-size:14px!important;
color: Green !important;
} 
**/

/** END Category Text **/

/*******************************************************************************************************/

/* Style's Text Color */

/* Enabled */
#styles richlistitem:not([is-disabled='true']):not([selected='true']) {
color: darkgreen !important;
}

/* Disabled */
#styles [is-disabled='true'] {
color: darkred !important;
}

/* END Style's Text Color */

/*******************************************************************************************************/

/* Compressed UI */

/* Move Icons Left */
#styles richlistitem:not([selected='true']){
margin-left: -5px !important;
}

/* Smush Text */
#styles richlistitem:not([selected='true']){
padding-top: 3px !important;
padding-bottom: 0px !important;
margin-top: -5px !important;
margin-bottom: -5px !important;
}

/* Move Enabled Left */
#styles richlistitem:not([is-disabled='true']) label{
margin-left: -5px !important;
}

/* Remove Disabled Icons */
#styles richlistitem[is-disabled='true']:not([selected='true']) image[anonid="status-icon"]{
display: none  !important;
}

/*Move Disabled Right */
#styles richlistitem[is-disabled='true']:not([selected='true'])
{margin-left: 8px !important;}

/* Checkmark Enabled */
#styles richlistitem:not([selected='true']) image[anonid="status-icon"]{
list-style-image: url("data:image/png;base64,") !important;
margin-top: -7px !important;
}

/* Checkmark Enabled Selected */
#styles richlistitem[selected='true'] image[anonid="status-icon"]{
list-style-image: url("data:image/png;base64,") !important;
margin-top: -7px !important;
}

/* remove Disable Selected Checkmark */
#styles richlistitem[is-disabled='true'][selected='true'] image[anonid="status-icon"] { 
display: none !important;
}

/* Remove Lines */
#styles :not([selected='true']){
border:none !important;
}
#styles [selected='true']{
border:none !important;
}

/* Move Selected Enabled Checkmark Left */
#styles richlistitem[selected='true'] image[anonid="status-icon"]{
margin-left: -7px !important;
}

/* Text And Highlight Of Selected Style */
  #styles richlistitem[selected='true'] 
{
color: blue !important;
 background: #efefef !important;
}

/* Remove Sort By: */
#styles-container{
color: transparent !important;
}

/* WIDEN SEARCHBOX */
#styles-filter{
margin-right: -50px !important;
}

/* END Compressed UI */

/*******************************************************************************************************/

/* END Highlight */
#styles richlistitem:hover
{
color: #FF00ee !important;
}
/* END Highlight */
