/**
 * Wikipedia Minimal
 * Copyright 2010-2011 Tom Vincent <http://www.tlvince.com/contact/>
 *
 * See: https://github.com/tlvince/userstyles/tree/master/wikipedia
 */
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain(wikipedia.org), domain(wikibooks.org),
    domain(wikiversity.org), domain(wiktionary.org), domain(wikiquote.org),
    domain(wikisource.org), domain(wikimedia.org), domain(wikinews.org), 
    domain(uncyclopedia.org) {
    /* Global page styles */
    body {
        font-size: 1.2em !important;
        background: none !important;
    }
    /* Centre the main content and fit to around 12 words per line */
    #content {
        max-width: 24em;
        margin-left: auto !important;
        margin-right: auto !important;
        background-image: none !important;
        padding: 0 0 1em !important;
    }
    /* Main page cleaning */
    .noprint, .editsection, #footer, #centralNotice, .metadata {
        display: none;
    }
    /* Add a scroll bar for elements that are likely to be too wide */
    pre {
        overflow: auto;
    }
    /* Shrink references */
    .reference {
       font-size: 0.5em !important;
    }
    /* Add padding for the scroll bar */
    pre {
        padding: 0 0 0.5em !important;
    }
    /* Centre most non-textual elements */
    .metadata, .thumb, .thumbinner, table, .quotebox {
        float: none !important;
        margin: auto !important;
    }
    /* Non-article text */
    .infobox, .mbox-text, .thumb, #toc {
        font-size: 0.8em !important;
    }
    /* Expand to the width of the main content */
    .quotebox {
         width: auto;
    }
    /* http://en.wikipedia.org/wiki/Help:Reference_display_customization */
    /* Disable columns for {{reflist}} and {{refbegin}} */
    .references-column-count, .references-column-width {
        column-count: 1 !important;
        column-width: auto !important;
        -moz-column-count: 1 !important;
        -moz-column-width: auto !important;
        -webkit-column-count: 1 !important;
        -webkit-column-width: auto !important;
    }
}
