css:
@namespace url(http://www.w3.org/1999/xhtml); 
@-moz-document domain("freekick.org") { 

body { 
    background-color: #252 !important; 
} 



.width { 
   background-color: #252 !important; 
   margin: 0 auto !important; 
   width: 1000px !important; 
    
} 
.green-head{ 
   background-image: none !important; 
   background-color: #030 !important; 
} 
.red-head{ 
   background-image: none !important; 
   background-color: #030 !important; 
    
} 
.blue-head{ 
   background-image: none !important; 
   background-color: #030 !important; 


    
} 
.green-title{background-image: none !important;} 
.red-title{background-image: none !important;} 
.blue-title{background-image: none !important;} 
.green-title a, .red-title a, .blue-title a{ 
   color:#fff !important; 
} 
.green-crn-re{background-image: none !important;} 
.red-crn-re{background-image: none !important;} 
.blue-crn-re{background-image: none !important;} 
.red-crn-r{background-image: none !important;} 
.green-crn-r{background-image: none !important;} 
.blue-crn-r{background-image: none !important;} 

a, .link
  {
    color: #fff !important;
  }



#main-green-wrap{                
   background-image: none !important; 
   background-color: #bca !important;
} 
#green-round{ 
   background-image: none !important; 
   height: 10px !important; 
} 

#white-content{ 
   background-color:#474 !important; 
   background-image: none !important; 
} 
.rigth-crn{ 
   background-color:#484 !important; 
   background-image: none !important; 
   background-repeat: repeat-y !important; 
      
} 
.btm-shadow{ 
   background-color:#484 !important; 
   background-image: none !important; 
   width: 400px !important;    
} 

.section-head{ 
   color:#fff !important; 
} 
.red-stripe{ 
   background-image:none !important; 
   background-color:#030 !important; 
} 
#footer-tabs span{ 
   background-image: none !important; 
} 


.odd, tr.odd td { 
   background-color: #060 !important; 
}    
.even, tr.even td { 
   background-color: #050 !important; 
} 
.odd a, tr.odd td a, .even a, tr.even td a { 
   color: #fff !important; 
} 







table th{ 
   background-color:#464 !important; 
   color: #fff !important; 
} 






.dotted, table.dotted tr th , table.dotted tr td, table.dotted-header tr th , table.achievers tr th , table.achievers tr td, table.transfers tr th , table.transfers tr td, table.moments tr th , table.moments tr td, table.clubnews tr th , table.clubnews tr td, table.standard-list tr th, table.standard-list tr td, .players td, .players th { 
background-image: none !important;  
background-image:none !important; 

   border-bottom: 1px dotted #696 !important; 
} 
#main-green-wrap { 
   color: #fff !important; 
} 
#content-l a{ 
   color: #fff !important; 
} 
#content-r a{ 
   color: #fff !important; 
} 
#blue-bar{      
   background-image: none !important; 
   background-color: #030 !important; 
   height:24px !important; 
   } 
#blue-nav{ 
   background-image: none !important; 
   } 
#blue-bar a{ 
   color:#fff !important; 
} 
#lang-select{ 
   background-image:none !important; 
   background-color: #363 !important; 
   cursor: default !important ; 
} 
#menu-left{ 
   background-image:none !important; 
   background-color: #ddd !important; 
   margin-top:1px !important; 
   width: 160px !important; 
} 
#inner-menu{    
   background-image: none !important; 
   background-color: #ddd !important; 
} 
#menu-left2{ 
   background-image:none !important; 
   background-color: #ddd !important; 
   width: 160px !important; 
} 
#inner-menu2{    
   background-image: none !important; 
   background-color: #ddd !important; 
} 
.menu-btm{ 
   background-image: none !important; 
   background-color: #ddd !important; 
} 
/** club name on left menu ****/ 
.club-name a{ 
   color:#030 !important; 
} 
.settings-header { 
   color:#030 !important; 
} 
.status-right{ 
   color:#fff !important; 
} 

























/*****   MENU & STATUS   *****/

  .menu-sprite
  {
    background-image: none !important;
    background-color: #040 !important;
  }

  .menu-header
  {
    -moz-border-radius-topright: 5px;
  }

  .menu-bottom
  {
    -moz-border-radius-bottomright: 5px;
  }

  #left-wrap
  {
    margin-top: 12px !important;
  }

  #left-wrap .green-stripe
  {
    background-image: none !important;
    display: none !important;
  }

  #left-wrap .club-name, #left-wrap .settings-header, #left-wrap .title-menu-l, #left-wrap .login-header
  {
    border-bottom: 3px double #FF7700 !important;
    padding-top: 10px !important;
    padding-bottom: 5px !important;
    color: #FFFFFF !important;
    text-align: center !important;
    font-size: 11px !important;
  }

  #left-wrap .club-name a
  {
    color: inherit !important;
  }

  .makeMenu li
  {
    background-image:none !important;
    border-bottom: 1px dotted #008800 !important;
    padding-bottom: 3px !important;
    padding-top: 4px !important;
  }

  .makeMenu li a, .menu-status li a
  {
    color: #DDDDDD !important;
    font-weight: bold !important;
    border-left: 3px solid #FF7700 !important;
  }

  .makeMenu li a:visited, .menu-status  li a:visited
  {
    color: #DDDDDD !important;
    text-decoration: none !important;
  }

  .makeMenu li ul
  {
    border:3px solid #004400 !important;
    background-color:#003300 !important;
    -moz-border-radius: 5px;
  }

  .makeMenu ul li a, .menu-status ul li a
  {
    border-left: 3px solid #FF7700 !important;
  }

  .makeMenu li:hover, .menu-status li:hover
  {
    background-color: #004400 !important;
  }

  .makeMenu li a:hover, .menu-status li a:hover 
  {
    background-color: inherit !important;
  }

  .menu-status ul
  {
    padding-left: 0 !important;
    padding-right: 0 !important;
    padding-bottom: 5px !important;
    border-bottom: 3px double #DD7700 !important;
    margin-bottom: 5px !important;
    width: 100% !important;
  }

  .menu-status-left
  {
    font-weight: bold !important;
    font-size: 9px !important;
  }

  .menu-status-right
  {
    color: #FFFFFF !important;
    font-size: 9px !important;
  }

  .on
  {
    color: #00CC00 !important;
  }

  .off
  {
    color: #991100 !important;
  }

  #left-wrap .menu-sprite.menu-body.menu-status li a
  {
    text-decoration: none !important;
  }

  #left-wrap .login a.reg
  {
    font-size: 10px !important;
    color: #FF7700 !important;
  }

  #form-login
  {
    padding-top: 5px !important;
    margin-bottom: 15px !important;
  }

  #form-login label
  {
    color: #55AA55 !important;
  }

  #form-login ul
  {
    margin-bottom: 8px !important;
  }

  #form-login ul input
  {
    width: 97% !important;
    margin: 1px auto !important;
  }

  #form-login input#username, #form-login input#password
  {
    height: 100% !important;
  }

  #form-login .login-submit
  {
    margin: 0 auto !important;
  }

  #form-login .login-submit input
  {
    margin: 0 !important;
  }

  .menu-body .dots, .menu-body .dotted
  {
    display: none;
  }

  .menu-body .reg
  {
    background-image: none !important;
    padding-left: 0 !important;
  }















/*****   MAIN CONTENT   *****/

  /***  TABS  ***/

  #main-green-wrap
  {
    margin-top: 12px !important;
    background-image: none !important;
    background-color: #002900 !important;
    -moz-border-radius: 5px;
    color: #FFFFFF !important
  }

  #green-round
  {
    background-image: none !important;
    height: 10px !important;
  }

  #main-tabs
  {
    padding-left: 15px !important;
  }

  #main-tabs li
  {
    padding:0 !important;
    background-image: none !important;
    -moz-border-radius-topright: 5px;
    -moz-border-radius-topleft: 5px;
  }

  #main-tabs li a
  {
    display: block;
    color: #444444 !important;
    padding: 4px 10px;
  }

  #main-tabs li a:hover
  {
    text-decoration: none !important;
  }

  #main-tabs li.tab-hidden
  {
    background-color: #BBBB99 !important;
    margin-bottom: 3px !important;
  }

  #main-tabs li.tab-hidden:hover
  {
    background-color: #BBC699 !important;
  }

  #main-tabs li.tab-active
  {
    padding-bottom: 2px !important;
  }

  #main-tabs li.tab-active a
  {
    color: #006600 !important;
  }












/***  PLAYERS TABS  ***/


  .players-tabs li
  {
    background-image: none !important;
    padding: 2px 2px 0px 2px !important;
  }

  .players-tabs li a
  {
    background-image: none !important;
    background-color: #bbc699 !important;
    border-right: 8px solid #bbc699 !important;
    color: #002900 !important;
    -moz-border-radius-topright: 5px;
    -moz-border-radius-topleft: 5px;
  }

  .players-tabs li.players-selected
  {
    background-image: none !important;
    background-color: #0099FF !important;
    margin: 0 2px !important;
    padding: 1px 1px 0px 1px !important;
    -moz-border-radius-topright: 5px;
    -moz-border-radius-topleft: 5px;
  }

  .players-tabs li.players-selected a
  {
    background-image: none !important;
    background-color: #fff !important;
    border-right: 8px solid #fff !important;
    color: #060 !important;
    -moz-border-radius-topright: 5px;
    -moz-border-radius-topleft: 5px;
  }



 div.players-wrap
  {
    background-color: #060 !important;
    border-style: solid !important;
    border-color: #fff !important;
    border-width: 1px 0 !important;
    -moz-border-radius: 5px;
  }

 

  tr.odd td, .odd
  {
    background-color: #006600 !important;
  }

  tr.even td, .even
  {
    background-color: #005A00 !important;
  }

  tr.odd-marked td, tr.even-marked td, .marked td
  {
    background-color: #333333 !important;
  }

  tr.greyLine td
  {
    background-color: #002200 !important;
    border: none !important;
  }

  tr.grayLine td
  {
    border-bottom: 2px solid #002200 !important;
  }












  /* skills */
  .awesome {color: #dd6611 !important;}
  .brilliant {color: #FF8833 !important;}
  .superb {color: #FF9966 !important;}
  .excellent {color: #FF99CC !important;}
  .good {color: #CC33CC !important;}
  .decent {color: #FFFFFF !important;}
  .weak {color: #33CCCC !important;}
  .poor {color: #FFFF00 !important;}
  .awful {color: #FF0000 !important;}


.age-old, .drop { color:#f71 !important; }
.age-senior { color:#FFFFFF!important; }
.age-youth-0, .abovepot { color:#33CCFF!important; }
.age-youth-1, .age-youth, .pot { color:#33CCFF!important; }
.age-youth-2 { color:#33CCFF!important; }
.age-youth-3 { color:#33CCFF!important; }
.age-youth-4, .age-youth-5 { color:#33CCFF!important; }
.age-retired { color: #F00 !important; } 




.hidden-result { color:#DFF !important; } 
a.sortval { color:#DFD !important; } 
a.sortnumeric { color:#DFD !important; } 
a.sortvaltext { color:#DFD !important; } 
a.sortcaseinsensitive { color:#DFD !important; } 
a.none-player { color: #AFA !important;} 
.income {color:#030 !important; } 
.financesMain {color:#000 !important; } 
.news-title {background-color:#272 !important; } 
.offline {color:#020 !important;} 
.online {color:#dfd !important;} 
.guestbook-title {background-color:#aca !important; } 
.message-open {background-color:#7a7 !important; } 
.answ-content {color:#444 !important;} 
.header {background-color:#7a7 !important; } 
.expense {color:#f88 !important; } 
.income {color:#8f8 !important; } 
.pos {color:#fff !important; font-weight: bold !important; } 
.even-marked { background-color: #5a5 !important; } 
.odd-marked { background-color: #7c7 !important; } 
}
