css:
@namespace url(http://www.w3.org/1999/xhtml);
@namespace /*[[name]]*/ url(http://vimperator.org/namespaces/liberator);

@-moz-document url-prefix(/*[[name]]*/://help) {

html[/*[[namespace]]*/highlight~="Help"] { 
        background-color: #2B2B2B !important;
}

body[/*[[namespace]]*/highlight~="HelpBody"] { 
	max-width: 85% !important;
        background-color: #FFFFFE !important;
        padding: .5em 2em !important;
        border-radius: 10px !important;
        box-shadow: 0 0 2px #CCCCCC;
        font: 13px/145% "Trebuchet MS", Verdana, Tahoma, Helvetica, "Lucida Grande", "DejaVu Sans", sans-serif !important; 
}

div[/*[[namespace]]*/highlight~="HelpTags"] a[/*[[namespace]]*/highlight~="HelpTag"]{
        color:  #DB8072 !important;
}

div[/*[[namespace]]*/highlight~="HelpSpec"] {
        color: #FF7F36 !important;
        margin-left: -12em !important;
}

div[/*[[namespace]]*/highlight~="HelpOptInfo"] {
        margin-left: 0 !important;
        margin-bottom: 0 !important;
}

span[/*[[namespace]]*/highlight~="HelpDefault"]{
        white-space: nowrap !important;
} 

span[/*[[namespace]]*/highlight~="HelpDefault"] span {
        white-space:pre !important;
}

pre[/*[[namespace]]*/highlight~="HelpCode"], 
html[/*[[namespace]]*/highlight~="Help"] dl {
        background-color: #F9F9F9  !important;
        border: 1px dashed #4D4D4D  !important;
        color: black !important;
        line-height: 1.1em !important;
        padding: 2px 4px !important;
        border-left: 5px solid #4D4D4D  !important;
        overflow: auto !important;
}

span[/*[[namespace]]*/highlight~="HelpEx"] a {
       font-weight: normal !important;
}

div[/*[[namespace]]*/highlight~="HelpItem"]  {
        margin: 1em 1em 1em 12em !important;
}

div[/*[[namespace]]*/highlight~="HelpDescription"] {
       margin: .5em 0 !important;
}

div[/*[[namespace]]*/highlight~="HelpTOC"]>ol{ 
       border: 1px solid #BBBBBB !important;
       background: #F0F0F0 !important;
       width: 25em !important;
       margin: .5em !important;
       padding-top: 1em !important;
       padding-bottom: 1em !important;
}

div[/*[[namespace]]*/highlight~="HelpTOC"] {
       margin-bottom: 1em !important;
}

html[/*[[namespace]]*/highlight~="Help"] a, 
html[/*[[namespace]]*/highlight~="Help"] a:visited  {
       color:#1E90FF !important;
       text-decoration: none !important;
}

html[/*[[namespace]]*/highlight~="Help"] a:hover {
       color: white !important;
       background-color:#1E90FF !important;
       text-decoration: none !important;
}

html[/*[[namespace]]*/highlight~="Help"] h2, h1 {
       padding: .5em 0 !important;
}

/*p a img[alt="Donate"] {display: none !important;}*/
}
