@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

/*............Firefox4/Strata40-Style Back/Forward Toolbar Buttons............*/

/* A userstyle by kittybella ~ meow
Used MBlue's "MBlue Nav-Buttons Vista on XP" style as a guide/base for this style 
(URL: http://userstyles.org/styles/8761.css */


/*//////////////////////BUTTON SETTINGS//////////////////////*/

toolbar[mode="icons"] #unified-back-forward-button {
-moz-appearance: none !important;
-moz-border-radius: 0px !important;
background: none !important;
list-style-image: none !important;
border: none !important;
outline: none !important;
padding: 0 !important;
}

toolbar[iconsize="small"][mode="icons"] #unified-back-forward-button {
min-height: 26px !important;
max-height: 26px !important;
}

toolbar[mode="icons"] #back-forward-dropmarker
 .toolbarbutton-menu-dropmarker {
display: none !important;
}

toolbar[mode="icons"] #back-button, 
toolbar[mode="icons"] #forward-button, 
toolbar[mode="icons"] #back-forward-dropmarker {
display: block !important;
-moz-appearance: none !important;
-moz-border-radius: 0px !important;
background: none !important;
list-style-image: none !important;
border: none !important;
outline: none !important;
padding: 0 !important;
}

toolbar[iconsize="small"][mode="icons"] #back-button {
min-height: 32px !important;
max-height: 32px !important;
min-width: 32px !important;
max-width: 32px !important;
margin-right: -1px !important;
}

toolbar[iconsize="small"][mode="icons"] #forward-button {
min-height: 32px !important;
max-height: 32px !important;
min-width: 32px !important;
max-width: 32px !important;
margin-right: 0px !important;
}

toolbar[iconsize="small"][mode="icons"] #back-forward-dropmarker {
min-height: 26px !important;
max-height: 26px !important;
min-width: 15px !important;
max-width: 15px !important;
margin-left: -2px !important;
margin-right: 0px !important;
}

/*//////////////////////SMALL BUTTONS//////////////////////*/

/*****BACK NORMAL*****/

toolbar[iconsize="small"][mode="icons"] #back-button {
background: 
url("data:image/png;base64,") no-repeat right !important;
}

/*****BACK HOVER*****/

toolbar[iconsize="small"][mode="icons"] #back-button:hover {
background: 
url("data:image/png;base64,")
no-repeat right !important;
}

/*****BACK PRESSED*****/

toolbar[iconsize="small"][mode="icons"] #back-button:hover:active {
background: 
url("data:image/png;base64,") no-repeat right !important;
}

/*****BACK DISABLED*****/

toolbar[iconsize="small"][mode="icons"] #back-button[disabled="true"] {
background: 
url("data:image/png;base64,") 
no-repeat right !important;
}



/*****FORWARD NORMAL*****/

toolbar[iconsize="small"][mode="icons"] #forward-button  {
background: 
url("data:image/png;base64,") 
no-repeat left !important;
}

/*****FORWARD HOVER*****/

toolbar[iconsize="small"][mode="icons"] #forward-button:hover  {
background: 
url("data:image/png;base64,") 
no-repeat left !important;
}

/*****FORWARD PRESSED*****/

toolbar[iconsize="small"][mode="icons"] #forward-button:hover:active  {
background: 
url("data:image/png;base64,") 
no-repeat left !important;
}

/*****FORWARD DISABLED*****/

toolbar[iconsize="small"][mode="icons"] #forward-button[disabled="true"]  {
background: 
url("data:image/png;base64,") no-repeat left !important;
}



/*****DROPMARKER*****/

toolbar[iconsize="small"][mode="icons"] #back-forward-dropmarker {
background: 
url("data:image/png;base64,") 
no-repeat left !important;
}

/*****DROPMARKER HOVER*****/

toolbar[iconsize="small"][mode="icons"] #back-forward-dropmarker:hover {
background: 
url("data:image/png;base64,")
no-repeat left !important;
}
