css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix('http://eu.battle.net/wow/en/forum/'), url-prefix('http://eu.battle.net/wow/fr/forum/'), url-prefix('http://eu.battle.net/wow/de/forum/'), url-prefix('http://eu.battle.net/wow/es/forum/'), url-prefix('http://eu.battle.net/wow/ru/forum/') ,url-prefix('http://us.battle.net/wow/en/forum/'), url-prefix('http://us.battle.net/wow/es/forum/'),url-prefix('http://eu.battle.net/wow/en/search'), url-prefix('http://eu.battle.net/wow/fr/search'), url-prefix('http://eu.battle.net/wow/de/search'), url-prefix('http://eu.battle.net/wow/es/search'), url-prefix('http://eu.battle.net/wow/ru/search') ,url-prefix('http://us.battle.net/wow/en/search'), url-prefix('http://us.battle.net/wow/es/search'), url-prefix('http://eu.battle.net/wow/en/blog/'), url-prefix('http://eu.battle.net/wow/fr/blog/'), url-prefix('http://eu.battle.net/wow/de/blog/'), url-prefix('http://eu.battle.net/wow/es/blog/'), url-prefix('http://eu.battle.net/wow/ru/blog/') ,url-prefix('http://us.battle.net/wow/en/blog/'), url-prefix('http://us.battle.net/wow/es/blog/') 

{

#blog-wrapper {background-color:#ddd!important; background-image:none!important; }
#blog, #blog-container .featured-news {background-image:none!important; background-color:#ececec!important;}


#page-comments h3 {color:#069!important;}
.comments-container {background-image:none!important; }
#page-comments #comment-form .comment-ta{ border: 1px solid #444!important;}
#page-comments .user-name .context-link {color:#069!important; }
#page-comments .comment-interior {background-color:#ececec!important; border: 1px solid #444!important;}
#page-comments .comment-actions a {background-color:#711D00!important;}
#page-comments .action a:hover {color:#ffae00!important;}
#page-comments .action a {color:#711D00!important;}
#page-comments .comment-interior .content {color:#333!important;}
#page-comments .comment-interior .time a {color:#636363!important; font-weight:normal!important; font-style:italic!important;}
#page-comments .comment-interior .time a:hover {color:#a3a3a3!important;}
#page-comments .noCharacter {color:#636363!important;}

.featured-news .featured a .featured-img { -moz-box-shadow:0 0 8px #000!important; -webkit-box-shadow:0 0 8px #000!important; box-shadow:0 0 8px #000!important; -moz-border-radius: 3px!important; }
.featured-news .featured-desc {color:#416699!important;}

#blog .blog-title { color:#069! important;}
#blog .detail {color:#333!important;}
#blog .detail strong, #blog .keyword-list strong { color:#000!important; }
#blog .byline {color:#636363!important;}
#blog a, #blog .byline .comments-link { color:#416699!important; }

#station-content .station-content-wrapper {background-color:#ddd!important; background-image:none!important; }
#station-content .station-inner-wrapper {background:none!important;}
.post-interior, .forum-link, .sidebar-content, .search-result { background-color:#ececec !important; }
#popular-topics .readmore, #forum-list .forum-parent, #station-content .coc { color:#069! important;}
#popular-topics .poptopic-list a:hover {background-color:#ececec!important; color:#069!important;}
#popular-topics .poptopic-list a:hover span{color:#416699!important;}
#station-content .coc a {color:#069!important; text-decoration:underline!important;}
#station-content .coc {background-image:none!important;}

.search-result .by-line, .search-result .result-title, .search-content {color:#333!important;}
.search-result a {color:#069!important;}
.search-result .result-title {border-bottom: 1px solid black!important;}

#posts .pageNav a:hover { color:#069!important; }

#forum-list {background-color:#ddd!important; }
.forum-link, .forum-link:hover span{color:#416699!important;}
.forum-link .desc {color:#333!important;}
.sidebar-content .title {color:#416699!important;}
.sidebar-content .desc {color:#333!important;}
.filter-show-all  {color:#333!important;}
.filter-show-all a {color:#069!important; text-decoration:underline!important}

.sidebar-content .featured-news .featured-img {margin-left:7px!important;}
.sidebar-content .featured-news .featured a {padding-left:5px!important;}
.sidebar-content .featured-news .featured .date {color:#636363!important;}
.sidebar-content a:hover {background-color:#ececec!important;}
.sidebar-content a:hover span{color:#069!important;}
.sidebar-module .sidebar-title h3, .sidebar-module .sidebar-title h3 a { color: #069!important;}
#sidebar-related .sidebar-title, #sidebar-recent .sidebar-title { margin-top: 10px!important; background-color:#ddd!important; }

#posts, #posts tr:hover, #posts-container, #post-preview  { background-color:#ececec !important; }
#posts .post-title a {color:#416699!important;}
#posts .post-title a:hover {text-decoration:underline!important;}
#posts .type-blizzard { color:blue!important;} 
#posts .post-status {color: grey!important;}
#posts tr.mvp .post-author, #page-comments .mvp .context-link { color:#091; }
#posts tr.featured { background: #ccefff!important; }

#thread .post-detail a, #post-preview a {color:#069!important; text-decoration:underline!important}
#thread .post-detail blockquote.quote-public, #post-preview blockquote.quote-public{color:#333!important;}
#thread .post-detail blockquote.quote-public div, #post-preview blockquote.quote-public div {color:#636363!important;}
#thread .post-detail code, #post-preview code {color:#636363!important; background-color:#ececec !important;}
#thread .mvp .post-detail, #page-comments .mvp .content  { color:#091!important;}
#thread .mvp .post-detail blockquote, #thread .post-detail .quote-mvp, #thread .post-detail .quote-mvp blockquote { border-color:#091!important; border-bottom-color:#83bb57; color:#091!important;}
#thread .blizzard .post-detail, #thread .blizzard .post-edited, #page-comments .blizzard .content {color: #03f !important;}
#thread .blizzard .post-detail blockquote { border-color:#03f!important; border-bottom-color:#002555; color: #03f !important;}
#thread .quote-blizzard {color:#03f!important;}

#forum-content .section-header .sticky-link .sticky-request { background-color:#1E1006!important;}

#thread .post-detail, #post-preview, #post-preview h3 {color:#333!important;}
#thread .achievements {color:yellow!important;}
#thread .post-user .character-info .user-name .context-link {color:#069!important;}
#thread .post-user .character-info .achievements { color:#aa0!important; }
#thread .post-user .character-info .color-c3 {color:#9BBF69!important;}
#thread .post-user .character-info .color-c4 {color:#aa0!important;}
#thread .post-user .character-info .color-c5 {color:#999!important;}
#thread .post-user .character-info .color-c8 {color:#3bf!important;}

#post-preview { 
margin-top:50px!important;
padding-left:10px !important;
padding-right:10px !important;
padding-top:10px !important;
}

#forum-content .forum-actions, #forum-content .section-header {background-color:#ececec!important;}
#forum-content .forum-actions.bottom {background-color:#1a0f08!important;}
#forum-content .section-header {color:#416699!important;}

.post{ 
  border-bottom: 2px solid #444!important; 
  border-top: 2px solid #444!important; 
  border-left: 3px solid #190E08!important;
  border-right: 3px solid #190E08!important;}
.post-info .postData .date, .post-edited { color:#636363; }
.post-info img {background-color:#190E08!important;}
.post-info .postData, .post-info .postData a { color:#069!important; }
.post-info .postData .highrated {color:#091!important;}
.high-rated .post-info .date { color:#636363!important; }
.post .mvp-title {color:#091!important;}

.high-rated { background-image:none!important; }

.forum-options a { color:#069!important; text-decoration:underline!important; }
div.forum-options a.active { color:#333!important; text-decoration:none!important; }
.forum-options a:hover { color:blue!important; }

.karma-feedback {background-color:#5B7382!important; color:#ddd!important;}
.karma-feedback  a {font-weight:bold}

}
