css:
@-moz-document url-prefix('http://www.myspace.com/forums/') {

.ForumPostArea {
	border: 1px solid #8ab4db!important;
	width: 505px !important;
	position: relative !important;
	padding-left: 215px !important;
} 

.ForumPostContentArea {
	background-color:#f4f4f4!important;
	border-top:1px solid #8ab4db!important;
	margin-left: -215px !important;
} 

.container > article > li, .ForumOuter > li, #forumHolder > li, #CommonBodyColumn > li, .ForumContentArea > li, 

.ForumPostList > li {
	padding: 10px !important;
	padding-top: 20px !important;
	padding-bottom: 0px !important;
	position: relative;
}

.ForumPostList > li {
	border:0px!important;
} 

.ForumPostUserPropertyList {
	width:200px!important;
	text-align:center!important;
	margin-top:5px!important;
	margin-bottom: 5px !important;
} 

.ForumPostUserName {
	font-size: 12px !important;
	font-family: verdana !important;
	padding-top: 5px !important;
	position: relative;
	z-index: 42;
} 

.ForumPostUserAttribute {
	display:block!important;
	color: #000 !important;
	font-size: 12px !important;
	font-family: verdana !important;
} 

.ForumPostListLinks {
	display:block!important;
	font-size: 9px !important;
	line-height: 12px !important;
	font-family: verdana !important;
	position: relative;
	z-index: 42;
} 

.ForumPostBodyArea {
	background-color:#fff!important;
	padding-left:5px!important;
	padding-top:5px!important;
	border-left:1px solid #8ab4db !important;
	height: 180px !important;
	width: 504px !important;
	overflow: auto !important;
	padding-bottom: 25px !important;
} 

.ForumPostUserAvatar {
	position: relative;
	z-index: 42;
}

.ForumPostUserAvatar img {
	height: auto!important;
	width: auto!important;
	max-height: 100px !important;
} 

.ForumPostHeader {
	padding: 5px !important;
	margin-bottom: 0px !important;
	background-color:#eff3ff!important;
	margin-left: -215px !important;
} 

.ForumPostHeader .postMeta {
	font-weight:bold!important;
	font-size:12px!important;
	color:#000!important;
	font-family: verdana !important;
	padding-top: 4px !important;
} 

.subForumHead {
	width: 720px !important;
	border: 0px !important;
	margin: 0px !important;
	margin-bottom: 5px !important;
} 

.ForumPagingArea {
	padding: 0px !important;
	width: 720px !important;
	margin-left: 155px !important;
	margin-top: -15px !important;
} 

.ddlSortOrder {
	margin-left: 25px !important;
} 

.Listing {
	width: 475px !important;
	text-align: center !important;
	font-size: 10px !important;
	margin: 0px !important;
	padding: 0px !important;
} 

.PrevNext {
	font-size: 10px !important;
	margin: 0px !important;
	text-align: right !important;
	padding: 0px !important;
} 

.column0 {
	width: 720px !important;
} 

.column0 a, .forgotPassword {
	color: #003399 !important;
} 

.column1, #tkn_leaderboard, .labeled_ad, .googleAdSenseModule, .AdContainerModule, .SitesGoogleAdsModule, #applead, 

.AdUnitModule, .mod.ad, .GoogleSponsoredLinksVerticalModule {
	display: none !important;
} 

.threadHeader {
	width: 940px !important;
	height: 50px !important;
	margin-top: -15px !important;
	border: 0px !important;
	position:relative;
	z-index: 42;
} 

.threadHeader h3 {
	font-size: 13px !important;
	float: left !important;
	margin-top: 18px !important;
	margin-bottom: 5px !important;
	max-width: 670px !important;
} 

.postOptions {
	position:absolute;
	width: 250px !important;
	right: 0px;
} 

.ForumReplyButton {
	border: 1px solid #8ab4db !important;
	padding-top: 2px !important;
	padding-bottom: 3px !important;
	padding-left: 5px !important;
	padding-right: 5px !important;
	background-color: #ededed !important;
	position: relative;
	z-index: 42;
} 

.ForumReplyButton:hover {
	background-color: #f4f4f4 !important;
} 

.SignatureSeperator {
	border-color: #8ab4db !important;
} 

.ForumGroupLastPostColumn {
	width: 240px !important;
	border-left: 1px solid #8ab4db !important;
	border-bottom: 1px solid #8ab4db !important;
	vertical-align: middle !important;
	padding: 2px !important;
} 

.ForumSubGroupNameColumn {
	border-left: 1px solid #8ab4db !important;
	border-bottom: 1px solid #8ab4db !important;
} 

h4 img {
	float: left !important;
	padding-right: 10px !important;
} 

.ForumNameUnRead {
	margin-left: -8px !important;
} 

.ForumMyRepliesColumn {
	border-right: 1px solid #8ab4db !important;
	border-left: 1px solid #8ab4db !important;
	border-bottom: 1px solid #8ab4db !important;
	vertical-align: middle !important;
} 

.ForumMyNameColumn {
	border-left: 1px solid #8ab4db !important;
	border-bottom: 1px solid #8ab4db !important;
	vertical-align: middle !important;
} 

.ForumGroupLastPostColumn .postMeta {
	color: #000 !important;
	font-size: 12px !important;
} 

.PostAuthorLink a {
	font-size: 11px !important;
	font-family: verdana !important;
	font-weight: normal !important;
	margin-top: 5px !important;
} 

.ForumListHeader {
	border: 1px solid #8ab4db !important;
	border-left: none !important;
	background-color: #eff3ff !important;
	text-align: center !important;
	color: #666 !important;
} 

.ForumListHeaderLeftMost {
	border: 1px solid #8ab4db !important;
	background-color: #eff3ff !important;
	text-align: center !important;
	color: #666 !important;
} 

.ForumGroupNameColumn {
	border-left: 1px solid #8ab4db !important;
	border-bottom: 1px solid #8ab4db !important;
} 

.ForumGroupTotalThreadsColumn {
	border-left: 1px solid #8ab4db !important;
	border-bottom: 1px solid #8ab4db !important;
	text-align: center !important;
} 

.ForumGroupTotalPostsColumn {
	border-left: 1px solid #8ab4db !important;
	border-right: 1px solid #8ab4db !important;
	border-bottom: 1px solid #8ab4db !important;
	text-align: center !important;
} 

.ForumSixshotOverdrive, .fakeForm {
	position:relative;
} 

.ComposeHead {
	margin-right: 115px !important;
} 

.ComposeFooter {
	position: absolute !important;
	top: 10px !important;
	left: 590px !important;
	width: 120px !important;
} 

.aPostCancel {
	background-color: #d33 !important;
	border: 1px solid #d22 !important;
	padding-left: 10px !important;
	padding-right: 10px !important;
	border-radius: 4px !important;
	-moz-border-radius: 4px !important;
} 

.aPostCancel:hover {
	background-color: #f55 !important;
} 

.subforumList a:hover {
	background-color: #8ab4db !important;
} 

.restore {
	margin-top: 8px !important;
} 

iframe.punymce {
	width: 673px !important;
} 

.tabContent textarea, .mceToolbar {
	width: 676px !important;
} 

blockquote img {
	max-width: 100px !important;
	max-height: 100px !important;
} 

blockquote object {
	width: 300px !important;
	height: 240px !important;
} 

.Signature {
	margin-top: 5px !important;
} 

#ctl00_ctl00_cpMain_bcr_PostList_ctl01_PostView_ReportAbuseDiv,
#ctl00_ctl00_cpMain_bcr_PostList_ctl02_PostView_ReportAbuseDiv,
#ctl00_ctl00_cpMain_bcr_PostList_ctl03_PostView_ReportAbuseDiv,
#ctl00_ctl00_cpMain_bcr_PostList_ctl04_PostView_ReportAbuseDiv,
#ctl00_ctl00_cpMain_bcr_PostList_ctl05_PostView_ReportAbuseDiv,
#ctl00_ctl00_cpMain_bcr_PostList_ctl06_PostView_ReportAbuseDiv,
#ctl00_ctl00_cpMain_bcr_PostList_ctl07_PostView_ReportAbuseDiv,
#ctl00_ctl00_cpMain_bcr_PostList_ctl08_PostView_ReportAbuseDiv,
#ctl00_ctl00_cpMain_bcr_PostList_ctl09_PostView_ReportAbuseDiv,
#ctl00_ctl00_cpMain_bcr_PostList_ctl10_PostView_ReportAbuseDiv,
#ctl00_ctl00_cpMain_bcr_PostList_ctl11_PostView_ReportAbuseDiv,
#ctl00_ctl00_cpMain_bcr_PostList_ctl12_PostView_ReportAbuseDiv,
#ctl00_ctl00_cpMain_bcr_PostList_ctl13_PostView_ReportAbuseDiv,
#ctl00_ctl00_cpMain_bcr_PostList_ctl14_PostView_ReportAbuseDiv,
#ctl00_ctl00_cpMain_bcr_PostList_ctl15_PostView_ReportAbuseDiv {
	right: 0px !important;
	margin-top: 10px !important;
	padding-right: 5px !important;
	position:absolute !important;
	bottom:0px !important;
	z-index: 42;
} 

.forumNav {
	background-color: #99B3CC !important;
} 

.forumNav .glue-dropdown a, .forumNav input {
	border: 1px solid #8ab4db !important;
} 

.ForumCreatePostButton {
	color: #FFF !important;
	text-shadow: none !important;
} 

.formTableList {
	margin-top: -15px !important;
	margin-left: 0px !important;
	margin-bottom: 20px !important;
} 

.lowerNav, .upperNav, .footertools, #msSubnav .nav {
	background: #0D366D !important;
} 

.lowerNav li a:hover, .lowerNav .selected a, #msSubnav li a:hover, #msSubnav .current {
	background-color: #0055B4 !important;
} 

#msSubnav .nav {
	border: none !important;
} 

#msSubnav {
	padding: 0px !important;
} 

#staticSubNavMenu, .alertsDrop {
	border: 3px solid #8ab4db !important;
}  

.staticSubNav:hover span, .userAlerts li:hover .MSIcon {
	border: 3px solid #8ab4db !important;
	border-bottom: none !important;
} 

.tray, .upperNav li.tab:hover a, .gamesSection {
	background-color: #99B3CC !important;
} 

.upperNav li:hover .tray a {
	background: none !important;
}  

.upperNav li:hover .signupSection a {
	background: #E0FF37 !important;
} 

.upperNav li:hover .signupSection a:hover {
	background: #EFFF98 !important;
} 

.search button, .ForumSearchButton {
	background-color: #24A7FF !important;
	text-shadow: none !important;
	font-weight: bold !important;
	border-color: #0893F0 !important;
} 

.search button:hover, .ForumSearchButton:hover {
	background-color: #34B7FF !important;
} 

.buddyConsoleTabHover, .consoleTabSelect, .consoleTabHover, .footertools li.trayActive {
	background: #8ab4db !important;
} 

.infoTray, .privacyTray {
	border-left: 1px dotted #000 !important;
} 

.footertools li > div:first-child {
	color: #fff !important;
} 

.footertools li section {
	border: 3px solid #8ab4db !important;
} 

.checkerLightBG {
	background: #EFF3FF !important;
} 

.onlineTray li.trayActive::after, .imTray.trayActive::after, .infoTray.trayActive::after, .privacyTray.trayActive::after {
	border-right: 3px solid #8ab4db !important;
	border-top: 3px solid #8ab4db !important;
}} 

@-moz-document url-prefix('http://www.myspace.com/forums/t/') { 

.ddlSortOrder {
	margin-left: 0px !important;
} 

.subForumHead {
	margin-bottom: 0px !important;
	padding-left: 0px !important;
	position: relative;
	z-index: 42;
} 

.ForumPostList > li:first-child {
	margin-top: -20px !important;
} 

.ForumOuter > li:first-of-type .ForumPostArea {
	position: relative;
	top: 10px;
	border: 0px !important;
	vertical-align: bottom;
}

.ForumOuter > li:first-of-type .ForumPostHeader {
	background: none !important;
}

.ForumOuter > li:first-of-type .ForumPostContentArea {
	border: 1px solid #8ab4db !important;
}

.ForumPagingArea {
	margin-top: -30px !important;
}

.Listing {
	width: 485px !important;
}

.Listing a {
	margin-right: 0px !important;
}

.PrevNext {
	font-size: 10px !important;
	margin: 0px !important;
	text-align: right !important;
	padding: 0px !important;
} 

.ForumCreatePostButton {
	color: #003399 !important;
}

.ForumPostArea a {
	color: #039 !important;
}

.forumNav .ddlForums > ul, .ddlSearchFilter > ul {
	z-index: 43 !important;
}

.postMeta a {
	position: relative;
	z-index: 42;
}

.subForumHead .ForumPagingArea {
	margin-top: 0px !important;
	margin-left: 10px !important;
}

.subForumHead .Listing {
width: 625px !important;
}} 

@-moz-document url-prefix('http://www.myspace.com/forums/mytopics') { 

.ForumMyImageColumn {
	border-bottom: 1px solid #8ab4db !important;
	border-left: 1px solid #8ab4db !important;
	width: 30px !important;
	vertical-align: middle !important;
} 

.ForumMyImageColumn img {
	width: auto !important;
	height: auto !important;
	margin-left: 10px !important;
	margin-right: 10px !important;
}  

.forumAvatar {
	float: left !important;
	margin: 2px !important;
} 


.ForumMyNameColumn {
	border-left: 0px !important;
} 

.CommonListArea td {
	padding: 2px !important;
} 

.ForumNameUnRead {
	margin-left: 0px !important;
	font-size: 1.2em !important;
} 

.PostAuthorLink {
	margin-top: 5px !important;
} 

.ddlSortOrder {
	position: absolute !important;
	margin-top: -15px !important;
} 

.ForumPagingArea {
	margin-top: 0px !important;
	margin-left: 100px !important;
}
}
