css:
@-moz-document domain('guides.rubyonrails.org'), url-prefix('http://guides.rubyonrails.org/') {
	#subCol {
		position:fixed;
		top:170px;
		right:10px;
		bottom:0;
		z-index:999;
		overflow:hidden;
		width:2em;
		background:rgba(255,255,200,0.5);
		-webkit-transition:width,background .8s;
	}
	#subCol:hover {
	  width:17em;
	  overflow:auto;
	  background:rgba(255,255,200,1.0);
	}

	#subCol ol{
		-webkit-transition: opacity .8s;
		opacity:0;
	}
	#subCol:hover ol {
	  opacity:1.0;
	}

	#subCol h3 {
	  width:203px;
	  -webkit-transform:translate(-80px,80px) rotate(90deg);
	  -webkit-transition: -webkit-transform 0.5s;
	}
	#subCol:hover h3 {
	  -webkit-transform:translate(0,0) rotate(0deg);
	}
}
