css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.conquerclub.com") {
html, body {
color: #AFAFAF !important; 
background: url(http://imgur.com/uBzWe.jpg) #2B2B2B repeat-x !important; }

#pageWrapper{
min-width: 940px !important;
background: url(http://imgur.com/lH3OE.png) no-repeat !important;
background-position: 75% 140px !important;
}

#outer-map{
border: 4px solid #131313 !important;
left:0 !important;
top:0 !important;
}

.hnav {

border:0px !important; 

border-bottom: 4px solid #1D1D1D !important;
}



/* header + top nav */
.hnav { 
height:24px !important; 
margin:0px !important; 
padding:0px !important; 
text-align: left !important; 
background-color:#333333 !important; 
background: url(http://imgur.com/Nrvhz.png) no-repeat  -14px 0px !important;
}

.hnav ul {
position:relative !important;
top:3px !important;
padding-left: 255px !important;
}

.hnav ul li {
font-size:90% !important;
padding:4px !important; 
padding-bottom:5px !important; 
}
.hnav ul li a:link, .hnav ul li a:visited{ 
color:#AFAFAF !important; 
}

.hnav ul li a:hover, .vnav ul li a:hover{ 
color:#FF0000 !important;
 }

.hnav ul li a{ 
background: none !important; 
color:#AFAFAF !important; 
border-left:0px !important; 
border-right:0px !important; 
padding-left:5px !important;
padding-right:5px !important;
}
.hnav ul li:hover {
background: #373737 !important; 
}
/* links */

a, .vnav ul li a, a:visited  {
color: #D74A3F !important;
}


a:hover, #invertedtabs a span:hover, .vnav ul li a:hover, #middleColumn a:hover {
color:#D1D8DF !important;
background-color:#2B2B2B !important;
}


a, a:link, a:visited, a:active {
    text-decoration: none !important;
}
.vnav ul li a {background-color:#1D1D1D !important;} 
.vnav ul li {border-color: #333333 !important;}
#bobmenuUl li ul {border-color: #A30202 !important;}
#bobmenuUl li ul  li a {background-color:#2B2B2B !important;} 

.vnav p, #login fieldset{
width: 150px  !important;
padding-bottom:4px  !important;
text-align: center  !important;

background-color:#2B2B2B !important;
} 
#login fieldset{
padding:10px  !important;
} 
.pColor0{
color: #7F7F7F !important; 
}

.player0 {color:#DFDFDF !important;}
.card0, .pColor1, .player1 {color:#FF4F56 !important;}
.card1, .pColor2, .player2 {color:#69FF4F !important;}
.card2, .pColor3, .player3 {color:#4F7EFF !important;}
.card3, .pColor4, .player4 {color:#FFE04F !important;}


#contOverviewWrapper {height: 50% !important; } 
#contOverview  {
background-color:#1D1D1D !important; 
padding:5px !important;
margin-top:5px !important;
}
#contOverview span {padding:5px !important;}
#dashboard td{
vertical-align: top !important;}



h4 {display:none !important;}
#time-remaining {display:block !important;}

#dashboard tbody{background-color: transparent !important;}



.insidegame tr, #mapinspect, #redemption, .insidegame fieldset, #chat, #log{background-color:transparent !important;}

table.listing tr.odd td, table.listing tr.odd2 td{
background-color:transparent !important; 
border-color:#000000 !important;
}
table.listing tr.even td, table.listing tr.even2 td{
background-color:#232323 !important; 
border-color:#000000 !important;
}

.insidegame fieldset, #chat, #log, #summary{background-color:#1D1D1D !important;}

#middleColumn, #leftColumn, #rightColumn, #outerColumnContainer, #innerColumnContainer, #pageWrapper, #SOWrap{ background-color:black !important;}

#leftColumn .inside div {opacity: 0.2 !important;}
#leftColumn .inside div:hover {opacity: 1 !important;}
#leftColumn .inside #login, #leftColumn .inside #login div, #leftColumn .inside .vnav, #leftColumn .inside .vnav div{display: inline !important;}


#outerColumnContainer{ background-color:black !important;}

/* Set basic background and border color */
#footer{background-color:#333333 !important; border-color:#333333 !important;}



/* Set slightly lighter background color */
#middleColumn th, #invertedtabs a, #invertedtabs a span, #headerline {background-color:#1D1D1D !important; border-color:transparent !important; color:#D74A3F !important;}

#middleColumn th{
color:#636363 !important;
}


/* Set lightest background color */
#middleColumn, #leftColumn, #rightColumn, #outerColumnContainer, #innerColumnContainer, #pageWrapper, #SOWrap   {background-color:transparent !important; border-left-color:transparent !important; border-right-color:transparent !important;}

/* no borders on main elements */
#pageWrapper, #masthead, #innerColumnContainer, #footer, #leftColumn, #rightColumn, #SOWrap{border:none !important;}

#leftColumn {width: 177px !important; margin:0 1px 0 -177px !important;}



/* no cutesyclub */
#masthead{display:none;}
#masthead {height:50px;}
a.rank.r3p {visibility:visible !important;}


/* heading and paragraph size and spacing */

h2{margin:0px  !important; padding:0px  !important; margin-top:6px !important; margin-bottom:3px !important; font-size: 130% !important; color: #FFFFFF !important;}


h3{margin:0px  !important; padding:0px  !important; margin-top:0px !important; margin-bottom:3px !important; font-size: 100% !important;}

.vnav h3{
margin-top:10px!important;
}

#gamelog h3{margin-top:0px !important;}
p{margin-top:6px !important; margin-bottom:3px !important;  }

#middleColumn, #middleColumn .insidegame  { 
padding:0 !important;  

}

/* Highlight games that are ready */


#footer{padding-bottom:0px !important;}



.vnav ul {
    border:black !important;
}

.ccform fieldset {
    background-color: transparent !important;
    border: none !important;
    padding: 0px !important;
}
.ccform legend {
    background-color: #181818 !important;
    border: none !important;
    color: #5F5F5F !important;
    font-size: 140%;
    padding: 2px 10px;
}

td.status_green, li.status_green {
background: url("http://imgur.com/2BgVN.png") no-repeat scroll left  transparent !important;
background-position: 1px 1px !important;
padding-left: 19px !important;
}

td.status_red, li.status_red {
background: url("http://imgur.com/wbc1W.png") no-repeat scroll left center transparent !important;
background-position: 1px 1px !important;
padding-left: 19px !important;
}

td.status_yellow, li.status_yellow{
background: url("http://imgur.com/WJHqY.png") no-repeat scroll left center transparent !important;
background-position: 1px 1px !important;
padding-left: 19px !important;
}
td.status_blocked, li.status_blocked{
background: url("http://imgur.com/qMEHI.png") no-repeat scroll left center transparent !important;
background-position: 1px 1px !important;
padding-left: 19px !important;
}

dl.details dt{

    color: #AFAFAF !important;
}
label, dl.details dd {
    color: #7F7F7F !important;
}
.bg1, .bg2{
    background-color: #2B2B2B !important;
    color: transparent !important;
}

.insideforum a{
color: white !important;
}
.insideforum #wrap div, 
.insideforum #wrap div blockquote, 
.insideforum ul,  .panel, .postbody, .content, .navbar {
color: white !important;
background: transparent !important;
}
.corners-top, .corners-bottom, .corners-top span, .corners-bottom span{
background-image:none !important;
}


.insideforum #wrap .post{
    background-color: #262526 !important;
    color: #1D1D1D !important;
}
.insideforum #wrap .postbody .signature{
opacity: 0.3  !important;
}
.insideforum #wrap .postbody .signature:hover{
opacity: 1  !important;
}
.insideforum ul dl:hover{
background-color: #1D1D1D !important;
}
.insideforum ul dl{
color: #B3B3B3 !important;
}

.insideforum ul dl strong{
color: #979797 !important;
}

.insideforum #wrap div{
border:none !important;
}
li.row {
    border: none !important;
}
.postprofile {
    border-left-color: #000000 !important;
}
h3 {
    color: #AFAFAF !important;
}
#navigation a, .cp-mini {
    background: #1D1D1D !important;
}

#dashboard dl {
position:absolute !important;
top:32px !important;
left:0 !important;
font-size: 90% !important;
color: #D74A3F !important;
background: url(http://imgur.com/Tdvm5.jpg) #373737 repeat-x !important;
padding:3px !important;
padding-left:6px !important;
padding-right:0px !important;
width:99.6% !important;
}
#dashboard dl:hover {
}

#dashboard dt {
position:relative !important;
top:-1px !important;
font-size: 90% !important;
    color: #B3B3B3!important;
    display: inline !important;
padding-top:6px !important;
padding-bottom:6px !important;
}
#dashboard dt:hover {
top:0px !important;
 color: #FFFFFF !important;
font-size: 100% !important;
}
#dashboard dt:after
{
content:":";
}
#dashboard dd {
    font-size:100% !important;
    display: inline !important;
    font-weight: bold !important;
    margin-left: 0 !important;
    margin-right: 10px !important;
}


/*[[show_options]]*/


.insidegame h3,
#log, 
#full-log,
#chat,
#full-chat,
#chat-form,
#summary, 
#stats{
position: relative  !important;
top: -20px !important;
width:100% !important;
background-color: #232323 !important;
margin:0px !important;
padding:0px !important;
}

#full-chat a, 
#full-chat a:visited,
#full-log a,
#full-log a:visited{
color: #BBBBBB !important;
}
#stats{
background-color: transparent !important;
}
#log:hover, 
#chat:hover,
#chat-form:hover,
#summary:hover{
background-color: #1D1D1D !important;
}
#map-cell{

}
#dashboard{
}

#right_hand_side{
}
#right_hand_side #stats{
position: relative  !important;
top: 0px !important;
}

#chat-form{
background-color: #1D1D1D !important;
}

.insidegame h3{
color: #D74A3F !important;
background-color: #1D1D1D !important;
margin-top:8px !important;
padding: 2px !important;
}

.insidegame td h3 {
position: static  !important;
color: #AFAFAF !important;
background: url(http://imgur.com/Tdvm5.jpg) #373737 repeat-x !important;
height: 24px !important;
margin:0px !important;
margin-left:-2px !important;
margin-bottom:20px !important;
padding-top:4px !important;
padding-left:5px !important;
}


#action-form fieldset, #rolls {
margin:0px !important;
padding-left:4px !important;
padding-right:4px !important;
background: url(http://imgur.com/s0h3b.jpg) #1D1D1D repeat-x !important;
border:2px #131313 solid !important;
} 

#action-form{
 width: 80% !important;
}

#action-form fieldset:hover{
}
#rolls:hover {
}
#rolls {
color: #ffffff !important;
padding-top:5px !important;
padding-bottom:5px !important;
border-top:none !important;
}
#rolls p{
margin-top: -1px !important;
}
.roll{
font-size: 160% !important;
}
#stats{
margin-top: 5px !important;

}
#time-remaining{
position:absolute !important;
top: -7px !important;
left:0px !important;
margin-left:190px !important;
font-size: 100% !important;
font-weight: normal !important;
}
#time-remaining #clock, #time-remaining #additionalClock b{
color: #D74A3F !important;

font-weight: bold !important;
}

#rolls p.attacker {
  margin-left: 0em !important;
}
span.eliminated {
opacity: 0.4 !important;
}



#headerline {
    background: none !important;
}
.listing td{
vertical-align: middle !important;
}

.postbody{

    background-color: #ffffff !important;
}

#middleColumn .status_green b a, #middleColumn .status_green b a:visited{ 
color: #BFBFBF !important;
}

#inner-map{
background-color: rgba(0,0,0,.5) !important;
}

}



@-moz-document 
url-prefix("http://www.conquerclub.com/player.php?mode=mygames"), url-prefix("http://www.conquerclub.com/player.php?mode=join"),
url-prefix("http://www.conquerclub.com/player.php?mode=find"),
url-prefix("http://www.conquerclub.com/forum")
{

#middleColumn a, #middleColumn a:visited  {
color: #9F817F !important;
}
.gameno{
margin-top:0px !important;
}

}

style_setting_options.value:
#dashboard dt {
display: none !important;
}
#dashboard dd:before
{
color:#C7C7C7 !important;
content:"> ";
}
#dashboard dl:hover {

    color: #FFFFFF!important;
}
