css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.kigges.de/forum/") {

/*
 * Kigges MakeOver
 * by NeururerFan
 */

#top {
  background-image: none !important;
  background-color: #A00 !important;
}
#top h1 {
  font-family:        Tahoma !important;
  letter-spacing:     -1px !important;
  text-shadow:        1px 1px 0 #555 !important;
}
#top h1 a {
  font-size:          1.4em !important;
}
#top br {
  display:            none !important;
}
#top font {
  display:            none !important;
  letter-spacing:     -1px !important;
  text-shadow:        1px 1px 0 #555 !important;
}
#top div.right a:link, #top div.right a:active, #top div.right a:visited {
  color:              #FF0 !important;
  letter-spacing:     -1px !important;
  text-shadow:        1px 1px 0 #555 !important;
}
#top label {
  color:              #FFF !important;
  text-shadow:        1px 1px 0px #000 !important;
}
#topnav {
  color:              #C0C000 !important;
  background:         #800 !important;
  border-top:         1px solid #780000!important;
  border-bottom:      1px solid #000 !important; 
}
#topnav a:link, #topnav a:active, #topnav a:visited {
  color:              #FFFF80 !important;
  text-shadow:        1px 1px 0px #000;
}

/* sidebar */

#latest-postings {
  border:             1px solid #B00 !important;
}
#latest-postings h3 {
  background-image:   none !important;
  background-color:   #B00 !important;
  color:              #FFF !important;
}


/* no advertising */
#top center {
  display:            none !important;
}

/* css link fix! */
.ct-postingbody a:link, .ct-postingbody a:active {
  color:              #A00 !important;
  text-decoration:    underline !important;
}
.ct-postingbody a:hover {
  color:              #D00 !important;
  text-decoration:    underline !important;
}
.ct-postingbody a:visited {
  color:              #A0A !important;
  text-decoration:    underline !important;
}
.posting a:link, .posting a:active {
  color:              #A00 !important;
  text-decoration:    underline !important;
}
.posting a:hover {
  color:              #D00 !important;
  text-decoration:    underline !important;
}
.posting a:visited {
  color:              #A0A !important;
  text-decoration:    underline !important;
}

/* other post styles */
.thread-sticky {
  background-image: none !important;
  background-color: #F4F4FF !important;
  border-left:      3px solid #00F !important;
  padding-left:     0.5em !important;
}
span.category, a[title="Zeige Vorschau"], .signature {
  display:            none;
}
.posting h1 {
  padding:            0.1em 0.2em !important;
  background-color:   #F4F4F4 !important;
  font-family:        Tahoma !important;
  font-size:          1.3em;
  font-weight:        bold !important;
  color:              #000 !important;
}
p.author, .ct-author {
  text-align:         left !important;
  font-style:         normal !important;
  font-size:          0.7em !important;
}
.ct-postinghead h1 {
  font-family:        Tahoma !important;
  font-size:          1.3em !important;
}
.ct-posting {
  border-left:        10px solid #B00 !important;
  -moz-border-radius: 10px;
}
a.stronglink:link, a.stronglink:active, a.stronglink:visited, a.stronglink:hover {
  color:              #A00 !important;
  font-weight:        normal !important;
  text-decoration:    underline !important;
  background-image:   none !important;
  padding-left:       1.2em !important;
}

}
