css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain(4chan.org){

body
{ 
	background: #e9f3ff !important;
	color: #000000 !important;
	font: 100% "Calibri", "Verdana", "Tahoma", Georgia, Sans-Serif !important;
}

th[bgcolor="#e04000"]
{ 
	border: 1px solid !important;
	background-color: #8e9fd9 !important;
	font: 100% "Calibri", "Verdana", "Tahoma", Georgia, Sans-Serif!important;
	font-weight: 700 !important;
	padding: 0px 0px 0px 0px !important;
}

td { color: #000000 !important; }
td[align="right"] { font-size: 100% !important; }
img[align="left"] { border: 1px solid !important; }
.filesize { font: 100% "Calibri", "Verdana", "Tahoma", Georgia, Sans-Serif !important; }
.filetitle, .replytitle { color: #386ec0 !important; font: 110% "Calibri", "Verdana", "Tahoma", Georgia, Sans-Serif !important; }
a { font-weight: 700 !important; color: #000000 !important; }
a:visited { font-weight: 700 !important; }
a:hover { color: #6990c9 !important; }
.oldpost { color: #26569f !important; }
textarea, .inputtext { font: 100% Calibri !important; vertical-align: top !important; }
[value="Submit"] { height: 20px !important; vertical-align: top !important; font: 100% Calibri !important; }

.postblock  {
	vertical-align: top !important;
	background-color: #5c87c6 !important;
	border-color: #000000 !important;
}

.postblock:not([align="center"])  {
	padding-top: 0.1850em !important;
	-moz-border-radius: 10px 0 0 10px !important;
	vertical-align: top !important;
	text-align: right !important;
	background-color: #5c87c6 !important;
	border-color: #000000 !important;
}

blockquote {
	font-size: 100% !important;
}

textarea.inputtext { height: 100px !important; }
.commentpostername a.linkmail[href="mailto:sage"] { color: #ff0000 !important;  }
.commentpostername a.linkmail[href="mailto:sage"]:after { content: " (sage)"; color: #ff0000 !important }
.deletebuttons .inputtext { width: 100px !important; -moz-border-radius: 10px !important; }

.reply, .replyhl, .pages {
	border: 1px solid !important;
	border-color: #000000 !important;
	-moz-border-radius: 0 20px !important;
	color: #000000 !important;
	padding-bottom: 3px !important;
	font-size: 100% !important;
}

.reply {
	background-color: #c9d5ff !important;
	padding-right: 10px !important;	
}

.reply .reply { font-size: 100% !important; }

.replyhl {
	background-color: #cde8ff !important;
	padding-right: 10px !important;	
}

.pages {
	background-color: #c9d5ff !important;
	padding-right: 5px !important;	
	padding-bottom: 0px !important;	
}

.postername, .commentpostername, .linkmail:not([href="mailto:sage"]) {
	color: #26569f !important;
	font: 120% "Calibri", "Verdana", "Tahoma", Georgia, Sans-Serif !important;
	font-weight: 700 !important;
}

[style^="color: rgb(128, 0, 128)"] { color: #3414ae !important; }
.quotelink { color: #143361 !important; }

.postertrip {
	color: #26569f !important;
	font-size: 120% !important;
text-shadow: 0px 2px 2px #fff;
}

.unkfunc { color: #26569f !important; }
.linkification-ext { color: #143361 !important; }
.logo font { color: #143361 !important; }
hr { border-color: #b7c5d9 !important; }
b { color: #000000 !important; }
.tn_thread {
	background-color: #d5dce5 !important;
	border-color: #000000 !important;
}
navtop, #navtop, navbot, #navbot { color: #8899aa !important; font-size: 90% !important; }
div#tw.reply { font-size: 100% !important; }

/* Buttons */

[src^="chrome://4chan/skin/buttons/futaba"] {
padding:0px 0px 18px 18px !important;
height:0px !important;
width:0px !important;
border: 0px !important;
}

[src$="post_expand_minus.png"] { background:transparent url("chrome://4chan/skin/buttons/burichan/post_expand_minus.png") no-repeat !important; }
[src$="post_expand_plus.png"] { background:transparent url("chrome://4chan/skin/buttons/burichan/post_expand_plus.png") no-repeat !important; }
[src$="stop.png"] { background:transparent url("chrome://4chan/skin/buttons/burichan/stop.png") no-repeat !important; }
[src$="arrow_up.png"] { background:transparent url("chrome://4chan/skin/buttons/burichan/arrow_up.png") no-repeat !important; }
[src$="arrow_right.png"] { background:transparent url("chrome://4chan/skin/buttons/burichan/arrow_right.png") no-repeat !important; }
[src$="arrow_down.png"] { background:transparent url("chrome://4chan/skin/buttons/burichan/arrow_down.png") no-repeat !important; }
[src$="post_expand_rotate.gif"] { background:transparent url("chrome://4chan/skin/buttons/burichan/post_expand_rotate.gif") no-repeat !important; }
[src$="arrow_down2.png"] { background:transparent url("chrome://4chan/skin/buttons/burichan/arrow_down2.png") no-repeat !important; }
[src$="empty.png"] { background:transparent url("chrome://4chan/skin/buttons/burichan/empty.png") no-repeat !important; }

.closebutton {
	background: url("data:image/gif;base64,R0lGODlhEAAQAMQAAAAAAP///wAAiDMzoDg4olBQrYCAxK6u2bOz3Li43sDA4sTE5MfH5cnJ5szM58/P6NTU6////wAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAACH5BAEAABEALAAAAAAQABAAAAVWoGCMZFmKQqqu6ygUDKsig2gISQDJToDYqUXgserFgCnGMGVUuVRKR9N5WzUCgSOV1cuynszsNAWePgKLrWAsOCvIN4WXBQkcgIWELKUgIPdfNiaDNyEAOw==") !important;
}

#filter-container {
	background: url("data:image/gif;base64,R0lGODlhEAAQAMQAAAAAAP///7bT21WZqleaq1mbrGGgsG2ntnWsun2xvoe3w5vDzc3h5tfn69/s7+fx8/P4+f///wAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAACH5BAEAABEALAAAAAAQABAAAAUz4CCOZGmeaKquQxO88PsUJRLHyincAYMakNshtYgJVITHC2JYKV4L1oDhIEgPCal2Kw0BADs=") no-repeat 28px 4px !important;
}

/* Ads */

#ad{ display:none !important; }
img[src*="http://content.4chan.org/dontblockthis"]{ display:none !important; }
center+hr{ display:none !important; }
hr[size="1"][width="100%"]{ display:none !important; }

/* Various */

.boxbar {
	background-color: #c9d5ff !important;
	color: #4c569f !important;
}

#ft li {
	background-color: #e9f3ff !important; 
	border-color: #4c569f  !important;
}

[id="thread_filter"] {
	font-size: 100% !important;
	padding-left: 10px !important;
}

hr { color: #4c569f !important; }

html { background: #e9f3ff !important; }

#twi_table_div  {
	border: 0 none !important; 
	opacity: 0.90 !important;
}

#twi_table_div > .postblock  {
	border-color: #000000!important;
	color: #000000 !important;
	border-width: 1px 1px 0 1px !important;
	-moz-border-radius: 10px 10px 0 0 !important;
	text-align: center !important;
}

#twi_table_div table .postblock  {
	-moz-border-radius: 0 !important;
	background-color: #8e9fd9!important; 
	margin: 0 !important;
	color: #000000 !important;
	text-align: center !important;
}

#twi_table_div > #twi_table {
	border-width: 0 1px 1px 1px !important;
	-moz-border-radius: 0 0 10px 10px !important; 
	padding: 0 0 5px 0 !important;
	opacity: 1.00 !important;
}

#twi_table .postername, .twi_remove, .twi_view { font-size: 100% !important; }

.reply[id^="4chan_ext_right"], .reply[id^="4chan_ext_nav_top"] { padding-left: 10px !important; }

/* Logo */

#logo { background-image: url("data:image/gif;base64,") !important; }

}
