@-moz-document domain("wikipedia.org") {

body {
  background-image:-moz-linear-gradient(top, white, white 40%, #f3f3f3) !important;
  -moz-background-size:100% 7em;
  background-repeat:no-repeat;
  background-attachment:fixed;
}

#mw-page-base,
#mw-head-base{
  z-index:2000;
  background-color:#F3F3F3;
}
#mw-head {
  background:-moz-linear-gradient(top, white, white 60%, #f6f6f6 98%, #a7d7f9);
  height:5em;
  left:10em;
  z-index:2000;
  overflow:hidden;
}
#left-navigation {
  left:0 !important;
}
#right-navigation{
  position:relative;
  right:10em;
}
#p-personal{
   right:10.75em !important;
}
#mw-page-base,
#mw-head-base,
#mw-head {
  position:fixed !important;
}
#content {
 margin-top:5em;
 z-index:0;
}
div.suggestions {
  z-index:2009 !important;
}

#bodyContent h2,
#bodyContent h3,
#bodyContent h4,
#bodyContent h5,
.anchor {
  overflow:hidden;
}
#bodyContent h2 >span[id],
#bodyContent h3 >span[id],
#bodyContent h4 >span[id],
#bodyContent h5 >span[id],
.anchor{
  position:relative;
}
#bodyContent h2 >span[id]{
  padding-top:4.3em;
}
#bodyContent h3 >span[id]{
  padding-top:5em;
}
#bodyContent h4 >span[id],
.anchor{
  padding-top:6em;
}
#bodyContent h5 >span[id] {
  padding-top:7em;
}

}
