css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix('http://www.tumblr.com/'), url-prefix('https://www.tumblr.com/')   {

* { text-shadow: none !important; }

div.l-content,#left_column #posts .post .permalink {
background-color:#b1d3ea !important
}

#dashboard_controls_suggested_blogs {
background:#73c1d2 !important;
border:none !important
}

#dashboard_index div.l-content,#dashboard_submissions div.l-content,#dashboard_drafts div.l-content,#dashboard_post_queue div.l-content,#dashboard_likes div.l-content,#dashboard_members div.l-content,#main_tag #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background:rgba(255,255,255,0.2) !important
}

#dashboard_index,#dashboard_edit_post,#dashboard_submissions,#dashboard_drafts,#dashboard_post_queue,#dashboard_likes,#dashboard_followers,#dashboard_following,#dashboard_members,#main_tag {
background:#ced273 url(http://i819.photobucket.com/albums/zz114/cadynotcaddy/jllk.jpg) no-repeat bottom left fixed !important
}

#dashboard_switch_blog_menu_current,#dashboard_switch_blog_arrow_and_divider,#dashboard_switch_blog_arrow_current {
background-image:none !important
}

#footer,.dashboard_options_form_divider {
display:none
}

#left_column #posts .notification {
background-color:#c9e5ee !important;
border-bottom:none !important;
color:#8691a9 !important
}

#left_column #posts .notification .avatar {
background-color:#8691a9 !important;
border-color:transparent !important
}

#left_column #posts .notification .block,.post_info,.post_info a {
color:#767f9e !important
}

#left_column #posts .notification a {
border:none !important;
color:#767f9e !important
}

#left_column #posts .notification blockquote {
border-left-color:#c9ca9e !important
}

#left_column #posts .notification.alt,#dashboard_switch_blog_menu_footer,#dashboard_switch_blog_menu_wrapper {
background-color:#e8eabd !important
}

#left_column #posts .post .post_avatar,#left_column #posts .post .sub_avatar .sub_avatar_inner {
background-color:#c4c9cc !important;
border-bottom:none !important
}

#left_column #posts .post,.dashboard_options_form {
border-bottom:none !important
}

#nav .nav_item .nav_menu a {
border-top-color:#e4e6b3 !important;
color:#9a9b79 !important
}

#nav .nav_item .nav_menu a:hover,.no_posts_found,h1.dashboard_header,h1.dashboard_header a,h1.dashboard_header span,#posts .post .footer_links a:hover,#posts .post .post_controls a:hover,#right_column a,.dashboard_subpages,.dashboard_controls_suggested_blog_title,.dashboard_controls_suggested_blog_description {
color:#767a45 !important
}

#nav .nav_item .nav_menu,#dashboard_switch_blog_menu_header {
background-color:#eff0d1 !important;
border:none !important
}

#nav .nav_item.active,#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel,#right_column .dashboard_nav_item #dashboard_controls_radar_media_fold {
background:#bde7f2 !important;
color:#8691ba !important
}

#pagination a {
background:#7f799b !important;
color:#b3dce6 !important
}

#pagination a:hover {
background:#817898 !important
}

#posts .post .footer_links a,#posts .post .post_controls a {
color:#aad3f5 !important
}

#right_column .dashboard_nav_item {
background:none !important;
border-bottom:1px solid #b4e2e7 !important
}

#right_column a:hover,.new_post_label:hover {
color:#2e2483 !important
}

#show_radar,#hide_radar,#hide_recommended_blogs,#show_recommended_blogs {
background:none !important
}

.dashboard_controls_radar_media {
background:#9ec3e9 !important
}

.dashboard_controls_suggested_blog {
border:1px solid #8fc3db !important;
border-width:0 0 1px !important
}

.dashboard_nav_title {
color:#1543ac !important
}

.dashboard_options_form,.dashboard_options_form_disclaimer {
background:#fff !important;
color:#555 !important
}

.dashboard_switch_blog_menu_item {
background-color:#d1e0f0 !important;
border:0 !important
}

.nav_item:hover {
color:#b3e2e6 !important
}

.new_post_label {
-webkit-text-stroke:0 !important;
color:#8badd8 !important;
opacity:1 !important
}

.notification_type_icon {
display:none !important
}

.sub_avatar {
background-color:transparent !important;
height:32px !important;
width:32px !important
}

h1,h2,h3,h4,h5,h6 {
color:#54566c !important
}

img[src*="http://assets.tumblr.com/images/new_post_icons_no_labels.png"] {
opacity:0;
visibility:hidden
}

ol#posts li.post.new_post {
background:#fff url('http://i54.tinypic.com/sn1lb5.jpg') no-repeat;
background-position: center bottom
}

ol#posts li.post.new_post:hover {
background-position:center bottom
}

}
