css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url("http://d.hatena.ne.jp/*/designdetail") {


.section { margin:0!important; padding:10px!important; }
.summary-wrapper { padding:0!important; }

.header-color-list li { margin-bottom:0!important; width:40px!important;height:1em;overflow:hidden;}
.header-color-list input { vertical-align: text-top!important; margin: 0; }

.header-color-list label { visibility:hidden; overflow:hidden;height:1em!important;}
.header-color-list label span { visibility:visible; }

.header-color-list span { font-size: 1.75em;line-height:0; }

#ucolor-wh +label span { text-shadow:
  1px  1px 0  #000,
  1px -1px 0  #000,
  -1px 1px 0  #000,
  -1px -1px 0 #000;
}

textarea{ font-size:13px!important; }

}
