css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://learn.open.ac.uk/mod/forumng/"),url-prefix("https://learn.open.ac.uk/mod/forumng/")
{
.forumng-replies{
margin-left: 5px !important;
}

.forumng-post{
max-width: 900px !important;
min-height: 60px !important;
border-color: #DBEDFF !important;
border-style:  solid !important;
border-width: 1px !important;
-moz-border-radius: 10px !important;

-moz-box-shadow: -1px -1px 4px 0px !important;
}

/*these empty divs are probably for mods, oops*/
.forumng-post .forumng-1{
display: none !important;
}
.forumng-post .forumng-2{
display:none !important;
}

.forumng-pic, .userpicture{
padding-top: 2px !important;
width:50px !important;
height: 50px !important;
}
.forumng-postmain, .forumng-subject, .forumng-info, .forumng-summary, .forumng-deleted-info {
margin-left: 50px !important
}
.forumng-post.forumng-read{
background-image: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAADwAAAAPCAIAAAA3cDsmAAAAAXNSR0IArs4c6QAAAAZiS0dEAP8A/wD/oL2nkwAAAAlwSFlzAAALEwAACxMBAJqcGAAAAAd0SU1FB9oMCBUIMmXW6hQAAAArSURBVEjHY7z99j/DUANMDEMQjDp61NGjjh519KijRx096uhRR486mgAAABo9AuVvP9j0AAAAAElFTkSuQmCC) !important;

}
.forumng-post.forumng-unread{
background-image: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAADwAAAAPCAIAAAA3cDsmAAAAAXNSR0IArs4c6QAAAAZiS0dEAP8A/wD/oL2nkwAAAAlwSFlzAAALEwAACxMBAJqcGAAAAAd0SU1FB9oMCBUWMSiehHEAAAAqSURBVEjHY2QwTmMYaoCJYQiCUUePOnrU0aOOHnX0qKNHHT3q6FFHEwAA/xUAt/mA91gAAAAASUVORK5CYII=) !important;

}
}
