css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("http://web.ebuddy.com/?startsession=1#") {
.rectanglebanner #container-app {
    right: 0!important;
}
.rectanglebanner #container-banner {
    width: 0 !important;
}
#container-banner {
    background: none repeat scroll 0 0 rgba(255, 255, 255, 0.5);
    border-left: 1px solid #FFFFFF;
    display: none !important;
    left: auto;
    width: 0 !important;
}
#banner {
    display: none !important;
}
#container-app {
    right: 0 !important;
}
#container-home-notifications {
    display: none;
}
#container-home {
    bottom: 5px !important;
}
#container-chat {
    bottom: 0 !important;
}
#container-chat .banner-visible .container-chat-banner {
    display: none !important;
}
#container-maintabs {
    top: 0 !important;
}
#container-home {
    top: 57px !important;
}
#container-home-tabs {
    display: none !important;
}
#container-home-content {
    left: 2px !important;
}
#container-contactactions {
    -moz-border-radius: 0 !important;
}
#container-chat {
    top: 57px !important;
}
#container-topmenu {
    z-index: 100 !important;
    width: 500px;
    float: right;
    position: relative;
}
}
