css:
@-moz-document domain('rememberthemilk.com'),url-prefix('http://www.rememberthemilk.com'){#content{width:980px !important;}
body.v2 #content{width:980px !important;}
body.v3 #content{width:980px !important;}
#listtabs{width:110px !important;float:left !important;border:1px solid #CACACA !important;margin:0px !important;padding:0px !important;clear:both !important;}
#listtabs li,#listtabs li a{background:none !important;clear:both !important;border:none !important;margin:0px !important;padding:0 0 0 3px !important;}
#listtabs ul{margin:0px !important;padding:0px !important;}
#listtabs.xtabs li{background:#f9f9f9 !important;border:none !important;width:98% !important;z-index:1000 !important;}
#listtabs.xtabs li a{color:#444 !important;font-weight:normal !important;}
#listtabs.xtabs li:hover{background:#E8EEF7 !important;width:98% !important;border-right:3px solid #0060BF !important;z-index:1000 !important;}
#listtabs.xtabs li.xtab_selected{background:#fff !important;color:#0060BF !important;border-top:1px dotted #CACACA !important;border-bottom:1px dotted #CACACA !important;border-right:1px solid #fff !important;width:98% !important;}
#listtabs.xtabs li.xtab_selected a{font-weight:bold !important;color:#0060BF !important;}
#listwrap{float:left !important;margin-left:15px !important;width:540px !important;}
#tools_spacer{border:none !important;margin:0px !important;padding:0px !important;}
#sorting{margin:0px !important;}
#tools{padding-right:5px !important;padding-top:7px !important;padding-bottom:0 !important;border-top:1px solid #cacaca !important;border-right:1px solid #cacaca !important;border-left:1px solid #cacaca !important;}
.xtoolbox_actions{float:right !important;}
.xtoolbox_actions select{width:145px !important;margin-right:10px !important;}
.xtoolbox_button{display:none !important;}
.xtoolbox_selector{color:#ffffff !important;margin-left:0px !important;padding-left:0px !important;font-size:2px !important;}
.xtoolbox_selector a{font-size:11px !important;padding:2px !important;}
#taskcloud_copy{display:none !important;}
#appfooter-news-list{display:none !important;}
.appfooter{clear:both !important;}
.appfootercontent{width:500px !important;margin-left:190px !important;}
.appfootercontent a{color:#CCCCCC !important;text-decoration:none !important;}
.appfootercontent a:hover{text-decoration:underline !important;}
#appheaderlogo{display:none;}
#datetime{display:none;}
#topnav{float:left !important;}
#break{padding:0px !important;margin:0px !important;display:none !important;}
#listFilter{width:370px !important;}
#searchbox{margin-top:-20px !important;}
#searchtogglewrap{float:right !important;}
#statusbox{margin-top:3px !important;padding-top:5px !important;padding-left:130px !important;width:530px !important;}
.orange_rbroundbox{background-color:#FFFFCC !important;background-image:none !important;border-radius:10px !important;padding-top:0px !important;}
.orange_rbtop{background-image:none !important;height:0px !important;display:none !important;}
.orange_rbtop div{background-image:none !important;display:none !important;height:0px !important;}
.orange_rbbot{background-image:none !important;}
.orange_rbbot div{background-image:none !important;}
}
