css:
@-moz-document domain("4chan.org") {

* { color: #666; font-family: "Lucida Grande" !important; } 
body { background: #2C2C2C !important; color: #eee !important; font-size: 8pt !important; }
center * { color: #666 !important; }
#updater { background: #2C2C2C !important; color: #666 !important; border-color: #2C2C2C !important; } 
select { display: none !important; }
blockquote, blockquote * { color: #fff; font-size: 9pt !important; } 
th { display: none !important; }
a { color: #aaa !important; text-decoration: none !important; } 
a.linkmail { color: #ccc8bc !important; text-decoration: none !important; }
hr { display: none !important; }
#special {color: #666 !important; } 
#fb_tr { display: none !important; } 
#navtop, #navbot { background: #2C2C2C; color: #2C2C2C !important; width: 100%; text-align: center; height: 12pt; font-size: 7pt !important; }	
#navtop *, #navbot * { color: #eee; font-size: 7pt; display: inline; }	
navtop a:hover, #navbot a:hover { background: #555; }
form { margin: 1em; }
.reply { font-size: 8pt !important; }
.inputtext, textarea { width: 309px; }
.rules { display: none; }
td.postblock { background: #444 !important; border: 1px solid #666 !important; color: #aaa !important; }
td.postblock * { font-size: 7pt; }
td.reply, td.replyhl { background-color:#333 !important; border: 1px solid #555 !important; color: #666 !important; }
td.replyhl { background-color:#4B4B4B !important; border: 1px solid #4B4B4B !important; }
.commentpostername, .filetitle, .postername { color: #ccc8bc !important; font-size 7pt !important; }
.filesize, span.filetitle, .filesize a, a.quotejs, .replytitle, .abbr, .omittedposts { color: #666 !important; font-size: 8pt;}
#hd, small, font[size="1"], font[size="2"], #ft, #filter-button, #option-button, #navtopr, #navbotr, .logo, .logo font b span, .doubledash, img.rotating, input[type="button"], td[align="right"] { display: none !important; }
.pages { background: #333 !important; border: 1px solid #555 !important; }	.pages td { color: #333 !important; font-size: 4pt; }
.pages td a, .pages td b { color: #666 !important; font-size: 8pt; }
.pages td b { color: #aaa !important; }
.pages td:first-child, .pages td:last-child { display: none !important;}
pre, textarea, input:not([type="button"]):not([type="checkbox"]):not([type="submit"]):not([type="reset"]), select { font-size: 9pt ! important; color: #fff !important; background: #333 !important; border-color: gray !important; }	 
input[type="submit"] { background: #444 !important; color: #aaa !important; border: 1px solid gray; font-size: 9pt; }
th { background: #2C2C2C !important; }
th font { font-size: 7pt; }
input[type="file"] { opacity: 0.12 !important; }
td.deletebuttons { font-size: 8pt !important; color:#eee !important; }

::-webkit-scrollbar {
	width: 6px !important;
	height: 6px !important;
}

::-webkit-scrollbar-button:start:decrement  {
    border-bottom-left-radius: 5px 5px !important;
    border-bottom-right-radius: 5px 5px !important;
    border-top-left-radius: 5px 5px !important;
    border-top-right-radius: 5px 5px !important;
	height: 0px !important;
	display: block !important;
	background-color: transparent !important;
}

::-webkit-scrollbar-button:end:increment {
    border-bottom-left-radius: 5px 5px !important;
    border-bottom-right-radius: 5px 5px !important;
    border-top-left-radius: 5px 5px !important;
    border-top-right-radius: 5px 5px !important;
	height: 0px !important;
	display: block !important;
	background-color: transparent !important;
}

::-webkit-scrollbar-track-piece {
    background-color: #333333 !important;
    border-bottom-left-radius: 5px 5px !important;
    border-bottom-right-radius: 5px 5px !important;
    border-top-left-radius: 5px 5px !important;
    border-top-right-radius: 5px 5px !important;
}

::-webkit-scrollbar-thumb:vertical, ::-webkit-scrollbar-thumb:horizontal {
	height: 50px !important;
	background-color: #9a9a9a !important;
	-webkit-border-radius: 5px !important;
}

::-webkit-scrollbar-thumb:vertical:hover, ::-webkit-scrollbar-thumb:horizontal:hover {
	background-color: #5e5e5e !important;
}

::-webkit-scrollbar-corner {
	display: none !important;
}
}
