css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("bakabt.com") {

/* BACKGROUNDS & BORDERS */

body#details { background: #dbeeee !important; }
div.comment_wrap, div.column_B_body, fieldset, div.suggestion { background: #f5f9f5 !important; }
div.note,  div#content > ol ~ div,  div#filelist li.alt0, div.torrent_mod > a  { background-color: #f5f9f5 !important; }
tr.alt1, div#tab_outer,  table.userstats td, table.default th, div#filelist li.alt1 {  background: #ddebff !important; }
div#tab_menu li:not(.selected) a, div.comment_wrap a.sublink, a.modlink, div.quote, div#screenshots, div#content > div > div.alt1 { background: #e8edf6 !important; }
table.userstats { background: lightblue !important; }

div.comment_wrap, div.column_B_body, fieldset, input#thesearchbox, input#thesearchbutton,
div#content > ol ~ div:not([style*="clear"]):not(.clear), div.suggestion, table.userstats, div.torrent_mod > a { border: 1px lightblue solid !important;  }
div#filelist li { border-color: lightblue !important;  }

table.torrents th, a.a_button { background: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAZCAYAAADwkER/AAAACXBIWXMAAA7EAAAOxAGVKw4bAAAAQ0lEQVQI10XMwQmAABADwZj+e7QGwedl48MTfwkDq/O+alq5RYbqvcC3EnmY1WTk9FgIkQNyfoUNJCNTyZDtFWRJegBZjkZksQhLJQAAAABJRU5ErkJggg==) !important; }

div#head { border-bottom: 1px black solid !important; }
div#upper_header, div#headbar { background: black !important; }

div.main { background: none !important; padding-left: 0px !important; padding-right: 0px !Important; border-width: 0px 1px 1px 1px !important; border-color: black !important; border-style: solid !important; }

/* TEXT */

div.pager a { letter-spacing: 0px !important; }

/* POSITIONING */

div.stats_out { float: left !important; width: 390% !important;  }
div.stats_out * { text-align: center !important; }
div.stats_out + a { display: none !important; }
div.stats h1 { margin-bottom: 5px !important; }

div#news { margin-top: 160px !important; }
div#news div:first-child { margin: 0 !important; }
div.faq { margin-top: 160px !important; }

div#headbar { position: fixed !important; top: 0px !important; left: 0px !important; width: 100% !important; }

ul#quicklinks { top: 26px !important; }
div#upper_header { margin-top: 27px !important; }

div#column_B div.torrents { margin-top: 3px !important; }

div#content { padding-top: 10px !important; }
div.searchcloud {  margin-top: 0px !Important; }

}

/* Fuck whoever invented iframes */

@-moz-document url("http://www.bakabt.com/"), url-prefix("http://www.bakabt.com/b"), url-prefix("http://www.bakabt.com/o"),
url-prefix("http://www.bakabt.com/m"), url-prefix("http://www.bakabt.com/s"), url-prefix("http://www.bakabt.com/i"),
url-prefix("http://www.bakabt.com/u"), url-prefix("http://www.bakabt.com/t"), url-prefix("http://www.bakabt.com/h"),
url-prefix("http://www.bakabt.com/a"), url-prefix("http://www.bakabt.com/a"), url-prefix("http://www.bakabt.com/w"),
url-prefix("http://www.bakabt.com/f"), url-prefix("http://www.bakabt.com/do"), url-prefix("http://www.bakabt.com/l"),
url-prefix("http://www.bakabt.com/1")  {

/* TEXT */

body{ background: #dbeeee !important;  font-family: Calibri, Georgia, "Trebuchet MS", Sans-Serif !important; }
a, h1, h2, h3, em, div.stats h1, div.stats h2 { color: #6485b2 !important; }
div#upper_header a, div#headbar a { color: white !important; }
div.faq a { color: red !important; }
th, th a, a.a_button { color: black !important; }
th { border-top : 1px black solid !important; }

a:hover { color: #375682 !important; }
ul#quicklinks a:hover, div#upper_header a:hover, div#headbar a:hover { color: lightblue !important; }
div.faq a:hover { color: darkred !important; }

h1, h2, h3 { font-family: Georgia, Cambria, "Times New Roman", Sans-Serif !important; }
div.searchcloud { font-size: 110% !important; }

}
