@-moz-document domain("wikipedia.org")
, domain("ja.uncyclopedia.info") {
/* template v4 2008-05-08 */
* {
background-image : none !important;
float : none !important;
text-align : left !important;
letter-spacing : 1px !important;
line-height : 1.5 !important;
min-width : 0 !important;
text-indent : 0 !important;
white-space : normal !important;
list-style-image : none !important;
}
pre {
white-space : pre !important;
overflow : auto;
}
html {
font-size : 100% !important;
background-color : #dcdcd4 !important;
}
body {
font-size : 112.5% !important;
margin : 0 auto !important;
padding : 0 !important;
background-color : #dcdcd4 !important;
color : #000 !important;
width : 40em !important;
max-width : 100% !important;
border : 0 !important;
}
table[width]
, tbody[width]
, thead[width]
, tr[width]
, td[width]
{ width : auto !important; }
noscript
, iframe
, hr
, br[clear="all"]
, #footer
, #banner
, .footer
, .banner
, img[width="1"]
, img[width="0"]
, img[src^="http://ad"]
, *[id^="ad"]
, *[class^="ad"]
, *[id*="calendar"]
, *[class*="calendar"]
, *[id*="track"]
, *[class*="track"]
{ display : none !important; }
u { text-decoration : none !important; }
a { text-decoration : underline !important; }
font[size] { font-size : 100% !important; }
table[width] { max-width : 40em !important; }
/* /template */

/* destroy div layout */
#globalWrapper
, #globalWrapper div {
font-size : 100% !important;
position : static !important;
background-color : transparent !important;
width : auto !important;
height : auto !important;
border : 0 !important;
margin : 0 !important;
padding : 0 !important;
}
#globalWrapper * {
font-size : 100% !important;
position : static !important;
background-color : transparent !important;
width : auto !important;
height : auto !important;
color : #000 !important;
}
#globalWrapper a:link { color : #0000ee !important; }
#globalWrapper a:visited { color : #551a8b !important; }
#globalWrapper a:hover { color : #ee0000 !important; }
#globalWrapper a:hover { background-color : transparent !important; }
/* /destroy div layout */

#p-lang ul li
{ display : none !important; }

#p-lang ul li.interwiki-en
, #p-lang ul li.interwiki-ja
, #column-one h5
, #p-cactions li
{ display : block !important; }

#p-cactions ul
, #p-cactions li
, #p-personal *
{ margin : 0 !important; }

#column-one h5
, #p-cactions li
, #p-cactions li a
, #p-personal *
{ padding : 0 !important; }

#p-cactions li
{ border : 0 !important; }

#globalWrapper h1
{ font-size : 150% !important; }

#bodyContent h2 span.mw-headline
{ font-size : 133.5% !important; }

#toctitle h2
{ font-size : 100% !important; }

#bodyContent h3
{ margin-bottom : 1em !important; }

#column-one h5
{ margin-top : 1em !important; }

#bodyContent h2 span.mw-headline
, #column-one h5
{ font-weight : bold !important; }

#p-cactions li a
{ font-weight : normal !important; }

#bodyContent p
, #bodyContent #catlinks
{ margin : 1em 0 !important; }

#bodyContent .editsection
{ float : right !important; }

}
