css:
#main-window[tabsontop=false] #TabsToolbar {
position:relative!important;
z-index:+99999!important;
margin-top:0px!important;
}

#main-window[sizemode=maximized][tabsontop=false] #nav-bar{
padding-top:3px!important;
padding-right: 110px!important;
padding-left: 20px!important;
margin-top:-18px !important;
margin-bottom:-8px!important;
}
#main-window[sizemode=normal][tabsontop=false] #nav-bar{
padding-right: 105px !important;
padding-left: 20px!important;
margin-top:-6px !important;
margin-bottom:-4px!important;
padding-bottom:0px!important;
z-index:+20000!important;
}


#main-window[tabsontop=false] #navigator-toolbox{
margin-top:-6px !important;
}


#main-window[sizemode=maximized][tabsontop=true] #TabsToolbar{
padding-top:2px!important;
padding-left:0px!important;
padding-right:0px!important;

}
#main-window[sizemode=normal][tabsontop=true] #TabsToolbar{
padding:10px 100px 0 20px!important;
margin-top:-19px !important;
}

#main-window[tabsontop=true] #navigator-toolbox{
margin-top:-5px !important;
}
#main-window[tabsontop=true] #TabsToolbar {
position:relative;
}
#main-window[sizemode=maximized] #appmenu-button-container{
position:fixed !important;
top:-8px!important;
left:-1px!important;
z-index:+9999;
}
#main-window[sizemode=normal] #appmenu-button-container{
position:fixed !important;
top:-12px!important;
left:0px!important;
z-index:+99999;
}
#appmenu-button-container{
position:fixed!important;
top:29px!important;
left:2px!important;
}
#main-window[sizemode=maximized] #appmenu-button{
height:26px!important;
width:30px!important;
border-bottom-right-radius:25px !important;
border-bottom-left-radius:0px !important;
border-top-left-radius:0px!important;
border-top-right-radius:0px!important;
}
#main-window[sizemode=normal] #appmenu-button{
height:26px!important;
width:30px!important;
border-bottom-right-radius:25px !important;
border-bottom-left-radius:0px !important;
border-top-left-radius:0px!important;
border-top-right-radius:0px!important;
border-left:none!important;
}

 #appmenu-button{
border:1px solid rgba(90,30,0,0.6)!important;
padding:1px 5px 1px 5px!important;
height:23px!important;
min-width:29px!important;
width:29px!important;
z-index:+1001;
border-bottom-left-radius:0px!important;
border-bottom-right-radius:0px!important;
border-top-left-radius:4px!important;
border-top-right-radius:4px!important;
background:url("data:image/png;base64,")no-repeat 5px, -moz-linear-gradient(top, #F9D685 4%, #F5B840 8%, #F6C04D 8%, #F5B840 13%, #F4B239 17%, #E4700C 75%, #E2690C 92%, #E57728 100%)!important;
}
 #appmenu-button .button-text{
display:none!important;
}
#appmenu-button dropmarker {
display:none !important;
}
