css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.c0t0d0s0.org") {

/* first make fonts/-sizes consistent */
h2, body, input, textarea, p, td, th, div, span, 
.serendipity_title, .serendipity_entry, serendipity_entryFooter, .serendipity_date, .serendipity_commentsTitle, td.serendipity_commentsLabel, .serendipity_commentDirection, table.serendipity_calendar td, table.serendipity_calendar a, .serendipitySideBarTitle
{ 
  font-family: inherit !important;
}

body, th, td { 
  font-size: 100% !important;
}

input, textarea { 
  font-size: 99% !important;
}

.homelink1, #serendipity_banner h1 { 
  font-size: 138.5% !important;
  letter-spacing: 0.5ex !important;
  font-weight: bold !important;
}

.homelink2, #serendipity_banner h2 { 
  font-size: 123.1% !important;
  letter-spacing: 0.5ex !important;
  font-weight: bold !important;
}

.serendipitySideBarTitle {
  font-size: 100% !important;  
  letter-spacing: 0.5ex !important;
}

.serendipitySideBarItem, .serendipity_entry
{
  font-size: 100% /* x-small */ !important;
}

.serendipity_title {
  font-size: 138.5% !important;  
  font-weight: bold !important;
}

.serendipity_date {
  font-size: 51% !important;  
  font-weight: bold !important;
}

.serendipity_commentsTitle,
.serendipity_comment, .serendipity_imageComment_txt,
.serendipity_commentForm, .serendipity_commentDirection,
td.serendipity_commentsValue input, td.serendipity_commentsValue select, td.serendipity_commentsValue textarea, td.serendipity_commentsLabel,
table.serendipity_calendar td, td.serendipity_weekDayName,
div.delicious-tags, div.delicious-extended
{
  font-size: 85% !important;
}

div.serendipity_entryFooter, .serendipity_center, 
.serendipity_freeTag, .serendipity_freeTag a
{
  font-size: 69% /* xx-small */ !important;
}


/* box layout / positioning */
body {
  margin: 1ex !important;
}

#serendipity_banner {
  width: auto !important;
  margin: 0 0 1ex 0 !important;
  padding: 0 !important;
  height: auto !important;
  -moz-background-size: 100% 100%;
  -o-background-size: 100% 100%;
  -webkit-background-size: 100% 100%;
  background-size: 100% 100%;
}

#serendipity_banner h1, #serendipity_banner h2 {
  padding: 0 !important;
  margin: 1ex !important;
  text-align: center;
}

.homelink1, .homelink2 {
  padding: 0 !important;
}

#mainpane {
  width: 100% !important;
  margin: 0 !important;
  border: none !important;
}

#serendipityLeftSideBar {
  width: 25% !important;
  margin-top: 0 !important;
  padding: 0 1ex 0 0 !important;
  text-align: justify !important;
}

div.serendipitySideBarContent ul {
  margin: 0 !important;
  padding-left: 2em;
  overflow: hidden;
}

#serendipity_categories_list, .category_link_all {
  padding-left: 1ex !important;
}

.serendipitySideBarItem {
  margin: 0 0 2em 0 !important;
  padding-bottom: 0 !important;
  border-bottom: none !important;
}

.serendipitySideBarTitle {
  border: 2px double /* ridge */ #DFE0E4 !important;
  padding: 1ex !important;
  margin-bottom: 1ex !important;
  overflow: hidden;
}

#serendipityQuickSearchTermField {
  width: 75% !important;
}

.quicksearch_submit {
  width: 16% !important;
}

#content {
  width: auto !important;
  padding: 0 0 0 1ex !important;
  overflow: hidden;
}

.serendipity_entrypaging {
  margin-bottom: 1em;
}

div.serendipity_Entry_Date {
  margin: 0 0 3em 0 !important;
}

#content > div:nth-last-child(2) {
  margin-bottom: 0 !important;
}

div.serendipity_title {
  padding: 0 !important;
  margin: 0 !important;
  width: auto !important;
}

.serendipity_date {
  padding: 1ex !important;
  margin: 0 0 0.3ex 2em !important;
  width: auto !important;
  float: right;
  background: #ebffeb;
}

.serendipity_entry {
  padding: 0 !important;
  margin: 0.3ex 0 0 0 !important;
  width: auto !important;
}

h4.serendipity_title:not(:first-of-type) {
  margin-top: 3em !important;
}

blockquote {
  margin: 1em !important;
  padding: 1ex !important;
  background: #fffffa;
}

ul.delicious {
  padding-left: 2em !important;
}

.serendipity_entryFooter {
  padding: 0 !important;
  margin: 1em 0 0 0 !important;
  width: auto !important;
  background: #ebffeb;
}

#content > div:last-child {
  padding: 1ex !important;
}

#content > div:last-child p {
  padding: 1ex 0 0 0 !important;
  margin: 0 !important;
}

.serendipity_image_center {
  margin: 1ex !important;
  text-align: center;
}

.serendipity_Entry_Date, .serendipity_comments {
  max-width: -moz-available;
  overflow: auto;
}

.serendipity_commentsTitle {
  width: auto !important;
  padding: 0 !important;
  margin: 1ex 0 0 0 !important;
}

.serendipity_comments {
  width: auto !important;
}

.serendipity_comment {
  width: auto !important;
  margin: 1em 0 0 0 !important;
}

.serendipity_commentsLabel {
  padding: 0 !important;
  vertical-align: middle !important;
  text-align: left !important;
}

td.serendipity_commentsValue input, td.serendipity_commentsValue select,
#serendipity_commentform_comment {
  width: 85% !important;
  /* width: -moz-available !important; */
} 

#serendipity_preview {
  margin-top: 1em;
  padding: 0 !important;
}

#serendipityRightSideBar {
  display:none;
}
}
