css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("boards.4chan.org") {
/* Saxamaphone !3.saxN0DHY presents Light Shadow (v1.0) */
BODY {
background: #EEE !important;
padding: 0 !important;
margin: 0 !important;
}

#navtop {
font: 12px "Helvetica" !important;
color: #BBB !important;
position: fixed !important;
top: 0px !important;
width: 100% !important;
left: 0px !important;
text-align: center !important;
background: rgba(255,255,255,.5) !important;
padding: 2px 0 3px 0 !important;
}

#navtop A {
color: #CCC !important;
}

#navtop A:hover {
text-shadow: 0 0 3px #CCC !important;
}

#navtopr {
display: none !important;
}

.logo {
text-align: right !important;
word-wrap: break-word !important;
}

.logo SPAN {
font: 50px "Helvetica" !important;
color: #EEE !important;
font-weight: bold !important;
text-shadow: 0 1px 0 #CCC, 0 -1px 0 #FFF !important;
margin-right: 25px !important;
}

.postarea > FORM {
margin: 0 !important;
}

.postblock {
background: #DDD !important;
border: 1px solid #CCC !important;
text-align: center !important;
-moz-border-radius: 2px !important;
}

.postblock > B {
font: 10px "Helvetica" !important;
font-weight: 100 !important;
color: #AAA !important;
text-transform: lowercase !important;
}

.postarea INPUT[type="text"] {
-moz-appearance: none !important;
font: 12px "Helvetica" !important;
color: #AAA !important;
border: 1px solid #CCC !important;
background: #DDD !important;
margin: 0px !important;
padding: 1px !important;
width: 300px !important;
}

.postarea #com_submit {
-moz-appearance: none !important;
border: 1px solid #CCC !important;
-moz-border-radius: 2px !important;
background: #DDD !important;
margin: 0 0 0 1px !important;
padding: 0 !important;
font: 10px "Helvetica" !important;
height: 17px !important;
width: 50px !important;
color: #AAA !important;
text-transform: lowercase !important;
}

.postarea TR:nth-child(3) TD {
font-size: 0 !important;
}

.postarea TR:nth-child(3) TD LABEL {
font: 10px "Helvetica";
color: #CCC;
}

.postarea TR:nth-child(3) TD LABEL > INPUT {
-moz-appearance: none !important;
border: 1px solid #CCC !important;
background: #EEE !important;
-moz-border-radius: 2px !important;
margin: 0 2px 0 2px !important;
}

.postarea TR:nth-child(3) TD LABEL > INPUT:checked {
color: #CCC !important;
}

.postarea TEXTAREA {
-moz-appearance: none !important;
font: 12px "Helvetica" !important;
line-height: 12px !important;
color: #AAA !important;
border: 1px solid #CCC !important;
background: #DDD !important;
margin: 0px !important;
padding: 1px !important;
height: 200px !important;
width: 300px !important;
}

.postarea INPUT[type="button"] {
-moz-appearance: none !important;
border: 1px solid #CCC !important;
-moz-border-radius: 2px !important;
background: #DDD !important;
margin: 0 0 0 1px !important;
padding: 0 !important;
font: 10px "Helvetica" !important;
height: 17px !important;
width: 50px !important;
color: #AAA !important;
text-transform: lowercase !important;
}

#recaptcha_table * {
height: auto !important;
padding: 0 !important;
}

#recaptcha_logo,
#recaptcha_tagline,
#recaptcha_switch_audio,
#recaptcha_whatsthis_btn {
display: none !important;
}

#recaptcha_image {
opacity: 1 !important;
border: 1px solid #CCC !important;
margin: 0 0 1px 0 !important;
width: 298px !important;
}

.postarea #recaptcha_table TR:nth-of-type(1) DIV IMG {
width: 298px !important;
}

#recaptcha_reload_btn {
position: relative !important;
right: 27px !important;
top: 21px !important;
z-index: 10 !important;
}

#recaptcha_response_field {
-moz-appearance: none !important;
font: 12px "Helvetica" !important;
line-height: 12px !important;
width: 300px !important;
padding: 1px !important;
}

IMG[src*="http://static.4chan.org/support"],
DIV[style*="width: 300px; position: absolute"],
HR,
.logo IMG,
.logo BR,
.logo FONT[size="1"],
TH[bgcolor="#e04000"],
SPAN[style="left: 5px; position: absolute;"],
.postarea .rules,
.postarea SMALL,
.postarea TR:nth-child(7),
.postarea TD:empty,
.doubledash,
SPAN:empty,
SPAN[style="left: 5px; position: absolute;"] + BR,
TABLE[align="right"],
#footer {display:none}

FORM[name="delform"] {
margin: 0 20px !important;
font: 12px "Helvetica" !important;
color: #AAA !important;
}

BLOCKQUOTE {
font: 12px "Helvetica" !important;
color: #999 !important;
background: #DDD !important;
padding: 5px !important;
-moz-border-radius: 5px !important;
}

TD.reply {
font: 12px "Helvetica" !important;
color: #999 !important;
background: #DDD !important;
border: 0 !important;
-moz-border-radius: 5px !important;
width: 1% !important;
padding: 3px 0 0 5px !important;
}

TD.replyhl {
font: 12px "Helvetica" !important;
color: #888 !important;
background: #BBB !important;
border: 0 !important;
-moz-border-radius: 5px !important;
width: 1% !important;
padding: 3px 0 0 5px !important;
}

TD.replyhl BLOCKQUOTE {
background: #BBB !important;
}

FORM[name="delform"] INPUT[type="checkbox"] {
display: none !important;
}

.filetitle, .replytitle {
font: 12px "Helvetica" !important;
color: #666 !important;
font-weight: bold !important;
}

.postername, .commentpostername {
font: 12px "Helvetica" !important;
color: #777 !important;
}

.postertrip {
font: 12px "Helvetica" !important;
color: #888 !important;
}

FONT.unkfunc {
color: #78AB46 !important;
}

.filesize {
font: 12px "Helvetica" !important;
color: #999 !important;
}

.filesize > SPAN {
font-size: 0 !important;
}

.filesize > SPAN:after {
content: attr(title) !important;
font-size: 12px !important;
}

TD .filesize > A[target="_blank"]:before {
content: " : " !important;
color: #999 !important;
}

FORM A {
font: 12px "Helvetica" !important;
color: #AAA !important;
text-decoration: none !important;
}

FORM A:hover {
text-shadow: 0 0 2px #AAA !important;
}

.omittedposts {
font: 12px "Helvetica" !important;
color: #999 !important;
}

.pages {
position: fixed !important;
bottom: 0 !important;
right: 50px !important;
background: rgba(255,255,255,.25) !important;
border: 1px solid #CCC !important;
border-bottom: 0 !important;
-moz-border-radius-topleft: 5px !important;
-moz-border-radius-topright: 5px !important;
}}

.pages TD {
font: 0 "Helvetica" !important;
padding: 2px !important;
}

.pages A {
font-size: 12px !important;
color: #AAA !important;
}

.pages B {
font-size: 12px !important;
color: #999 !important;
text-shadow: 0 0 3px #AAA !important;
}

.pages TD:first-child,
.pages FORM + TD,
.pages TD:last-child {
display: none !important;
}
