css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("lenta.ru") {

body {
  font-family: Verdana,sans-serif !important;
}

a {
  text-decoration: none !important;
}

td.statya {
  background: #fff !important;
}

td.statya table.vrezka {
  display: none !important;
}

td.statya td {
  background: #f0f0f0 !important;
  float: left !important;
  width: 80% !important;
}

td.pravaya {
  display: none !important;
}

table.top7 {
  background-color: #f0f0f0 !important;
}

table.top7 td.top6 div {
  background: #e0e0e0 !important;
  -moz-border-radius: 10px !important;
  padding: 5px !important;
}

table.top10 td.top9 div {
  background: #e0e0e0 !important;
  -moz-border-radius: 6px !important;
  padding: 0 6px 4px 6px !important;
}

table.peredovica td.glavnoe,
table.peredovica td.glavnoe2 {
  background-color: #f0f0f0 !important;
  float: left !important;
  width: 90% !important;
}

table.peredovica tr:nth-child(2) td {
  display: none !important;
}

td.last {
  width: 30% !important;
  font-size: 80% !important;
}

td.nav {
  width: 10% !important;
}

td.nav div.rubhead {
  font-size: 90% !important;
  font-weight: bold !important;
  text-decoration: none !important;
  position: relative !important;
  top: -16px !important;
  left: 100% !important;
  margin: 0 !important;
  padding: 0 !important;
}

td.nav div.rubhead a {
  color: #0066bb !important;
  font-family: "Liberation Sans",Arial,sans-serif;
  background: #f0f0f0 !important;
  padding: 4px 8px !important;
  -moz-border-radius-topleft: 8px;
  -moz-border-radius-topright: 8px;
}

td.nav div img {
  display: none !important;
}

table.top10 td.top9 div span.dt,
div.dt {
  color: #666 !important;
  background-color: #f0f0f0 !important;
  -moz-border-radius: 3px;
  padding: 0 3px !important;
}

div.hrk {
  display: none !important;
}

table.shapka td.logo {
  border-top: 0 !important;
}

td[width="2%"] {
  display: none !important;
}

td.column p {
  font: 90%/1.2 sans-serif !important;
}

div.group,
div.rsearch,
div.latest-news,
div.latest-articles,
div[style="margin: 12px 0pt 6px 18px;"] {
  display: none !important;
}

div.hrg {
  border-bottom: 0px solid black !important;
  width: 60% !important;
  padding: 40px 0 0 0 !important;
}

/* advertisement */
td[align="right"][width="26%"] {
  display: none !important;
}



}
