@-moz-document domain("esvbible.org") {
div#canvas, div#footer{min-width:1px !important;}

.jqmWindow{
background-color: white;
border: 1px solid #999;
border: 1px solid rgba(0, 0, 0, 0.3);
-webkit-border-radius: 6px;
-moz-border-radius: 6px;
border-radius: 6px;
-webkit-box-shadow: 0 3px 7px rgba(0, 0, 0, 0.3);
-moz-box-shadow: 0 3px 7px rgba(0, 0, 0, 0.3);
box-shadow: 0 3px 7px rgba(0, 0, 0, 0.3);
-webkit-background-clip: padding-box;
-moz-background-clip: padding-box;
background-clip: padding-box;
padding-bottom:35px;
}
.jqmWindow .modalWrapper {cursor:default;}
.jqmWindow iframe {
width: 100%;
height:100%;
border: none;
border-radius: 5px;
}

.modalTitleText {
border-bottom: 1px solid #EEE;
padding: 5px 15px;
}
.jqmWindow .jqmTitleText {font-size: 18px;}
.jqmWindow .jqResize{display:none;}

/*modals*/
div#col_center {
-webkit-box-shadow: rgba(0,0,0,0.5) 0 0 5px;
-moz-box-shadow: rgba(0,0,0,0.5) 0 0 5px;
box-shadow: rgba(0,0,0,0.5) 0 0 5px;
}
div#text_head {
border-bottom:1px solid #CFCFCF;
background: -moz-linear-gradient(100% 100% 90deg, #FEFEFE, #F4F4F4);
background: -webkit-gradient(linear, 0 0, 0 100%, from(#FEFEFE), to(#F4F4F4));
background: -moz-linear-gradient(100% 100% 90deg, #FEFEFE, #F4F4F4);
}
div#col_center #esv_text{margin:0 !important;}
div#esv_cols div.size {background:none !important;width:2px;}
div#esv_cols div.left ul.ui-tabs-nav, div#esv_cols div.left div.sidebar_content {
margin-right: 2px;}
div#esv_cols div.size img {display:none !important;}

.ui-widget-header{border-bottom:1px solid #eee;}
.ui-tabs .ui-tabs-nav {background:none !important;min-height:20px;}
.ui-tabs .ui-tabs-nav li a {background:white;border:none;}
.ui-tabs .ui-tabs-nav li.ui-tabs-selected a{background:white;border:none;}
.ui-state-active, .ui-widget-content .ui-state-active{background:none;}

.ui-tabs .ui-tabs-nav li a, .ui-tabs.ui-tabs-collapsible .ui-tabs-nav li.ui-tabs-selected a {border-right:none;
margin-left:0 !important;}

.ui-tabs .ui-tabs-nav li.ui-state-active a {
background:-webkit-gradient(linear, left top, left bottom, color-stop(0%,white), color-stop(100%,#F9F9F9));
border-left:1px solid rgba(0, 0, 0, 0.1);
border-right:1px solid rgba(0, 0, 0, 0.1);
}

/*point zero*/
::-webkit-scrollbar{height:16px;overflow:visible;width:16px}::-webkit-scrollbar-button{height:0;width:0}::-webkit-scrollbar-track{background-clip:padding-box;border:solid transparent;border-width:0 0 0 4px}::-webkit-scrollbar-track:horizontal{border-width:4px 0 0}::-webkit-scrollbar-track:hover{background-color:rgba(0,0,0,.05);box-shadow:inset 1px 0 0 rgba(0,0,0,.1)}::-webkit-scrollbar-track:horizontal:hover{box-shadow:inset 0 1px 0 rgba(0,0,0,.1)}::-webkit-scrollbar-track:active{background-color:rgba(0,0,0,.05);box-shadow:inset 1px 0 0 rgba(0,0,0,.14),inset -1px 0 0 rgba(0,0,0,.07)}::-webkit-scrollbar-track:horizontal:active{box-shadow:inset 0 1px 0 rgba(0,0,0,.14),inset 0 -1px 0 rgba(0,0,0,.07)}.jfk-scrollbar-dark::-webkit-scrollbar-track:hover{background-color:rgba(255,255,255,.1);box-shadow:inset 1px 0 0 rgba(255,255,255,.2)}.jfk-scrollbar-dark::-webkit-scrollbar-track:horizontal:hover{box-shadow:inset 0 1px 0 rgba(255,255,255,.2)}.jfk-scrollbar-dark::-webkit-scrollbar-track:active{background-color:rgba(255,255,255,.1);box-shadow:inset 1px 0 0 rgba(255,255,255,.25),inset -1px 0 0 rgba(255,255,255,.15)}.jfk-scrollbar-dark::-webkit-scrollbar-track:horizontal:active{box-shadow:inset 0 1px 0 rgba(255,255,255,.25),inset 0 -1px 0 rgba(255,255,255,.15)}::-webkit-scrollbar-thumb{background-color:rgba(0,0,0,.2);background-clip:padding-box;border:solid transparent;border-width:1px 1px 1px 6px;min-height:28px;padding:100px 0 0;box-shadow:inset 1px 1px 0 rgba(0,0,0,.1),inset 0 -1px 0 rgba(0,0,0,.07)}::-webkit-scrollbar-thumb:horizontal{border-width:6px 1px 1px;padding:0 0 0 100px;box-shadow:inset 1px 1px 0 rgba(0,0,0,.1),inset -1px 0 0 rgba(0,0,0,.07)}::-webkit-scrollbar-thumb:hover{background-color:rgba(0,0,0,.4);box-shadow:inset 1px 1px 1px rgba(0,0,0,.25)}::-webkit-scrollbar-thumb:active{background-color:rgba(0,0,0,0.5);box-shadow:inset 1px 1px 3px rgba(0,0,0,0.35)}.jfk-scrollbar-dark::-webkit-scrollbar-thumb{background-color:rgba(255,255,255,.3);box-shadow:inset 1px 1px 0 rgba(255,255,255,.15),inset 0 -1px 0 rgba(255,255,255,.1)}.jfk-scrollbar-dark::-webkit-scrollbar-thumb:horizontal{box-shadow:inset 1px 1px 0 rgba(255,255,255,.15),inset -1px 0 0 rgba(255,255,255,.1)}.jfk-scrollbar-dark::-webkit-scrollbar-thumb:hover{background-color:rgba(255,255,255,.6);box-shadow:inset 1px 1px 1px rgba(255,255,255,.37)}.jfk-scrollbar-dark::-webkit-scrollbar-thumb:active{background-color:rgba(255,255,255,.75);box-shadow:inset 1px 1px 3px rgba(255,255,255,.5)}.jfk-scrollbar-borderless::-webkit-scrollbar-track{border-width:0 1px 0 6px}.jfk-scrollbar-borderless::-webkit-scrollbar-track:horizontal{border-width:6px 0 1px}.jfk-scrollbar-borderless::-webkit-scrollbar-track:hover{background-color:rgba(0,0,0,.035);box-shadow:inset 1px 1px 0 rgba(0,0,0,.14),inset -1px -1px 0 rgba(0,0,0,.07)}.jfk-scrollbar-borderless.jfk-scrollbar-dark::-webkit-scrollbar-track:hover{background-color:rgba(255,255,255,.07);box-shadow:inset 1px 1px 0 rgba(255,255,255,.25),inset -1px -1px 0 rgba(255,255,255,.15)}.jfk-scrollbar-borderless::-webkit-scrollbar-thumb{border-width:0 1px 0 6px}.jfk-scrollbar-borderless::-webkit-scrollbar-thumb:horizontal{border-width:6px 0 1px}::-webkit-scrollbar-corner{background:transparent}body::-webkit-scrollbar-track-piece{background-clip:padding-box;background-color:#f5f5f5;border:solid #fff;border-width:0 0 0 3px;box-shadow:inset 1px 0 0 rgba(0,0,0,.14),inset -1px 0 0 rgba(0,0,0,.07)}body::-webkit-scrollbar-track-piece:horizontal{border-width:3px 0 0;box-shadow:inset 0 1px 0 rgba(0,0,0,.14),inset 0 -1px 0 rgba(0,0,0,.07)}body::-webkit-scrollbar-thumb{border-width:1px 1px 1px 5px}body::-webkit-scrollbar-thumb:horizontal{border-width:5px 1px 1px}body::-webkit-scrollbar-corner{background-clip:padding-box;background-color:#f5f5f5;border:solid #fff;border-width:3px 0 0 3px;box-shadow:inset 1px 1px 0 rgba(0,0,0,.14)}

/*scroll end */
.cke_skin_kama .cke_contents iframe {display:block;}
.cke_1[style]{width:100%!important;}
#cke_6 > .cke_toolgroup:last-of-type{display:none !important;}

.form-bubble {
margin-left:-6px;
margin-right:5px;
padding: 3px 8px;
font-size:11px;
line-height:18px;
background-color: black;
border:none;
color: white;
text-align: center;
max-width: 200px;
text-decoration: none;
-webkit-border-radius: 4px;
-moz-border-radius: 4px;
border-radius: 4px;
box-shadow:none;
-moz-box-shadow:none;
-webkit-box-shadow:none;
-webkit-transition: opacity 0.15s linear;
-moz-transition: opacity 0.15s linear;
-ms-transition: opacity 0.15s linear;
-o-transition: opacity 0.15s linear;
transition: opacity 0.15s linear;
filter: alpha(opacity=90);
-khtml-opacity: 0.9;
-moz-opacity: 0.9;
opacity: 0.9;
}

.form-bubble .form-bubble-pointer-right,
.form-bubble .form-bubble-pointer-top-right,
.form-bubble .form-bubble-pointer-left,
.form-bubble .form-bubble-pointer-top-left,
.form-bubble .form-bubble-pointer-bottom-left{background:none;margin:0;  position: absolute;
  width: 0;
  height: 0;}

.form-bubble .form-bubble-pointer-left,
.form-bubble .form-bubble-pointer-top-left,
.form-bubble .form-bubble-pointer-bottom-left {
top: 50%;
left: -19px;
margin-top: -5px;
border-top: 5px solid transparent;
border-bottom: 5px solid transparent;
border-right: 5px solid black;
}

.form-bubble .form-bubble-pointer-right,
.form-bubble .form-bubble-pointer-top-right,
.form-bubble .form-bubble-pointer-bottom-right {
top: 50%;
right: -19px;
margin-top: -5px;
border-top: 5px solid transparent;
border-bottom: 5px solid transparent;
border-left: 5px solid black;
}
.sidebar_content .form-bubble .form-bubble-pointer-top-right,
.sidebar_content .form-bubble .form-bubble-pointer-top-left{top:16%;}

html{background:none #fff;}

#highlight_menu {
    background:none !important;
    height: 18px !important;
}

