div#main.single_page #content #esv_text h2.extra_text{ margin:0 0 5px 0 !important; }
#main[style]{overflow:hidden;margin-top:-2px !important;border-top:1px solid #eee;}
#esv_cols[style]{margin-top:-2px !important;overflow:hidden !important;}
#footer{background:#222 !important;}

#crossrefJqmFooter{ display:none; }

div#header{margin-bottom:1px;height:auto !important;overflow:auto;}
div#main_nav {margin-top: 2px;margin-bottom: 3px;}
div#resourcepage h3:first-of-type {margin-top:0 !important;padding-top:0 !important;}

#shareDialog, #highlightCopy {top: 10% !important;}
#copyTools {width:199px !important;}
#copyContent { height:225px; }
#shareButton{ margin:10px; }
#shareTo{margin:0 10px 0 10px !important; }


#id_search{padding: 1px 0 2px 5px !important;}

#my_notes_tab .advanced_search{display:none !important;}
#my_notes{margin-top:15px;}
div#main.single_page{ margin:0 !important; overflow:hidden; }
div#main.single_page #content{ margin:0 !important; width:100% !important; }

#sub_pager{ border-bottom:1px solid #C7C5C5; border-top:1px solid #C7C5C5; padding:8px 0 8px 0 !important; }
#sub_pager #book_page h2{ width:110px; }

#pager_col{width:23% !important;margin-right:20px !important;margin-left:20px !important;}
#pager table{ width:100% !important; }

#search_results h1{ margin:5px 0 5px 0 !important; }
#results_list{ margin:10px 0 0 0 !important; }
#results_list li{ margin:0 0 7px 0 !important; padding:0 0 7px 0 !important; border-bottom:1px solid #C7C5C5; }
#results_list li:last-child{ border-bottom:none; }
#results_list li h3{ margin:0 !important; }
#results_list li p.indent{ text-indent: 1.2em; }
#results_list li p{ font-size: 123% !important; line-height: 130% !important; font-family:'Calibri','Trebuchet MS','Verdana','Helvetica',Arial,sans-serif; margin-bottom:0 !important; }


.note_ref{ height: 19px; border-top: 1px solid #ccc; border-bottom: 1px solid #ccc; padding: 2px !important; margin: 0; overflow: hidden; background: #f8faff; }
.view_my_note, .view_my_note p { font-size:93% !important; line-height:1.5em; }
.my_note_form .edit_passage {
    border: 1px solid #D3D3D3 !important;
	padding: 2px 4px !important;
    border-radius: 5px 5px 5px 5px;
    width: 94%;
}

/*'Calibri','Trebuchet MS',*/
.esv-text{font-family:Calibri,tahoma,Verdana,'Helvetica',Arial,sans-serif !important;font-size:17px !important; line-height:28px !important;}

.book-name { padding-bottom:4px !important; }
.esv-text .chapter-num { font-family:'Calibri','Trebuchet MS','Verdana','Helvetica',Arial,sans-serif !important; line-height: 0.8 !important; color:#CCCCCC; display:block; float:left; font-size:50px; margin:0 !important;}
.esv-text span.verse-num { font-family: Helvetica,Arial,sans-serif !important; line-height: 1.5em !important; padding-right:0 !important; }
.esv-text a.cf {line-height: 1.4em !important; font-weight:normal !important; font-size: 0.7em !important; padding-bottom: 0.6em !important; }
.esv-text span.footnote .fn { line-height: 1.4em !important; font-weight:normal !important; font-size: 0.7em !important; }
.esv-text h3{ color:#A6A6A6 !important; font-weight:bold !important; font-style:normal !important; font-family:Calibri,Verdana,Arial,sans-serif; }
.esv-text h2, .esv-text h3 { margin-left:0 !important; }



#highlight_toolbar{ background:-moz-linear-gradient(-90deg, rgba(0, 0, 0, 0.7), rgba(0, 0, 0, 0.9))!important;
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,rgba(0,0,0,0.8)), color-stop(100%,rgba(0,0,0,0.9))) !important;
padding:4px !important; border: 1px solid black; border-radius: 5px; box-shadow: 0 0 1px white inset, 0 0 3px #111111; text-shadow: 0 -1px 0 #333333;}
#highlight_toolbar .highlight-inner{padding:0;}
#highlight_toolbar .search{display:none;}

#highlight_toolbar a.text:hover{ text-decoration:underline !important; }
#highlight_toolbar a.highlightClose { background-image:none !important; height: 15px; position: absolute; right: 0; top: 0; width: 15px;}
#highlight_toolbar a.highlightClose:after{ content: "x"; color:#fff;}
#highlight_toolbar span.passage{padding-left:0 !important;}

#highlight_toolbar span.passage a {font-size: 14px ;margin-left:4px; text-decoration:none !important; color: #fff!important; font-weight: bold;}


ul.commands{clear:both;}
ul.commands li{text-transform: uppercase; font-size: 9px !important; float:left; display:block; clear:none !important; padding:0 !important;margin-bottom:3px !important;}
#highlight_toolbar .prompt{display:none !important;}
div#highlight_toolbar a.text:hover{text-decoration:underline !important;}
div#highlight_toolbar a.text{margin-left:5px !important;text-decoration:none !important;top:0 !important;}
}
