css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("multitran.ru") {


body table tr td table tr td table tr td {
  border-bottom: 1px dashed #ddd !important;
}

body table tr td table tr td table tr:hover {
  background: #eee !important;
}

td[width="10%"] {
  width: 15% !important;
}

td[width="80%"] {
  width: 70% !important;
}

body table tr td:nth-child(2) table:nth-child(3) {
  display: none !important;
}

body table tr td:nth-child(2) [valign="baseline"] {
  width: 0 !important;
}

body table tr td:nth-child(2) table:first-child {
  display: none !important;
}

body table {
  width: 100% !important;
}

div.englishnatali {
  display: none !important;
}

object {
  display: none !important;
}

a {
  color: #115588 !important;
}

body {
  background: #fcfcfc !important;
  margin: 0 !important;
  padding: 0 !important;
}

td {
  font: normal 90%/125% sans-serif !important;
}

td[bgcolor="#dbdbdb"] {
  background: #eee3dc !important;
  padding: 6px 3px !important;
}

td[bgcolor="#dbdbdb"] a:nth-child(1) {
  font: normal 110% sans-serif !important;
  color: #b00000 !important;
}

td[bgcolor="#dbdbdb"] img {
  height: auto !important;
  width: auto !important;

}

img {
  height: 45px !important;
  width: 142px !important;
}

img[width="1"] {
  display: none !important;
}

.topnavy a {
  font: normal 50% sans-serif !important;
  color: #cccccc !important;
}

input {
  font-size: 120% !important;
}

input#s {
  width: 80% !important;
}

form#translation {
  position: relative !important;
  top: -14px !important;
}

tr[height="5"][colspan="2"] {
  display: none !important;
}

input[name="q"], input[name="sa"] {
  display: none !important;
}

}
