css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("joindiaspora.com"), domain("diasp.org"), domain("geraspora.de") {
/* PLEASE REPLACE JOINDIASPORA.COM WITH THE DOMAIN NAME OF YOUR POD ABOVE if not in the list^ */

/* ------Header color------ */
header{
background:-moz-linear-gradient(center bottom,#ffffff 1%,#000066 12%) !important;
background:-webkit-gradient(linear,0% 0,0% 100%,from(#000066),to(#fff),color-stop(0.9,#000066));
}

/* ------User menu------ */
#user_menu {
background-color:#e1ebed !important;
-moz-box-shadow:0 1px 2px #fff !important;
color:#006 !important;
}

#user_menu a{
color:#000 !important;
text-shadow: #e1ebed 1px -1px 4px !important;
}

user_menu.active{
background-color:#E1EBED !important;
-moz-box-shadow:0 1px 2px #000060 !important;
}


#user_menu.active a{
color:#000 !important;
text-shadow: #ccc 0px -0px 4px;
}

#user_menu.active a:hover{
background-color:#B6BEE0 !important;
}

/* ------Aspect tabs------ */
#aspect_nav{
margin-bottom:2px !important;
}

#aspect_nav a:hover {
background-color:#fff !important;
color:#333 !important;
text-shadow: #fff 0px -0px 4px !important;
}

/* ------Search bar------ */

/* ------Links on login page------ */
#landing_nav a {
color:#fff !important;
}

}
