css:
@namespace url("http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul");

@-moz-document url("chrome://browser/content/browser.xul"){

window[sizemode='normal'] {
	border-top: none !important;
	border-left: 4px solid transparent !important;
	border-right: 4px solid transparent !important;
	border-bottom: 4px solid transparent !important;
}

window[sizemode='maximized'] {
	border: none !important;
}

window[active="true"] resizer {
	border: none !important;
	background-color:#6096CF!important;
}

window:not([active="true"]) resizer {
	border: none !important;
	background-color:#E0DFDF!important;
}

resizer.frame-resizer {
	opacity: 1 !important;
	border: none !important;
	-moz-appearance:none !important;
	background-image: none !important;
	-moz-border-radius:0px; 
	background-color:transparent!important;
	position:fixed;
}

resizer.frame-resizer[dir="top"] {
	height: 4px!important;
	border-bottom:none !important;
	cursor: n-resize!important;
	-moz-border-radius-topleft:5px!important; 
	-moz-border-radius-topright:5px!important;

}

window[active="true"] resizer.frame-resizer[dir="top"] {
	border-left:1px solid #838383!important;
	border-right:1px solid #838383!important;
	border-top: 1px solid #838383 !important;
}

window:not([active="true"]) resizer.frame-resizer[dir="top"] {
	border-left:1px solid #c7c7c7!important;
	border-right:1px solid #c7c7c7!important;
	border-top: 1px solid #c7c7c7 !important;
}

resizer.frame-resizer[dir="left"] {
	width: 4px!important;
	border-right:none !important;
	cursor: w-resize !important;
	-moz-border-radius-topleft:5px!important; 
	-moz-border-radius-bottomleft:5px!important;
}

window[active="true"] resizer.frame-resizer[dir="left"] {
	border-top:1px solid #838383!important;
	border-bottom:1px solid #838383!important;
	border-left: 1px solid #838383 !important;
}

window:not([active="true"]) resizer.frame-resizer[dir="left"] {
	border-top:1px solid #c7c7c7!important;
	border-bottom:1px solid #c7c7c7!important;
	border-left: 1px solid #c7c7c7 !important;
}

resizer.frame-resizer[dir="right"] {
	width: 4px!important;
	border-left:none !important;
	cursor: e-resize!important;
	-moz-border-radius-topright:5px!important; 
	-moz-border-radius-bottomright:5px!important;
}

window[active="true"] resizer.frame-resizer[dir="right"] {
	border-top:1px solid #838383!important;
	border-bottom:1px solid #838383!important;
	border-right: 1px solid #838383 !important;
}

window:not([active="true"]) resizer.frame-resizer[dir="right"] {
	border-top:1px solid #c7c7c7!important;
	border-bottom:1px solid #c7c7c7!important;
	border-right: 1px solid #c7c7c7 !important;
}

resizer.frame-resizer[dir="bottom"] {
	height: 4px!important;
	border-top:none !important;
	cursor: s-resize!important;
	-moz-border-radius-bottomleft:5px!important; 
	-moz-border-radius-bottomright:5px!important;
}

window[active="true"] resizer.frame-resizer[dir="bottom"] {
	border-bottom:1px solid #838383!important;
	border-left:1px solid #838383!important;
	border-right: 1px solid #838383 !important;
}

window:not([active="true"]) resizer.frame-resizer[dir="bottom"] {
	border-left:1px solid #c7c7c7!important;
	border-bottom:1px solid #c7c7c7!important;
	border-right: 1px solid #c7c7c7 !important;
}

resizer.frame-resizer[dir="topleft"] {
	width: 8px!important;
	height: 8px!important;
	border-bottom:none!important;
	border-right:none!important;
	cursor: nw-resize!important;
	background-color:transparent!important;
	-moz-border-radius-topleft:5px!important; 
}

window[active="true"] resizer.frame-resizer[dir="topleft"] {
	border-top: 1px solid #838383 !important;
	border-left: 1px solid #838383 !important;
}

window:not([active="true"]) resizer.frame-resizer[dir="topleft"] {
	border-top:1px solid #c7c7c7 !important;
	border-left:1px solid #c7c7c7 !important;
}

resizer.frame-resizer[dir="topright"] {
	width: 8px!important;
	height: 8px!important;
	border-bottom:none!important;
	border-left:none!important;
	cursor: ne-resize!important;
	background-color:transparent!important;
	-moz-border-radius-topright:5px!important; 
}

window[active="true"] resizer.frame-resizer[dir="topright"] {
	border-top: 1px solid #838383 !important;
	border-right: 1px solid #838383 !important;
}

window:not([active="true"]) resizer.frame-resizer[dir="topright"] {
	border-top:1px solid #c7c7c7 !important;
	border-right:1px solid #c7c7c7 !important;
}

resizer.frame-resizer[dir="bottomleft"] {
	width: 8px!important;
	height: 8px!important;
	border-top:none!important;
	border-right:none!important;
	cursor: sw-resize!important;
	background-color:transparent!important;
	-moz-border-radius-bottomleft:5px!important; 
}

window[active="true"] resizer.frame-resizer[dir="bottomleft"] {
	border-left:1px solid #838383!important;
	border-bottom:1px solid #838383!important;
}

window:not([active="true"]) resizer.frame-resizer[dir="bottomleft"] {
	border-left:1px solid #c7c7c7!important;
	border-bottom:1px solid #c7c7c7!important;
}

resizer.frame-resizer[dir="bottomright"] {
	width: 8px!important;
	height: 8px!important;
	border-top:none!important;
	border-left:none!important;
	cursor: se-resize!important;
	background-color:transparent!important;
	-moz-border-radius-bottomright:5px!important; 
}

window[active="true"] resizer.frame-resizer[dir="bottomright"] {
	border-right:1px solid #838383!important;
	border-bottom:1px solid #838383!important;
}

window:not([active="true"]) resizer.frame-resizer[dir="bottomright"] {
	border-right:1px solid #c7c7c7!important;
	border-bottom:1px solid #c7c7c7!important;
}

#main-window {
	margin-left:0px!important;
	margin-right:0px!important;
	margin-bottom:0px!important;
}

window[sizemode='maximized'] #main-window{
	border:none!important;
}

#main-window[sizemode='normal'] .tabbrowser-strip{
	top:4px !important;
	left:3px!important;
	right:3px!important;
}

#main-window[sizemode='maximized'] .tabbrowser-strip{
	top:0px !important;
	left:0px!important;
	right:0px!important;
}

.tabbrowser-strip{
	position:fixed !important;
	width:auto !important;
}

.tabbrowser-strip .tabbrowser-tabs{
	background-color:transparent !important;
	width:100% !important;
}

.tab-drop-indicator-bar{
	position:fixed!important;
	top:4px !important;
	left:0px !important;
	width:100%!important;
	z-index:5000!important;
}

.tabbrowser-tabs{
	background: none !important;
}

window:not([active="true"]) .tabbrowser-strip{
	background-image:url("chrome://browser/skin/tabbrowser/tabstrip-bg.png")!important;
	background-position:bottom !important;
	background-color:#E0DFDF !important;

}

window[active="true"] .tabbrowser-strip{
	background-image:url("chrome://browser/skin/tabbrowser/tabstrip-bg.png")!important;
	background-position:bottom !important;
	background-color:#6096CF !important;
}

window[active="true"] .tabbrowser-tabs{
	opacity:1!important;
}

window[active="true"] .tabs-container{
	opacity:1!important
}

window:not([active="true"]) .tabbrowser-tabs{
	opacity:0.75!important;
}

window:not([active="true"]) .tabs-container{
	opacity:0.75!important
}

#navigator-toolbox {
	margin-bottom:0px !important;
	padding-top:0px !important;
}

#main-window[sizemode='maximized'] #navigator-toolbox {
	margin-top:28px !important;
}

#main-window[sizemode='normal'] #navigator-toolbox {
	margin-top:30px !important;
}

#fullscr-toggler {
	min-height: 2px !important;
	height: 2px !important;
	max-height: 2px !important;
	border: 0 !important;
	padding: 0 !important;
	margin: -30px 0 0px 0!important;
}

/*.tabs-bottom {
visibility: hidden !important;
}
*/

/*PREVENTS SHIFTING WHEN HOVERED FIR UNSELECTED TABS */
tab:hover {
	padding-top: 0px !important;
}

/*PREVENTS SHIFTING WHEN HOVERED FIR SELECTED TAB*/
tab[selected="true"]:hover {
	padding-top: 0px !important;
}

#ff-controls {
	margin:0px!important;
	padding:0px!important;
}

#ff-controls toolbarbutton{
	-moz-appearance: none!important;
	-moz-box-align: stretch;
	-moz-box-pack: start;
	background: none transparent!important;	
	border: none;
	padding: 0px;
	margin: 0px 0px 0px 0px!important;
	z-index: 500;
}

#main-window[sizemode='normal'] #ff-controls{	
	margin:-11px -4px 0px 0px !important;
}

#main-window[sizemode='maximized'] #ff-controls{	
	margin:-11px -4px 2px 0px !important;
}

#ff-minimize-button{
	list-style-image: url("data:image/png;base64,") !important;
	-moz-image-region: rect(0px 27px 18px 0px)!important;

}
#ff-minimize-button:hover {
	-moz-image-region: rect(0px 54px 18px 27px)!important;
}
#ff-minimize-button:active {
	-moz-image-region: rect(0px 81px 18px 54px)!important;
}

#ff-maximize-button{
	list-style-image: url("data:image/png;base64,%3D%3D") !important;
	-moz-image-region: rect(0px 25px 18px 0px)!important;
}

#ff-maximize-button:hover{
	-moz-image-region: rect(0px 50px 18px 25px)!important;
}
#ff-maximize-button:hover:active{
	-moz-image-region: rect(0px 75px 18px 50px)!important;
}

#main-window[winstate="1"] #ff-maximize-button{
	list-style-image: url("data:image/png;base64,") !important;
}

#ff-close-button{
	list-style-image: url("data:image/png;base64,%3D%3D") !important;
	-moz-image-region: rect(0px 46px 18px 0px)!important;
	opacity:.8;
}
#ff-close-button:hover{
	-moz-image-region: rect(0px 92px 18px 46px)!important;
	opacity:1;  
}
#ff-close-button:hover:active {
	-moz-image-region: rect(0px 138px 18px 92px)!important;
	opacity:1;
}


#ff-fullscreen-button{
	list-style-image: url("data:image/png;base64,") !important;
	-moz-image-region: rect(0px 16px 16px 0px)!important;
}

#ff-fullscreen-button:hover {
	-moz-image-region: rect(0px 32px 16px 16px)!important;
}

#ff-fullscreen-button:hover:active {
	-moz-image-region: rect(0px 48px 16px 32px)!important;
}

}
