css:
.balance { width: 100% !important; }


.profile .avatar { width: 100px !important; height: 100px !important; margin: 0 10px 0 0 !important; text-align: center !important; background: #e9e9e9 !important; overflow: hidden !important; }
.profile .avatar img { width: auto !important; height: 100px !important; }
.facebookAvatarLogo { display: none !important; }
.inappropriate { display: none !important; }
.sidebar { display: none !important; }
#FORUMS_FORM { display: none !important; }

.crumbs li li { font-size: 1.4em !important; }
.crumbs ul li ul li { float: left !important; }
.crumbs a[href='/ForumHome'] { font-size: 1em !important; }
.crumbs ul { }
.crumbs ul li, .crumbs ul li ul { display: block !important; }

#SHOW_FORUMS_TABLE td a { font-weight: normal !important; text-decoration: none !important; color: #555555 !important; }
#SHOW_FORUMS_TABLE td b a { font-weight: bold !important; text-decoration: underline !important; color: #202D95 !important; }
#SHOW_FORUMS_TABLE td b a:visited { font-weight: normal !important; text-decoration: none !important; color: #333 !important; }
#SHOW_FORUMS_TABLE a:hover { background: #202D95 !important; color: #fff !important; text-decoration: none !important; }
#SHOW_FORUMS_TABLE td b a:hover { background: #202D95 !important; color: #fff !important; text-decoration: none !important; }
#SHOW_FORUMS_TABLE a { padding: 1px !important; }
#PAGE { width: 100% !important; background: #669933 !important; }
#BODYCON { width: 98% !important; margin-right: 14px !important; }


.topicTopCommerce, .ad { display: none !important; }
