css:
#navigator-toolbox[sizemode="maximized"][tabsontop="true"] #TabsToolbar 
{ 
     padding-left: 0px !important; 
     padding-top: 1px !important; 
     padding-right: 0px !important; 
}

#navigator-toolbox[tabsontop="true"] #TabsToolbar 
{ 
     padding-left: 0px !important; 
     padding-top: 1px !important; 
     padding-right: 0px !important; 
} 

#main-window[sizemode="maximized"][tabsontop="true"] #appmenu-button 
{ 
     padding: 3px !important; 
}

#main-window[tabsontop="true"] #appmenu-button 
{ 
     padding: 3px !important; 
}

#main-window[sizemode="maximized"] #navigator-toolbox[tabsontop="true"] #TabsToolbar 
{ 
     padding-left: 68px !important; 
     padding-top: 6px !important; 
     margin-right: 110px !important; 
}
#main-window #navigator-toolbox[tabsontop="true"] #TabsToolbar 
{ 
     padding-left: 68px !important; 
     padding-top: 10px !important; 
     margin-right: 110px !important; 
}

#appmenu-button
{
    border: 1px solid rgba(90,30,0,0.8)!important;
    border-top:none!important;
    padding: 1px 5px 1px 5px !important;
    height: 22px !important;
    min-width: 64px !important;
    width: 64px !important;
    -moz-box-shadow: 0 0 3px rgba(255,255,255,.5) inset, 0 0 1px rgba(255,250,200,0.8) inset, 0 0 1px rgba(255,250,200,0.8) inset, 0 0 1px rgba(255,250,200,0.8) inset, 0 0 1px rgba(255,250,240,0.5) !important;
}

#appmenu-button dropmarker
{ 
    margin: 0 1px 0 0 !important; 
}

.tabbrowser-tab[selected="true"]
	{background-image: -moz-linear-gradient(rgba(255, 255, 255, .78), rgba(255, 255, 255, .35)),
                           -moz-linear-gradient(rgba(255, 255, 255, .6), rgba(255, 255, 255, 0) 31%),
                           -moz-linear-gradient(RGBA(212, 208, 200, 0), RGBA(212, 208, 200, 0)) !important; }

#main-window[tabsontop="true"] #navigator-toolbox > *:not([id="TabsToolbar"]):not([id="toolbar-menubar"]),
#main-window:not([tabsontop="true"]) #navigator-toolbox > *:not([id="nav-bar"]):not([id="toolbar-menubar"]),
#sidebar-header
	{background-color: RGBA(212, 208, 200, 0) !important; }

.tabbrowser-tab:not([selected="true"]):not([titlechanged="true"]),
.tabs-newtab-button
	{-moz-box-shadow: RGBa(212, 208, 200, .1) 0 0PX 0,
                          rgba(50, 50, 50, .25) 0 1px 0 !important; 
         background-image: -moz-linear-gradient(RGBa(212, 208, 200, .3) 000%, rgba(0, 0, 0, .0) 000%),
                           -moz-linear-gradient(rgba(153, 153, 153, 0.45), rgba(114, 114, 114, 0.45) 50%) !important; }
#main-window[tabsontop="true"]  #nav-bar
	{background-clip: padding-box !important; 
         border-top: 1px solid transparent !important; 
         -moz-box-shadow: RGBa(212, 208, 200, 1) 0 0 0 -0PX inset,
                          rgb(160, 160, 160) 0 0 0 -0PX inset !important; }

#main-window[tabsontop="true"][lwtheme="true"]  #nav-bar
	{border-top: none !important; }

#main-window[tabsontop="true"]  #navigator-toolbox
	{border-bottom: 1px solid transparent !important;
         -moz-box-shadow: RGBa(212, 208, 200, .25) 0 0 0 -0PX inset,
                          rgb(160, 160, 160) 0 0 0 -1px inset !important; }

.tabbrowser-tab:not([selected="true"]):not([titlechanged="true"]):hover,
.tabs-newtab-button:hover
	{-moz-box-shadow: RGBa(212, 208, 200, .1) 0 0PX 0,
                          rgba(80, 80, 80, .25) 0 1px 0 !important; 
         background-image: -moz-linear-gradient(RGBa(212, 208, 200, .6) 000%, rgba(0, 0, 0, .0) 000%),
                           -moz-linear-gradient(rgba(204, 204, 204, 0.55), rgba(153, 153, 153, 0.55) 50%) !important; }

#sidebar-header
	{-moz-appearance: none !important;
         background-image: -moz-linear-gradient(rgba(255, 255, 255, 0.45), rgba(255, 255, 255, 0)) !important;
         border-top: none !important; }

#navigator-toolbox label:not([class^="menu-"]):not([class="tab-text"]):not([id^="identity-icon"]),
#sidebar-header
	{text-shadow: rgb(255, 255, 255) 0px 0px 8.4px, 
                      rgb(255, 255, 255) 0px 0px 16px,  
                      rgb(255, 255, 255) 0px 0px 16px, 
                      rgba(255, 255, 255, 0.4) 0px 1px 0px !important; }

#main-window[tabsontop="true"] .tabbrowser-tab
        {text-shadow: rgb(255, 255, 255) 0px 0px 16px, 
                      rgba(255, 255, 255, 0.4) 0px 1px 0px !important; }

#main-window[tabsontop="true"] .tabbrowser-tab[selected="true"]
	{text-shadow: rgb(255, 255, 255) 0px 0px 8.4px, 
                      rgba(255, 255, 255, 0.4) 0px 1px 0px !important; }

#browser-bottombox:not(:-moz-lwtheme), 
#status-bar,
statusbarpanel,
#addon-bar,
#status4evar-status-text
{ -moz-appearance: none !important;
background: transparent !important; 
padding: 0 !important;
margin: 0 !important;
border: none !important;
-moz-box-shadow: none !important;
background: none !important;
font-family: Segoe UI, sans serif !important;
font-size: 12px !important;
/*font-weight: bold !important;*/
color: #B6D0D6 !important;
text-shadow: 1px 1px 1px black, 1px 1px 1px black, 1px 1px 4px #B2C7CF !important; }

#addon-bar
{ margin: 0px 0 -4px 8px !important; }

#appmenu-button-container {
position: fixed !important;
}

#appmenu-button:-moz-window-inactive {
-moz-box-shadow: 0 1px 0 rgba(255,255,255,.25) inset, 0 0 0 1px rgba(255,255,255,.35) inset !important;
background-color: transparent !important;
}

#appmenu-button dropmarker:before {
content: "" !important;
}

#toolbar-menubar { margin: -30px 100px 0px 100px;}
#navigator-toolbox[tabsontop="true"] #TabsToolbar { margin: 0 0px 0px;}

#appmenu-button {
padding: 3px 10px !important;
margin-top: -1px !important;
margin-left: 1px !important;
background-color: transparent !important;
border: 2px solid !important;
border-top: none !important;
-moz-border-radius: 0 0 4px 4px !important;
-moz-border-left-colors: rgba(255,255,255,.5) rgba(83,42,6,.9) !important;
-moz-border-bottom-colors: rgba(255,255,255,.5) rgba(83,42,6,.9) !important;
-moz-border-right-colors: rgba(255,255,255,.5) rgba(83,42,6,.9) !important;
-moz-box-shadow: 0 1px 0 rgba(255,255,255,.25) inset, 0 0 0 1px rgba(255,255,255,.40) inset !important;
}

#appmenu-button:-moz-window-inactive {
-moz-box-shadow: 0 1px 0 rgba(255,255,255,.25) inset, 0 0 0 1px rgba(255,255,255,.35) inset !important;
background-color: transparent !important;
}

#appmenu-button:hover:not(:active):not([open]),
#appmenu-button:hover:-moz-window-inactive:not(:active):not([open]) {
-moz-box-shadow: 0 1px 0 rgba(255,255,255,.1) inset, 0 0 2px 1px rgba(250,234,169,.7) inset, 0 -1px 0 rgba(250,234,169,.5) inset !important;
}


#appmenuPrimaryPane {
    background-color: #FFFFFF;

}
#appmenuSecondaryPane {
    background-color: #F1F5FB;
    -moz-box-shadow: 20px 0 20px -30px black inset;
}
