css:
@-moz-document domain('pinboard.in'), url-prefix('http://pinboard.in/add'), url-prefix ('http://pinboard.in/u:lwallenstein/organize/') {
form {
            background: #FFFFFF;
            border: 8px solid #e5e5e5;
}
#instructions { color: #fff !important }
}
@-moz-document domain('pinboard.in'), url-prefix('http://pinboard.in') {

    body {
        background-color: #3d332a;
        font: 76%/150% "Lucida Grande", "Lucida Sans Unicode", "Lucida Sans", Arial, sans-serif;
        margin: 0;
        padding: 15px 0 0 0;
    }
        a, a:link {
            color: #3d332a;
        }
        a:visited {
            color: #42423d;
        }
        a:hover {
            text-decoration: underline;
        }
        h2, h3, .settings_heading, td.settings_heading {
            background: #e9e7e0;
            color: #a0998d;
        }
        #content {
            background: #FFFFFF;
            border: 8px solid #e5e5e5;
            clear: left;
            display: inline-block;
            height: 100%;
            padding: 15px;
        }
            #content:after {
            	content: ".";
            	display: block;
            	clear: both;
            	visibility: hidden;
            	line-height: 0;
            	height: 0;
            }
            html[xmlns] #content {
            	display: block;
            }
             
            * html #content {
            	height: 1%;
            }        
            #banner {
                background: #42423d;
                border-bottom: 8px solid #3b3b36;
                color: #fff;
                padding: 10px;
            }
                #logo {}
                    #logo .pin_logo {
                        display: none;
                    }
                    #logo #pinboard_name {
                        background: url(data:image/png;base64,) center left no-repeat;
                        height: 14px;
                        line-height: 14px;
                        padding-left: 16px;
                    }
                    #timer {
                        color: #fff;
                    }
                #banner a, #banner a.banner_username{
                    color: #fff;
                }
            #main_column {
            }
                .bookmark {
                    border-bottom: 1px dashed #ebecee;
                    margin-bottom: 10px;
                }
                    .star {
                        margin-left: 0;
                    }
                    .display {
                        float: right;
                        width: 580px;
                    }
                        .bookmark_title {
                            font-size: 15px;
                        }
                    .edit_links {
                        opacity: 1;
                        float: right;
                        text-align: right;
                    }
                        .edit_links a {
                            color: #d1cfc9;
                        }
                            .edit_link a:hover {
                                font-weight: bold;
                            }
                    .tabs {
                        background: none;
                    }
                        .tab {
                            background: #e9e7e0;
                            border-bottom: none;
                            margin-right: 10px;
                        }
                        .tab_selected {
                            background: #fff;
                        }
                        .tab_spacer {
                            display: none;
                        }
                    #tab_panes {
                        border-top: 1px solid #aaa ;   
                    }
            #right_bar {
                background: #e9e7e0;
                border-bottom: 8px solid #d1cfc9;
                float: right;
                padding: 10px;
            }
                #right_bar a:link, #right_bar a {
                    color: #a0998d;
                    font-weight: bold;
                }
                     
}
