css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("www.wowwiki.com") {
/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */

/* #########################################################################   
   User Config >>                                                              
   - Configure some details if you want.                                       
   ######################################################################### */

	/* facebook icon "like" -------------- */
	.likes
		{
		display: none !important; /* set "none" or "block" */
		}

	/* page width ------------------------ */
	 body
	,section#WikiaPage
	,section#WikiaPage > header#WikiHeader
	,section#WikiaPage > article#WikiaMainContent
		{
		width:     800px !important;
		min-width: 800px !important;
		}

	/* color ------------------------ */
	 body
	,section#WikiaPage
	,section#WikiaPage > article#WikiaMainContent > header#WikiaPageHeader > details
	,section#WikiaPage > article#WikiaMainContent > nav#WikiaArticleCategories
		{
		color: #333 !important;
		background: #fff !important;
		}

/* ------------------------------------------------------------------------- */
/* base                                                                      */
/* ------------------------------------------------------------------------- */

	 header#WikiaHeader
	,header#WikiHeader div.buttons
	,header#WikiHeader img.shadow-mask
	,section#WikiaPage > div#TOP_LEADERBOARD
	,section#WikiaPage > .WikiaMainPageBanner
	,section#WikiaPage > article#WikiaMainContent > div#WikiaArticle > .home-top-right-ads
	,section#WikiaPage > article#WikiaMainContent > div#WikiaArticle > h2:last-of-type /* External Sponsor Links */
	,section#WikiaPage > article#WikiaMainContent > div#WikiaArticle > div.sponsormsg
	,section#WikiaPage > article#WikiaMainContent > div#WikiaArticleBottomAd
	,section#WikiaPage > div#WikiaRail > section
	,section#WikiaPage > div#WikiaRail > div
	,footer
	,*[src*="blank.gif"]
		{
		display: none !important;
		}

	 body
	,section#WikiaPage
	,section#WikiaPage > article#WikiaMainContent > header#WikiaPageHeader > details
	,section#WikiaPage > article#WikiaMainContent > nav#WikiaArticleCategories
		{
		border: none !important;
		}

	h1,h2,h3,h4,h5,h6
		{
		color: #000 !important;
		}

	h3,h4,h5,h6
		{
		margin: 2em 0 1.5em !important;
		padding: .5em .3em !important;
		border-bottom: 1px solid #ddd !important;
		border-top: 1px solid #ddd !important;
		font-weight: bold !important;
		}

	a:link
		{
		padding: 0 .2em !important;
		color: #82a900 !important;
		text-decoration: none !important;
		font-weight: bold !important;
		}
	a:visited
		{
		color: #000 !important;
		font-weight: bold !important;
		}
	a:hover
		{
		color: #fff !important;
		background-image: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAIAAAACCAYAAABytg0kAAAAFklEQVQImQXBAQEAAACAkP6fFgVRhQxbtgn3kkdJewAAAABJRU5ErkJggg==") !important;
    	text-decoration: none !important;
    	border: none !important;
		}
	 a:active
	,a:focus
		{
		outline: none !important;
		}

	p
		{
		margin-bottom: 2em !important;
		line-height: 1.8 !important;
		}

	li
		{
		margin-bottom: 1em !important;
		}

	pre
		{
		background-color: #efefef !important;
		border: 1px solid #dfdfdf !important;
		}

	*[style*="color: white"]
		{
		color: #777 !important;
		}

/* ------------------------------------------------------------------------- */
/* global layout                                                             */
/* ------------------------------------------------------------------------- */

	 body
	,section#WikiaPage
		{
		height: 100% !important;
		margin: 0 auto !important;
		padding: 0 !important;
		}

	section#WikiaPage > header#WikiHeader
		{
		position: fixed !important;
		top: 0 !important;
		margin: 0 auto !important;
		padding: 10px 0 5px !important;
		z-index: 2 !important;
		}

	section#WikiaPage > article#WikiaMainContent
		{
		margin-top: 6em !important;
		}

	section#WikiaPage > article#WikiaMainContent > div
		{
		margin: 0 !important;
		padding: 0 !important;
		}

/* ------------------------------------------------------------------------- */
/* details                                                                   */
/* ------------------------------------------------------------------------- */

	.speciallink
		{
		color: #aaa !important;
		}

	*[class*="bg-dark"]
		{
		background: #e9e9e9 !important;
		}

	 .qc-common
	,.qc-common a
		{
		color: #000 !important;
		}

	#mptabs
		{
		margin: 1em 0 1px !important;
		}
	#mptabs a:hover
		{
		background: none !important;
		}
	#mptabs span[class*="activetab"]:hover
		{
		color: #000 !important;
		background: #888 !important;
		}
	#mptabs .activetab
		{
		color: #333 !important;
		background: #e9e9e9 !important;
		}
	#mptabs .inactivetab
		{
		color: #666 !important;
		background: #bbb !important;
		}

	section#WikiaPage > header#WikiHeader
		{
		background: -moz-linear-gradient(top, #000, #555) !important;
		background: -webkit-gradient(linear, left top, left bottom, from(#000), to(#555)) !important;
		border-left: 1px solid #888 !important;
		border-right: 1px solid #888 !important;
		border-bottom: 1px solid #777 !important;
		-webkit-box-shadow: 0 1px 2px #000 !important;
		-moz-box-shadow: 0 1px 2px #000 !important;
		border-radius-bottomleft: 4px !important;
		-moz-border-radius-bottomleft: 4px !important;
		-webkit-border-radius-bottomleft: 4px !important;
		border-radius-bottomright: 4px !important;
		-moz-border-radius-bottomright: 4px !important;
		-webkit-border-radius-bottomright: 4px !important;
		opacity: .9 !important;
		}

	/* site logo ----------------------------- */
	section#WikiaPage > header#WikiHeader > h1
		{
		width: 5em !important;
		margin-left: 15px !important;
		height: auto !important;
		float: left !important;
		background: none !important;
		text-align: center !important;
		}
	section#WikiaPage > header#WikiHeader > h1 > a
		{
		height: auto !important;
		color: #fff !important;
		text-indent: 0 !important;
		border: none !important;
		}
	section#WikiaPage > header#WikiHeader > h1 > a > img[alt="WoWWiki"]
		{
		display: none !important;
		}
	section#WikiaPage > header#WikiHeader > h1 > a:before
		{
		content: "WoWWiki" !important;
		}
	section#WikiaPage > header#WikiHeader > h1:before
		{
		background: none !important;
		}


	/* global navigation --------------------- */
	section#WikiaPage > header#WikiHeader > nav
		{
		width: auto !important;
		position: relative !important;
		left: auto !important;
		bottom: auto !important;
		margin-left: 15em !important;
		}
	section#WikiaPage > header#WikiHeader > nav a
		{
		padding: 0 10px !important;
		background-image: none !important;
		color: #fff !important;
		border: none !important;
		}
	section#WikiaPage > header#WikiHeader > nav > ul
		{
		padding-top: 1em !important;
		}
	 section#WikiaPage > header#WikiHeader > nav > ul > li:hover
	,section#WikiaPage > header#WikiHeader > nav > ul > li > ul.subnav > li a:hover
		{
		background: none !important;
		background-color: #000 !important;
		}
	section#WikiaPage > header#WikiHeader > nav > ul > li > a:hover
		{
		color: #fff !important;
		}
	section#WikiaPage > header#WikiHeader > nav > ul > li > ul.subnav
		{
		top: 2.9em !important;
		padding: .5em 10px !important; /* = nav ul li a */
		}
	section#WikiaPage > header#WikiHeader > nav > ul > li > ul.subnav > li > a
		{
		padding: .5em 0 !important;
		color: #ccc !important;
		}


	/* article header ------------------------ */
	section#WikiaPage > article#WikiaMainContent > header#WikiaPageHeader
		{
		position: relative !important;
		margin: 0 !important;
		padding: 10px 320px 10px 15px !important;
		background: -moz-linear-gradient(top, #ddd, #fafafa) !important;
		background: -webkit-gradient(linear, left top, left bottom, from(#ddd), to(#fafafa)) !important;
		border-left: 1px solid #dfdfdf !important;
		border-top: 1px solid #ddd !important;
		border-right: 1px solid #fff !important;
		border-bottom: 1px solid #fff !important;
		-webkit-box-shadow: 1px 1px 1px #ddd !important;
		-moz-box-shadow: 1px 1px 1px #ddd !important;
		border-radius: 4px !important;
		-moz-border-radius: 4px !important;
		-webkit-border-radius: 4px !important;
		}

	section#WikiaPage > article#WikiaMainContent > header#WikiaPageHeader > h1
		{
		font-size: 30px !important;
		font-weight: bold !important;
		line-height: 1.4 !important;
		}

	section#WikiaPage > article#WikiaMainContent > header#WikiaPageHeader > details
		{
		width: auto !important;
		margin: 20px 0 0 !important;
		}

	section#WikiaPage > article#WikiaMainContent > header#WikiaPageHeader > details > ul.history
		{
		top: 0 !important;
		left: 0 !important;
		}

	.WikiaSearch input[type="text"] {
		width: 282px !important;
		}

	.WikiaPageHeader .WikiaSearch {
		top: 10px !important;
		right: 15px !important;
		}
	.WikiaSearch button.secondary {
		top: 10px !important;
		display: none !important;
		}

	/* article main -------------------------- */
	section#WikiaPage > article#WikiaMainContent > div#WikiaArticle > h2
		{
		margin: 2em 0 1em !important;
		padding: .2em 15px .1em 15px !important;
		color: #fff !important;
		background: -moz-linear-gradient(top, #777, #444) !important;
		background: -webkit-gradient(linear, left top, left bottom, from(#777), to(#444)) !important;
		border-left: 1px solid #555 !important;
		border-top: 1px solid #555 !important;
		border-right: 1px solid #666 !important;
		border-bottom: 1px solid #666 !important;
		-webkit-box-shadow: 1px 1px 1px #222 !important;
		-moz-box-shadow: 1px 1px 1px #222 !important;
		border-radius: 4px !important;
		-moz-border-radius: 4px !important;
		-webkit-border-radius: 4px !important;
		font-size: 18px !important;
		font-weight: normal !important;
		line-height: 1.4 !important;
		}
	section#WikiaPage > article#WikiaMainContent > div#WikiaArticle > h2 a:link
		{
		color: #fff !important;
		}
	section#WikiaPage > article#WikiaMainContent > div#WikiaArticle > h2 > span.mw-headline > a
		{
		font-weight: normal !important;
		}
	section#WikiaPage > article#WikiaMainContent > div#WikiaArticle > h2 > span.editsection > a
		{
		color: #fff !important;
		}

	.WikiaArticleInterlang h3
		{
		margin: 0 !important;
		padding: 0 !important;
		border: none !important;
		}

	/* button "Edit" ------------------------- */

	span.editsection a:nth-child(1)
		{
		display: none !important;
		}
	 a[title*="Edit"]
	,span.editsection a
		{
		color: #000 !important;
		background: none !important;
		font-family: monospace !important;
		font-size: 11px !important;
		font-weight: bold !important;
		border: none !important;
		opacity: .35 !important;
		}
	 a[title*="Edit"]:hover
	,span.editsection a:hover
	,section#WikiaPage > article#WikiaMainContent > div#WikiaArticle > h2 > span.editsection > a:hover
		{
		color: #ff00ff !important;
		font-weight: bold !important;
		background-image: none !important;
		opacity: 1 !important;
		}
	 a[title*="Edit"]:after
	,span.editsection a:after
		{
		content: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABAAAAAQCAYAAAAf8/9hAAAAVUlEQVQ4jWNgIA3wMjAwtDMwMDSQqA+ueTkDA8N/KJ5PiWaSDBHAoZkoA3DZPKoZD6BIMy4DiNY8nwES57vI0cyApAFmCEmaYQaQbCtVAbbQx4XhAABYXk+uLxj5uAAAAABJRU5ErkJggg==");
		}

	/* table --------------------------------- */
	 table.darktable
	,.WikiaWideTablesWrapper table
	,table.navbox
		{
		padding: 0 !important;
		background-color: #f6f5f5 !important;
		border-spacing: 0 !important;
		border: 1px solid #ccc !important;
		}
	 table.darktable tr:nth-child(2n+1)
	,.WikiaWideTablesWrapper tr:nth-child(2n+1)
	,table.navbox tr:nth-child(2n+1)
		{
		background-color: #e5e6e6 !important;
		}
	 table.darktable th
	,table.darktable td
	,.WikiaWideTablesWrapper th
	,.WikiaWideTablesWrapper td
	,table.navbox th
	,table.navbox td
		{
		padding: .5em 1em !important;
		border: none !important;
		border-right: 1px solid #ccc !important;
		border-bottom: 1px solid #ccc !important;
		}
	 table.darktable tr
	,.WikiaWideTablesWrapper tr
	,table.darktable th
	,.WikiaWideTablesWrapper th
	,table.navbox tr
	,table.navbox th
		{
		background: none !important;
		}
	 table.darktable tr:last-of-type td
	,table.darktable tr:last-of-type th
	,.WikiaWideTablesWrapper tr:last-of-type th
	,.WikiaWideTablesWrapper tr:last-of-type td
	,table.navbox tr:last-of-type th
	,table.navbox tr:last-of-type td
		{
		border-bottom: none !important;
		}
	 table.darktable th:last-child
	,table.darktable td:last-child
	,.WikiaWideTablesWrapper th:last-child
	,.WikiaWideTablesWrapper td:last-child
	,table.navbox th:last-child
	,table.navbox td:last-child
		{
		border-right: none !important;
		}

	.alt
		{
		background: none !important;
		}

	img.popout
		{
		display: inline !important;
		}

	/* comment */
	.comments {
		color: #ddd !important;
		}

	/* autocomplete */
	.autocomplete {
		color: #82a900 !important;
		}

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
}
