css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.furryteens.org/forum/"){

/************************************************************ 
 *  Backgrounds and Borders -  - "Darkheart" (copb.phoenix) *
 ************************************************************/
/**************
 * Backgrounds
 **************/
/*** 
 CSS 
 ***/
input.button_submit
{
background: #202020 !important;
}
.windowbg, #preview_body
{
	background-color: #202020 !important;
}
blockquote.bbc_alternate_quote
{
	background-color: #262626 !important;
}
.windowbg2
{
	background-color: #303030 !important;
}

.activity_stats li div.bar div, blockquote.bbc_standard_quote
{
	background-color: #404040 !important;
}

.lockedbg
{
	background: #302020 !important;
}
.lockedbg2
{
	background: #403030 !important;
}

.stickybg
{
	background: #202030 !important;
}
.stickybg2
{
	background: #303040 !important;
}

.description, .plainbox, .roundframe
{
	background: #262626 !important;
}
/******
 Images
 ******
/* Backdrop */
body
{
  background: url(https://lh4.googleusercontent.com/-sM1VLFz9l9A/TQzg4yXU2JI/AAAAAAAAA9A/yiEkX_aSXZ8/s800/backdrop.png) #000 repeat-x!important;
}

/* Frame Repeat */
#content_section, #content_section div.frame
{
background-image: url(https://lh6.googleusercontent.com/-ZUZsu2r0ut8/TQ07seRjCCI/AAAAAAAAA9o/zB_l9Fv9OeA/s3000/frame_repeat.png) !important;
}

/* Menu Graphics */
.dropmenu li ul, .dropmenu li a.active, .dropmenu li a.active span.firstlevel, .dropmenu li a.firstlevel:hover, .dropmenu li:hover a.firstlevel,.dropmenu li a.firstlevel:hover span.firstlevel, .dropmenu li:hover a.firstlevel span.firstlevel, .buttonlist ul li a, .buttonlist ul li a:hover, .buttonlist ul li a span, .buttonlist ul li a:hover span, .buttonlist ul li a.active, .buttonlist ul li a.active span
{
background-image: url(https://lh5.googleusercontent.com/-XLeHAcfT2g4/TSBTE7HxiBI/AAAAAAAABBc/sf8-HSacp-8/s800/menugfx.png) !important;
}

/* Main Block */
#footer_section, #footer_section div.frame, #header, #header div.frame, .approvebg span.botslice, .approvebg span.botslice span, .approvebg span.topslice, .approvebg span.topslice span, #footer_section, #footer_section div.frame, #header, #header div.frame, span.lowerframe, span.lowerframe span, span.upperframe, span.upperframe span, .table_grid th.first_th, .table_grid th.last_th, .table_grid thead th, #upshrinkHeaderIC h3.catbg, #upshrinkHeaderIC h3.catbg2, #upshrinkHeaderIC h4.titlebg, #upshrinkHeaderIC h4.titlebg span.left, #upshrinkHeaderIC h3.catbg span.left, #upshrinkHeaderIC h3.catbg2 span.left, div.title_barIC, div.title_bar, div.cat_bar, h4.titlebg, h3.titlebg, h4.titlebg span.left, h3.titlebg span.left, h4.catbg, h4.catbg2 , h3.catbg , h3.catbg2 , .table_list tbody.header td.catbg, h4.catbg span.left, h4.catbg2 span.left, h3.catbg span.left, h3.catbg2 span.left, .table_list tbody.header td span.left, .catbg, .catbg2, tr.catbg td, tr.catbg2 td, tr.catbg th, tr.catbg2 th, .titlebg2, thead tr.titlebg th, tr.titlebg td, tr.titlebg2 td, .windowbg span.topslice span, .windowbg span.topslice, .windowbg span.botslice, .windowbg span.botslice span, .windowbg2 span.topslice, .windowbg2 span.topslice span, .windowbg2 span.botslice, .windowbg2 span.botslice span, .approvebg span.topslice, .approvebg span.topslice span, .approvebg span.botslice, .approvebg span.botslice span
{
	background-image: url(https://lh5.googleusercontent.com/-c6waBaI3080/TROUObBsA-I/AAAAAAAAA_s/Rkc8rvqvduA/s3000/main_block.png) !important;
}


/**********
 * Borders
 **********/
blockquote.bbc_alternate_quote, blockquote.bbc_standard_quote
{
	border-top: 2px solid #606060 !important;
	border-bottom: 2px solid #606060 !important;
}
.postbg, .roundframe, span.clear.upperframe, span.upperframe, span.lowerframe, td.smalltext, tr.titlebg td
{
	border-left: 1px solid #404040 !important;
	border-right: 1px solid #404040 !important;
}
.description, div.cat_bar, .lockedbg, .lockedbg2, .plainbox, select#display_jump_to_select, .stickybg, .stickybg2, td.catbg, td.info, td.lastpost, textarea, thead, tr.titlebg, .windowbg, .windowbg2
{
    border:1px solid #404040 !important;
}
span.clear.upperframe, span.upperframe, td
{
        border-top: 1px solid #404040 !important;
}
span.lowerframe, td
{
        border-bottom: 1px solid #404040 !important;
}
.activity_stats li div.bar div
{
      border-top: 1px solid #101010 !important;
      border-left: 1px solid #101010 !important;
      border-right: 1px solid #101010 !important;
}

/* Buttons and such just looking pretty */
input, button, select, textarea {
      border:1px solid #404040 !important;
      background-color: #262626 !important;
      color: #A0A0A0 !important;
      overflow: auto !important;
}

/* Buttons and such on hover */
input:hover, textarea:hover, button:hover, select:hover
{
      border:1px solid #303030 !important;
      background-color: #202020 !important;
      color: #A0A0A0 !important;
      overflow: auto !important;
}
/* Buttons and such flipping the freak out */
input:active, textarea:active, button:active, select:active
{
      border:1px solid #202020 !important;
      background-color: #161616!important;
      color: #A0A0A0 !important;
      overflow: auto !important;
}
/* Buttons and such getting some luv */
input:focus, textarea:focus, button:focus, select:focus
{
      border:1px solid #303030 !important;
      background-color: #1E1E1E!important;
      color: #A0A0A0 !important;
      overflow: auto !important;
}

/******************************************
 * Font Colors "Darkheart" (copb.phoenix) *
 ******************************************/
a:link, h3.catbg
{
	color: #A076C6 !important;
}

a:visited
{
	color: #A090B0 !important;
}

/* "Normal" Text */
body, blockquote.bbc_alternate_quote, blockquote.bbc_standard_quote, .description, h4, h5, .lockedbg, .lockedbg2, .plainbox, #preview_body, .stickybg, .stickybg2, td.info, td.smalltext, textarea,.titlebg, .titlebg2, thead tr.titlebg th, tr.titlebg td, tr.titlebg2 td, .windowbg, .windowbg2
{
	color: #A0A0A0 !important;
}

/**************************************************
 * Images - "Darkheart" (copb.phoenix) ************************
 **************************************************/
/************************
 * Emotes 
 ************************/
/* :phoenix: */
img[src$="http://www.furryteens.org/static/smileys/default/CopbPhoenixIcon.png"]
{
height:25px!important;
padding-left:25px!important;
width:0!important;
background-image:url("https://lh5.googleusercontent.com/-40ZH9iYldrg/TROPsSt4tUI/AAAAAAAAA_g/JYXLiYa3fBc/s800/CopbPhoenixIcon.png")!important;
}


/* :enrique: */
img[src$="http://www.furryteens.org/static/smileys/default/EnriqueIcon.png"]
{
height:25px!important;
padding-left:25px!important;
width:0!important;
background-image:url("https://lh6.googleusercontent.com/-oMcpGsPyffw/TROSAW8JNQI/AAAAAAAAA_k/BG8vkTmvAhI/s800/EnriqueIcon.png")!important;
}

/************************
 * Profile Sidebar
 ************************/
/* Mars */
img[src$="http://www.furryteens.org/static/default/images/Male.gif"]
{
height:12px!important;
padding-left:12px!important;
width:0!important;
background-image:url("https://lh3.googleusercontent.com/-mS2c7bU_D-Y/TQ-Lvs6lQbI/AAAAAAAAA_E/HoUC2hZBM6I/s800/male.png")!important;
}

/* User Off */
img[src$="http://www.furryteens.org/static/default/images/useroff.gif"]
{
height:11px!important;
padding-left:12px!important;
width:0!important;
background-image:url("https://lh3.googleusercontent.com/--_dDSCRTBzs/TQ5mXFDsy6I/AAAAAAAAA-Y/_-6SPe3p5M0/s800/useroff.png")!important;
}

/* User On */
img[src$="http://www.furryteens.org/static/default/images/useron.gif"]
{
height:11px!important;
padding-left:12px!important;
width:0!important;
background-image:url("https://lh6.googleusercontent.com/-Ra6MwjMzExg/TQ5mXDqTJ9I/AAAAAAAAA-c/iz4bGcR1B1k/s800/useron.png")!important;
}

/* Venus */
img[src$="http://www.furryteens.org/static/default/images/Female.gif"]
{
height:12px!important;
padding-left:12px!important;
width:0!important;
background-image:url("https://lh6.googleusercontent.com/-PRVHWMpIsRY/TQ-G8agRtuI/AAAAAAAAA_A/_wcqGye7pUA/s800/female.png")!important;
}

/************************
 * Topic Display
 ************************/
/* "New" Sticker */
img[src$="http://www.furryteens.org/static/default/images/english/new.gif"]
{
height:11px!important;
padding-left:26px!important;
width:0!important;
background-image:url("https://lh6.googleusercontent.com/-V7QjXqvjlHs/TQ5ijEW3UlI/AAAAAAAAA-U/x2IzUOmK0m8/s800/new.png")!important;
}

}
