/*
 * Author:            Will (war59312@gmail.com)
 * Description:       Cleans Up and Removes Ads On palemoon.org
*/

/* +++ changelog +++
Dec 18th, 2010
- original release
*/

@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain(palemoon.org) {

/* Hides "Compatible With Windows 7 Image" */
IMG[src="images/win7compat.jpg"] { display: none !important }

/* Hides Right Side-bar */
TD[width="160"][style="padding-top: 2px; padding-bottom: 2px; vertical-align: top;"] { display: none !important }

/* Hides "Powered By Mozilla Pad" Ad */
HTML > BODY > TABLE > TBODY > TR:first-child + TR + TR + TR + TR > TD > DIV { display: none !important }

/* Hides "Sity Hosting" Ad */
HTML > BODY > DIV { display: none !important }

/* Hides "Softpeida, Geardownload, Downloadroute.com, FirewareFiles.com" Ads */
HTML > BODY > SCRIPT:first-child + SCRIPT + SCRIPT + TABLE + DIV + CENTER { display: none !important }

/* Hides "Fiber Download, Editor's Pick, Editor's Choice, Brothersoft" Ads */
HTML > BODY > SCRIPT:first-child + SCRIPT + SCRIPT + TABLE + DIV + CENTER + CENTER { display: none !important }

/* Hides "View more awards on the Awards Page" Text Ad */
HTML > BODY > SCRIPT:first-child + SCRIPT + SCRIPT + TABLE + DIV + CENTER + CENTER + CENTER { display: none !important }

/* Hides "MySpace Views Counter" Ad */
.statcounter { display: none !important }

}
