css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.cs.chalmers.se/Cs/Grundutb/Kurser/krypto/") {

body {
  font-size: 12pt;
  font-family: helvetica, "lucida grande", sans-serif;
  margin: 0;
  padding: 2em;
}

h1, h2, h3, h4, h5, h6 {
  font-family: georgia, times, "times new roman", serif;
}

p,a,ul,li,em,strong,span,div {
  font-size: 1em;
}

p {
  width: 37em;
}

ul, ol {
  width: 35em;
  margin: 1em 0;
}

li + li {
  margin-top: 1em;
}

img {
  border: 0;
}

pre {
  width: inherit;
  overflow-x: auto;
}

a:link, a:visited, a:active {
  color: #1C5380;
  padding: 0.1em 0.2ex 0 0.1em;
}

a:visited {
  color: #584781;
}

a[href]:hover {
  color: #111;
}

a[href^=http] {
  background: transparent url(data:image/gif;base64,R0lGODlhCQAJAJEAAP///wAAAP///wAAACH5BAUUAAIALAAAAAAJAAkAAAIXlI8ZK8sdGpggWhrAyXVGSVUGh42OIxQAOw%3D%3D) no-repeat scroll right center;
  padding-right: 13px;
}

a[href$=pdf]:after {
  content: ' (pdf)';
}

div.navbar {
  background: url(http://www.chalmers.se/portal_themes/chalmers/front/1325947213/1972179326/image/index_html) no-repeat bottom center;
  padding-bottom: 150px;
}

div.navbar h3, div.navbar h4 {
  margin: 0.5em 0;
  text-align: center;
}

ul.menu {
  border-right: 1px solid #ddd;
  border-left: 1px solid #ddd;
  border-top: 1px solid #f0f0f0;
  margin: 3em 0;
  padding: 0;
  list-style-type: none;
  width: 100%;
}

ul.menu li {
  padding: 0;
  margin: 0.1em 0;
}

ul.menu li a {
  display: block;
  padding: 0.2em 1em 0.1em 0.5em;
  text-decoration: none;
  background: #fafafa;
  color: #222;
  border-bottom: 1px solid #ddd;
  border-top: 1px solid #fefefe;
}

ul.menu li a:hover {
  color: #000;
  background: #FEF7ED;
  padding: 0.2em 1em 0.1em 0.55em;
}

ul.menu li a:active {
  font-weight: bold;
}

table {
  border: 1px solid #eee;
  font-size: 0.8em;
}

th, td {
  border: 0 !important;
}

th {
  font-weight: bold;
  font-size: 1em;
  border-top: 1px solid #fefefe;
  border-bottom: 1px solid #f0f0f0;
  background: #FEF7ED;
}

tr {
  background: #fafafa;
  color: #222;
  border: 1px inset #cacaca !important;
}

tr:hover {
  background: #FEF7ED;
}
}
