css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("michiganstate.247sports.com") {body {background: lightgray !important;}

body, .legend, .main_content, .pg_bdy, .brd_bdy, .brd_ftr {background: lightgray !important;}

.brdnm_blk {font-family: Californian, serif !important; padding-left: 10px !important;}

.brdinfo_blk h1:after {content:url(http://twinorbs.com/public/MSU-Wordmark-Green-120-pxls.gif) "--" url(http://twinorbs.com/public/Spartan-helmet-Green-50-pxls.gif) !important; float: right !important; margin: 0px 0px 10px 50px !important; color: lightgray !important;}

.pagination:before {content:"Watch out Heygood!" !important; margin: 0px 100px 0px 0px !important; font-weight: bold !important; font-size: 1.2em !important;} 

.pg_bdy > div, .pg_hdr > div {width: 100% !important; border-color: transparent !important; margin: 0px !important;}

table, th, tr, td {border: thin solid #18453b !important; font-family: Californian, serif !important; font-size: 1.05em !important;}

th {background: whitesmoke !important; color: #18453b !important; font-weight: bold !important; font-size: 1.25em !important; height: auto !important;}

tr.alt {background: whitesmoke !important;}
tr {background: lightgray !important;}

.pagn a.active, .toolbar .pagn a.active, .brd_hdr.skn2, .search {border-radius: 10px 10px !important;
-moz-border-radius: 10px 10px !important;}

.gnav_blk > ul {border-top-right-radius: 10px 10px !important;
-moz-border-top-right-radius: 10px 10px !important;
border-top-left-radius: 10px 10px !important;
-moz-border-top-left-radius: 10px 10px !important;}

.brd_hdr, .brdinfo_blk, .usrnav_blk, .brd_bdy > div, .brd_ftr > div, .brd_bdy, .commentnav_lst, .commenttools_lst {min-width: 99% !important; padding: 0px !important; margin: 0px !important;}

.brditm_hdr, .main_content {width:100% !important; padding: 0px !important;}

.brdlegend_lst > .ignore:after {content:url(http://twinorbs.com/public/MSU-Wordmark-Green-120-pxls.gif) !important; float: right !important; margin: 0px 0px 0px 0px !important;}

.brdlegend_lst > .follow:after {content:url(http://twinorbs.com/public/Spartan-helmet-Green-50-pxls.gif) !important; float: right !important; margin: 0px 0px 0px 0px !important;}

.brdlegend_lst > .video:after {content:"That's criminal!" !important; float: right !important; font-weight: bold !important; color: #18453b !important; font-size: x-large !important; font-family: Californian, serif !important; margin-top: 20px !important; text-shadow: 0px 0px 1px black !important;}

.trial_blk, .pg_footer, .pg_ftr, .sidebar, .ldr_ad_rail, .ad728_blk, .m_lt10 {display: none !important;} 
}
