@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("linuxsir.org"), domain("linuxsir.com") {
body {
  margin: 0 5% 0 5% !important;
  width: auto !important;
  padding: 0 5px 0 5px !important;
  border-left:1px dashed #eebb88;
  border-right:1px dashed #eebb88;
  background:#ffffdf !important;
}
/* the "page" is white... */
.page {
  /* background:red!important; */ /* to see where it is */
}
a {
  text-decoration:none !important;
}
/* text color, font */
body, a, .tcat, .vbmenu_control, .tfoot, .tcat a:link, .tcat_alink, .vbmenu_control a:link, .vbmenu_control_alink, .smallfont strong, .thead, .thead strong, .thead a:link, .thead_alink, .thead a:visited, .thead_avisited, .thead a:active, .thead_ahover, .navbar, .navbar a, .smallfont, .time, .alt1, .alt1Active, .alt2, .alt2Active, .panel {
  color: #3c3c3c !important;
  font:normal 12px verdana,geneva,lucida,'lucida grande',arial,helvetica,sans-serif,å¾®è½¯é›…é»‘,simsun,å®‹ä½“,bitstream,Nimbus !important;
}
/* link hover */
a:hover, a:active, body_ahover, .thead a:hover {
  color:#000000 !important;
  text-decoration:none !important;
}
/* threads */
#threadslist td.alt1 {
  border-bottom: 1px dashed #eebb88 !important;
}
/* link visited */
td.alt1 a:visited {
  color:#774444 !important;
}
.tborder {
  background:none !important;
  padding: 0 !important;
}
/* color of bars */
.tcat, .vbmenu_control, .tfoot {
  background:none repeat scroll 0 0 #eebb88 !important;
}
/* sidebar navbar etc */
.alt2, .alt2Active, .thead, .panelsurround, .panel, .vBulletin_editor {
  background:none repeat scroll 0 0 #f6f2c2 !important;
}
/* user name font */
.bigusername {
  font-size: 1.7em !important;
}
/* post message */
.alt1, .alt1Active {
  background:none repeat scroll 0 0 #f9f9f9 !important;
}
.alt1 hr {
  border-top: 1px dashed #eebb88 !important;
  height: 1px;
}
.vbmenu_popup, .columncell {
  border:1px solid #d9d9d9 !important;
}
/* [ code ] */
#posts pre.alt2 {
  font-family: monospace !important;
  font-size: 1em !important;
  width: 96% !important;
  border: 0 !important;
  border-left: 1px dashed #eebb88 !important;
  margin-left: 20px !important;
}
/* [ quote ] */
#posts td div td.alt2 {
  border: 0 !important;
  -moz-box-shadow: 2px 2px 0 rgba(170, 160, 130, 0.5), 4px 4px 0 rgba(200, 190, 165, 0.5) !important; /* shadow fun */
}
/* pagenav */
.pagenav td.alt2 {
  -moz-box-shadow: 0px 0px !important;
}
/* reply box */
.panel {
  border: 0 !important;
}
div.panel div {
  max-width: 800px !important;
}
#vB_Editor_QR {
  padding:0 !important;
  border:1px !important;
}
#vB_Editor_QR_textarea, #vB_Editor_001_textarea {
  width: 790px !important;
  height: 200px !important;
  margin:0 !important;
}
.smallfont fieldset {
  border:1px solid #eebb88 !important;
}
/* hide annoying images */
.smallfont img[alt="å‘è¡¨æ–°ä¸»é¢˜"], .smallfont img[alt="å›žå¤"]{
  padding: 0 !important;
  margin: 0 !important;
}
.smallfont img[alt="å‘è¡¨æ–°ä¸»é¢˜"]{position:absolute;top:0;left:20%;width:228px;height:90px;opacity:0 !important;}
.smallfont img[alt="å›žå¤"]{display:none;}
/* remove table cellspacing */
.tborder {border-collapse: collapse !important;}
}
