@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("linuxsir.org"), domain("linuxsir.com") {
body {
  margin: 0 5% 0 5% !important;
  width: auto !important;
  padding: 0px !important;
  border: 2px solid rgb(187,204,255) !important;
  border-top: 4px solid rgb(187,204,255) !important;
  border-bottom: 0 !important;
  background: rgb(255,255,255) !important;
}
.page {
  background: rgb(255,255,255) !important;
}
a {
  text-decoration: none !important;
}
/* text color, font */
body, td, th, p, li, a, .tcat, .vbmenu_control, .tfoot, .tcat a:link, .tcat_alink, .vbmenu_control a:link, .vbmenu_control_alink, .smallfont strong, .thead, .thead strong, .thead a:link, .thead_alink, .thead a:visited, .thead_avisited, .thead a:active, .thead_ahover, .navbar, .navbar a, .smallfont, .time, .alt1, .alt1Active, .alt2, .alt2Active, .panel {
  color: rgb(3,3,3) !important;
  font:normal 12px verdana,geneva,lucida,'lucida grande',arial,helvetica,sans-serif,å¾®è½¯é›…é»‘,simsun,å®‹ä½“,bitstream,Nimbus !important;
}
/* link hover */
a:hover, a:active, body_ahover, .thead a:hover {
  color: rgb(0,0,0) !important;
  text-decoration:none !important;
}
/* threads */
#threadslist td.alt1 {
  border-bottom: 2px solid rgb(227,233,255) !important;
}
/* link visited */
td.alt1 a:visited {
  color: rgb(64,64,64) !important;
}
.tborder {
  background: none !important;
  padding: 0 !important;
}
/* color of bars */
.tcat, .vbmenu_control, .tfoot {
  background: none repeat scroll 0 0 rgb(187,204,255) !important;
}
/* sidebar navbar etc */
.alt2, .alt2Active, .thead, .panelsurround, .panel, .vBulletin_editor {
  background: none repeat scroll 0 0 rgb(221,230,255) !important;
}
/* thread header bar */
.thead {
  background: none repeat scroll 0 0 rgb(227,233,255) !important;
  border:none;
}
/* user name font */
.bigusername {
  font-size: 1.7em !important;
}
/* post message */
.alt1, .alt1Active {
  background:none repeat scroll 0 0 rgb(237,240,249) !important;
}
.alt1 hr {
  border-top: 1px solid rgb(201,215,241) !important;
  height: 1px;
}
.vbmenu_popup, .columncell {
  border:1px solid rgb(204,210,230) !important;
}
/* [ code ] */
pre.alt2 {
  font-family: monospace !important;
  font-size: 1em !important;
  width: 96% !important;
  border: 2px solid rgb(204,210,230) !important;
  margin-left: 20px !important;
}
/* [ quote ] */
td div td.alt2 {
  border: 1px solid rgb(204,210,230) !important;
}
/* reply box */
.panel {
  border: 0 !important;
}
div.panel div {
  max-width: 800px !important;
}
#vB_Editor_QR {
  padding:0 !important;
  border:1px !important;
}
#vB_Editor_QR_textarea, #vB_Editor_001_textarea {
  width: 790px !important;
  height: 200px !important;
  margin:0 !important;
}
.smallfont fieldset {
  border:1px solid rgb(204,210,230) !important;
}
/* hide annoying images */
.smallfont img[alt="å‘è¡¨æ–°ä¸»é¢˜"], .smallfont img[alt="å›žå¤"]{padding:0 !important;margin:0 !important;}
.smallfont img[alt="å‘è¡¨æ–°ä¸»é¢˜"]{position:absolute;top:0;left:20%;width:228px;height:90px;opacity:0;}
.smallfont img[alt="å›žå¤"]{display:none;}
/* remove table cellspacing */
.tborder {border-collapse: collapse !important;}
}
