css:
@-moz-document domain("www.deezer.com")
{
	#tab_premium, .offer, #hq,#tips_beta,#tab_store
	{
		display: none !important;
	}
	
	.pub_mobile, .ads, #ads_player, #bottom , #banner_habillage, #ads_habillage_pixel, #ads_square_music, #ads_square_music_pixel, #ads_adsense_music, #ads_expand, #ads_slider, #ads_slider_pixel, #ads_sponsored_link, #ads_sponsored_link_pixel, #ads_300x250, #ads_120x600
	{
		display: none !important;
	}
	
	body
	{
		background: url(http://cdn-files.deezer.com/img/common/bg_music.png) repeat-y center top !important;
	}
	.title1
	{
		color: #222 !important;
	}
	#footer
	{
		font-size:8px !important;
	}
	#footer img
	{
		display: none !important;
	}
	#footer .columns ul
	{
		height:110px !important;
		margin:0 25px 0 0 !important;
		padding:0 7px 0 0 !important;
	}
	.player_2_free
	{
		height: 90px !important;
	}

[class = 'bgheader bgrepeat']{position:fixed!important;margin-top:-32px!important;width:100%!important}
[id = 'dz_player']{position:fixed!important;bottom:0!important}
body{padding-top:32px!important;padding-bottom:64px!important}

[id = 'playeroptions'],[class = 'tipsy-inner'],[class = 'middle']{position:fixed!important;bottom:74px!important}

[class = 'arrow main'],[id = 'musicbox_arrow'],[class = 'tipsy-arrow'],[class = 'top_suggest main']{display:none!important}

[class ='li_list noborder li_buy_amazon'],[class ='li_list li_buy_itunes'],[class ='li_list li_buy_starzik']{background:#151515!important;}

[id = 'home_search_suggest'],[class = 'list'],[class ='li_list noborder li_buy_amazon']{-moz-border-radius-topright:8px!important;-moz-border-radius-topleft:8px!important}
[class = 'list']{padding-top:12px!important;padding-left:12px!important;position:absolute!important;bottom:56px!important}

[class = 'arrowbottom main']{-moz-transform: rotate(180deg) !important}

[id = 'home_search_suggest']{margin-top:-256px!important}

}
