css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document
domain("ogame.ru"),
domain("ogame.fr"),
domain("ogame.org"),
domain("ogame.de"),
domain("ogame.dk"),
domain("ogame.com.pt"),
domain("ogame.us"),
domain("ogame.com.hr"),
domain("board.ogame.ru"),
domain("ogame.gameforge.com")
{

/*
	Ogame Minimal Mod for mobile
	By Warlock8
	http://userstyles.org/styles/41444
*/
body, html  {background-image: none !important;}

/*#rechts #myPlanets .smallplanet a.moonlink {
  left: 115px !important;
}
#rechts, #myPlanets {
  width: 149px !important;
}
#box {
  width: 100% !important;
}*/
.originFleet span, .destFleet span { width: 100% !important; }

img.icon-moon { width: 20px !important; height: 20px !important;}

label.auction_detail {font-size: 10px !important;}
a.build-faster span, td.desc {font-size: 9px !important;}
#itemDetails #features a.build-it.activateItem, #itemDetails #features a.build-it_disabled.activateItem {margin-top: 15px !important;}
.detail_screen div#features a.build-it, .detail_screen div#features a.build-it_disabled  {margin-top: 1px !important;}

#inhalt .item_img span.level, #inhalt .inventory_slider ul span.level, #inhalt .item_img .item_img_box span.level, #shop #inhalt ul span.level, div.values span.level {color: #FFFFFF !important;}

div#retreatText, div.retreatText { display: none !important;}
#battlereport .win, #battlereport .draw, #battlereport .lose, #battlereport div#combatants, #battlereport .winner, 
.battlereport .win, .battlereport .draw, .battlereport .lose, .battlereport div.combatants, .battlereport .winner {
margin: auto !important;
padding: 5px 0 !important;}
.ui-dialog .ui-dialog-content.overlayDiv {background-image: none !important;}

#siteFooter .content { width: 850px !important; }
#siteFooter .fleft { width: 360px !important; }
#siteFooter .fright { width: 490px !important; }
#siteFooter a, #siteFooter a:link {margin: 0 !important;}
#siteFooter {position: relative !important;	bottom: -32px !important;}

#fleet1 input.fleetValues {	
    height: 14px !important; 
} 
#fleet2 input.galaxy, 
#fleet2 input.system, 
#fleet2 input.planet {	
    height: 16px !important; 
}
DIV.ui-dialog.ui-widget.ui-widget-content.ui-corner-all.ui-draggable {
    top: 145px !important; 
}

a#continue {border: thin solid green !important;}
a#continue.off {border: thin solid gray !important;}

body#fleet1 .on a {border: thin solid red !important;}
body#fleet3 ul#missions li.on, #trader #buttonz a.active {border: thin solid yellow !important;}
#fleet2 #buttonz table#mission td#start a.planet_selected, 
#fleet2 #buttonz table#mission td#target a.planet_selected,
#fleet2 #buttonz table#mission td#start a.moon_selected,
#fleet2 #buttonz table#mission td#target a.moon_selected,
#fleet2 #buttonz table#mission td#target a.debris_selected {border: thin solid white !important;}

#fleet1 .allornonewrap {margin-top: 2px !important;}

#siteFooter a {font: 9px/19px Verdana,Arial,Helvetica,sans-serif !important;}
li.techtree a span.label, li.demolish a span.label {font: 9px/14px Verdana,Arial,Helvetica,sans-serif !important;}

#message_alert_box span {margin-top: 8px !important;}

#message_alert_box.emptyMessage, a#message_alert_box_default {
    background:url(data:image/gif;base64,) no-repeat !important;
}

a#message_alert_box.emptyMessage:hover, a#message_alert_box_default:hover, a#message_alert_box:hover {
    background: url(data:image/gif;base64,) no-repeat scroll 0 0 transparent !important;
}

#message_alert_box, message_alert_box_default {
    background: url(data:image/gif;base64,) no-repeat scroll 0 0 transparent !important;
}

body#shop, body#traderOverview, body#notices, body#tutorial, body#changelog, body#highscore, body#station-moon, body#premium, body#trader, body#preferences, body#alliance, body#netz, body#combatreport, body#techtree, body#resourceSettings, body#defense, body#overview, body#resources, body#station, body#shipyard, body#research, body#statistics, body#messages, body#galaxy, body#fleet1, body#fleet2, body#fleet3, body#movement, #info, body[id] #planet {
background-image:none !important;
}

*[id="showSpyReportsNow"] td, #messagebox td, #netz .contentz td, #showSpyReportsNow { 
padding-top:0 !important;
padding-bottom:0 !important;
}

#netz .contentz table {
margin-top:5px !important;
}

table.list td.value, table.spy2 td {
text-align:right !important;
padding-right: 50px !important;
}

table.spy2 td.item {
text-align:left !important;
}

#galaxyLoading, #techDetailLoading, #statisticsLoading {
background: transparent url(data:image/png;base64,) no-repeat scroll center center !important;
}

#eventboxLoading img, #star, #star1, #star2 { display:none !important; }

/*Ogame Light Starter [OLS]*/
body > #start {background-image: none !important;
background-color: #040e1e !important;}
body#overlay #tabContent {background: none !important;
background-color: #040e1e !important;}
body#overlay {background: none !important;
background-color: #040e1e !important;}

#mmonetbar {background: none !important;}

#start #trailer {display: none !important;}
#start #flashTrailer {display: none !important;}

#start #contentWrap
{position: relative !important; bottom: 105px !important; right: 95px !important;}
#start #contentWrap #tabContent {background: none !important;}
#start #contentWrap #contentFooter {background: none !important;}


/* LOGIN */
#start #login {display: block !important; background: none !important; background-color: #1E2B39 !important;}
#start #login {right: -125px !important; top: 40px !important;}
#start #header a#loginBtn {display: none !important;}

#start #header #login #resendLink #formSubmit, #pwLostForm #formSubmit, #loginForm #loginSubmit {background: none !important; background-color: #983232 !important;}

/* REGISTRATION */
#start #subscribeForm
{position: relative !important;
bottom: 150px !important;
right: 100px !important;}
#start #subscribeForm:after {content: "\00a9 \00a0 OLS powered by Johnson" !important; 
color: grey !important;
font-size: 10px !important;
padding: 7px !important;
}

#start #subscribe
{border:none !important;}

#start #subscribe #subscribeForm #submitWrap #regSubmit
{background: none !important; background-color: green !important;}

#footer
{}


}
