css:
 @namespace url(http://www.w3.org/1999/xhtml);
 
@-moz-document domain("gruporigel.org") {
 
body {
background:#fff !important ;
color:#000000 !important;
border:0px solid #fff !important;
text-decoration:none !important;
font-weight: 400 !important;
}
 
font{
font-size:9pt !important;
font-weight: normal !important;
color:#000 !important;
}
 
h1, h2, h3 {
font-weight: normal !important;
color:#000 !important;
}
 
a{
font-size:9pt !important;
font-weight: normal !important;
color:#000 !important;
 
}
 
a img{
  width: 12px !important;
  height: 12px !important;
}
a img:hover{
  width: auto !important;
  height: auto !important;
}
 
div#main .title{
  font-size:9pt !important;
  font-weight: normal !important;
  color:#000 !important;
  font-weight: bold !important;
}
 
div#main .tabs, h2.title, #site-name{
  display: none !important;
}
 
table{
 border: none;
}
 
img[src^="/sites/all/themes/zeropoint/logo.png"], div#footer-wrapper, div.by{
 display: none !important;
}
 

object:hover, embed:hover{
width: auto !important;
height: auto !important;
}
 
a:hover {
color:#000 !important ;
background-color: #fff !important ;
}
 
a:visited {
color:#000 !important;
text-decoration:none !important;
font-weight: 400 !important;
}
 
div.page#top_bg, div#top_right, div#top_left, div#body_right, div#brand, div#headimg, ul.links a, h3.title{
background: none !important;
}
 
.block.themed-block, .block-user .block-icon  {
background: none !important;
}
 
body.bicons32 .block-comment .block-icon, body.bicons32 .comments-block-icon .block-icon, body.bicons32 #block-views-comments_recent-block .block-icon {
background: none !important;
}
 
body.picons .terms li.first, body.picons .terms ul.taxonomy {
background: none !important;
margin:10px 0;
padding-left:20px;
}
 
a, a:link, .title, .title a, .node .terms, #aggregator .news-item .categories, #aggregator .source, #aggregator .age, #forum td .name, div.admin-panel .description {
color:#000 !important;
}
 
li{
     list-style-image:none !important;
     list-style-type: none !important;
}
div.outer {
  margin-right: 0px !important;
}
div.colmain, div.float-wrap{
   float:left !important;
   width:70% !important;
}
 
/*   Efecto persiana lateral en menu*/
div.colright{
   width: 24px !important;
   float: left !important;
   width: auto !important;
}
div.colright:hover div.colright:hover .menu{
   width:500px!important;
   float: left !important;
   width: 30% !important;
}
div.colright div[class^="block-wrapper odd"] a,
div.colright div[class^="block-wrapper even"] a,
div.colright div[class^="block-wrapper even"] li,
div.colright div[class^="block-wrapper odd"] div.content,
div.colright div[class^="block-wrapper odd"] div.content div.item-list h3{
   color:#fff !important;
   width:20px!important;
}
 
div.colright:hover div[class^="block-wrapper odd"] a,
div.colright:hover div[class^="block-wrapper even"] a,
div.colright:hover div[class^="block-wrapper even"] li,
div.colright:hover div[class^="block-wrapper odd"] div.content,
div.colright:hover div[class^="block-wrapper odd"] div.content div.item-list h3{
    color:#000 !important;
    width:20%!important;
}
 

}
