css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("boards.4chan.org") {

    html,body {
        background-image:url('http://static.dyp.im/LM051Ia8GK/d7c4cd5526e57ee47c044e505f993a96.jpg') !important;
        background-repeat:no-repeat !important;
        background-attachment:fixed !important;
        background-size:cover !important;
        font-family:"/*[[fontfamily]]*/", Times, serif !important;
        font-size:/*[[fontsize]]*/ !important;
    }
    
    hr, .center {
        display:none !important;
    }
    
    td {
        border:0 !important;
        background:rgba(255,255,255,0) !important;
    }
    
    input, textarea {
       background:rgba(255,255,255,0.8) !important;
    }
    
    .extended-small .thread {
        width:165px !important;
    }
       
    .reply:target, .reply.highlight, .reply.highlight-anti {
        background:rgba(162, 232, 255, 1) !important;
        border:0 !important;
    }
    
    .reply.preview, .op.preview {
        background:rgba(162, 232, 255, 0.9) !important;
        width:auto !important;
    }
    
    .pagelist.desktop, .reply{
        border:0 !important;
        background:rgba(255,255,255,0.3) !important;
    }
    
    .extPanel.reply {
        width:auto !important;
    }
    
    .reply {
        width:40% !important;
    }
    
    .opContainer {
        display:table !important;
        width:41.5% !important;
    }
}
