/*[[set_style]]*/

style_setting_options.value:
/* mouse over slide in and out */

#navigator-toolbox:not([customizing]) ~ #browser-bottombox {
position:fixed;
bottom:0;
left:0;
}

#browser-bottombox {margin-left:2px!important;}

#FindToolbar {
border:none!important;
border-top:#a3a3a3 solid 1px!important;
}

#FindToolbar .findbar-container {
margin-bottom:-2px!important;
margin-left:0px!important;
border-radius:0 0px 0 0!important;
background:-moz-linear-gradient(rgba(255,255,255,.8), rgba(200,200,200,.8))!important;
box-shadow:0 0 0 0px rgba(0,0,0,.0) inset, 0 0 0 0px rgba(255,255,255,.0) inset;
}

#FindToolbar .find-status-icon[status="notfound"] + .findbar-find-status, #FindToolbar .find-status-icon[status="wrapped"] + .findbar-find-status {
padding-right:5px!important;
}

#FindToolbar .find-status-icon[status="notfound"], #FindToolbar .find-status-icon[status="wrapped"] { margin-left:-1px!important;
}

#FindToolbar .find-status-icon:not([status="notfound"]):not([status="wrapped"]) + .findbar-find-status {
display:none!important;
}

#addonbar-closebutton {
display:none!important;
}

#addon-bar {
padding:0!important;
margin:0!important;
border-radius:0!important;
border:0!important;
position:absolute!important; /* lose it */
}

#addon-bar > *,#addon-bar > * > *,#addon-bar > * > * > * {
border:0!important;
margin:0!important;
padding:0!important;
border-radius:0!important;
}

#addon-bar > * {
border-top:#a3a3a3 solid 1px!important;
border-left:#a3a3a3 solid 1px!important;
border-right:#a3a3a3 solid 1px!important;
background-image:-moz-linear-gradient(rgba(255,255,255,.8), rgba(200,200,200,.8))!important;
position:fixed!important;
right:17px!important;
bottom:-10px!important;
opacity:0!important;
-moz-transition:bottom 0.25s 0.5s ease-in, opacity 0.1s 0.65s ease-in, right 0.01s 0.75s!important;
padding-left:5px!important;
padding-right:5px!important;
min-height:26px!important;
}

#addon-bar > * > * {
padding:0 2px!important;
vertical-align:middle!important;
margin-top:2px!important;
}

#addon-bar > * > * > * {background-color:transparent!important;}

#addon-bar > *:hover {
opacity:1!important;
bottom:0px!important;
-moz-transition:bottom .25s .25s ease-out, opacity 0.1s 0.25s ease-out!important;
}

#status-bar > * {height:22px!important;}

#main-window[sizemode="maximized"] #FindToolbar, 
#main-window[sizemode="fullscreen"] #FindToolbar {
border-right:1px solid #a3a3a3!important;
}

#main-window[sizemode="maximized"] #addon-bar > * {
right:16px!important;
}

#main-window[sizemode="fullscreen"] #addon-bar > * {
right:16px!important;
}

