css:
body {
    background-attachment: fixed;
    background-color: #000000;
    background-image: url("http://img406.imageshack.us/img406/1677/glowbg.jpg");
    background-position: center center;
    background-repeat:repeat-y;
    color: #FFFFFF;
    font-family: arial,sans-serif;
    font-size: 12px;
    margin: 0;
    padding: 0 1em;
    text-shadow: 0 0 0.2em #0094FF, 0 0 0.2em #0094FF, 0 0 0.2em #0094FF;
}
.body {
    background-color: #000000;
    line-height: 1.5em;
    margin: 0;
    opacity: 0.75;
    overflow: hidden;
    padding: 0.5em;
    text-shadow: 0 0 0.2em #0094FF, 0 0 0.2em #0094FF, 0 0 0.2em #0094FF;
    white-space: normal;
    word-wrap: break-word;
}
.body a {
    color: #FFFFFF;
}
.body img {
    border: medium none;
    float: left;
    margin-bottom: 6px;
    margin-right: 7px;
}
.body ul.menu {
    clear: both;
    margin: 0.5em 0 0;
    text-align: right;
}
.body ul.menu li {
    margin: 0 0 0 0.5em;
}
.codebox {
    color: #8B8B8B;
    display: block;
    font: 0.9em/1.3em monaco,"andale mono","courier new",courier,mono;
    height: auto;
    margin: 3px 0;
    max-height: 250px;
    overflow: auto;
    padding-top: 5px;
    white-space: normal;
}
span.quote {
    color: #58FAF4;
}
.highlighted {
}
.spoiler {
    background-color: #000000;
}
.spoiler:hover {
    color: #FFFFFF;
}
.border {
    background-color: #FFEED9;
    border: 3px dashed #FCE1CE;
    margin: 1em;
    padding: 0.5em;
}
.highlight {
    background-color: #FFFF99;
    font-style: normal;
    font-weight: 700;
}
a {
    color: #FFFFFF;
}
#notice {
    background-color: #000000;
    border: 2px solid #000000;
    padding: 0.5em;
}
h1 {
    font-size: 17px;
    text-align: center;
}
h1 a {
    color: #FFFFFF;
    text-decoration: none;
}
h2 {
    font-size: 16px;
    text-shadow: 0 0 0.2em #0094FF, 0 0 0.2em #0094FF, 0 0 0.2em #0094FF;
}
h2 a:hover {
    text-decoration: underline;
}
h3 {
    background-color: #000000;
    font-size: 12px;
    font-weight: 400;
    margin: 0;
    opacity: 0.9;
    padding: 0.5em;
}
.reply_id {
    float: right;
    text-align: right;
    vertical-align: middle;
}
h4.section {
    font-size: 15px;
    margin-bottom: 7px;
}
h4.user {
    font-size: 14px;
    margin: 0 0 0.5em;
}
label.common {
    float: left;
    padding-right: 0.5em;
    text-align: right;
    width: 15em;
}
input, textarea {
    display: block;
}
textarea {
    font-family: monospace;
    width: 100%;
}
fieldset {
    margin: 1em 0;
}
ul.body, ul.error_message {
    list-style-type: none;
}
ul.error_message {
    margin: 0;
    padding: 0;
}
ul.error_message li {
    font-style: italic;
    padding: 0.2em;
}
ul.menu {
    margin: 1em;
    text-align: center;
}
ul.menu li {
    display: inline;
    font-weight: 700;
    margin: 0 1em;
}
ul#main_menu {
    margin: 0 0 1em;
    opacity: 0.9;
    padding: 0.5em;
}
ul.stuff {
    margin-bottom: 2em;
}
ul.stuff li {
    margin-bottom: 0.5em;
}
table {
    border-collapse: collapse;
    margin-bottom: 1em;
    width: 100%;
}
table th {
    background-color: #000000;
    opacity: 0.9;
    padding: 0.5em;
    text-align: left;
}
table td {
    background-color: #000000;
    opacity: 0.8;
    padding: 0.5em;
    vertical-align: top;
}
table tr.odd td {
    background-color: #000000;
    opacity: 0.75;
}
table tr:hover td {
    background-color: #000000;
    opacity: 0.9;
}
table .minimal {
    white-space: nowrap;
    width: 1%;
}
.profile_fix {
    overflow: hidden;
    white-space: normal;
    word-wrap: break-word;
}
.reply_body_snippet, .snippet {
    font-style: italic;
}
.topic_headline a {
    font-weight: bold;
    text-decoration: none;
}
.topic_headline a:visited {
    font-weight: bold;
    text-decoration: none;
}
.topic_info {
    cursor: default;
    float: right;
}
em.marked {
    background-color: #FFFF99;
    font-style: normal;
    font-weight: 700;
}
p.caption {
    font-size: 11px;
    margin-top: 2px;
}
.inline {
    display: inline;
}
#last_seen_marker {
    border-top: 2px solid #FFFFFF;
}
#snapback_link {
    left: 2px;
    position: fixed;
    top: 50%;
}
.video, video, a.video {
    clear: both;
    margin: 10px 0;
}
.standalone, .row {
    margin-bottom: 1em;
}
.body ul.menu li, .body ul.menu li a, .body ul.menu li span, .unimportant, .unimportant * {
    font-size: 10px;
}
a:hover, h2 a, #snapback_link a {
    text-decoration: none;
}
a.youtube_alternate, .noscreen {
    display: none;
}
h3#preview, h3#error, h3#replying_to, label, legend, .topic_headline a, .reply_body_snippet a, .snippet a {
    font-weight: 700;
}
.help, .help_cursor {
    cursor: help;
}
