@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

/*Designed by Jose Contreras aka xjcontrerasx Under Creative Commons License. Some rights reserved. 2010 */

/* ribbon tweak */

#titlebar #appmenu-button-container {margin-bottom:0px!important;position:relative!important;z-index:3!important;}
#tab-view-deck {position:relative!important;margin-top:-22px!important;margin-left:2px!important;}
#navigator-toolbox[tabsontop="true"] #TabsToolbar {margin-left: 102px !important;position:relative!important;z-index:1!important;}
#titlebar-spacer {height:0px!important;position:relative!important;z-index:0!important;}
#tabbrowser-tabs{margin-left: 6px!important;margin-right:111px!important;}
#navigator-toolbox:hover #tabbrowser-tabs {margin-right:60px!important;}

/* mouseover nav bar for menu bar animated */

#toolbar-menubar {
-moz-transition:height 0.50s ease-in, height 0.50s ease;
height:0px!important;
margin-left:110px!important;
}

#alltabs-button,#tabview-button,#new-tab-button {display:none!important;}

#navigator-toolbox:hover #toolbar-menubar {
-moz-transition:height 0.50s ease-out, height 0.50s ease;
height:19px!important;
margin-left:110px!important;
position:relative!important;
z-index:3!important;
}

#navigator-toolbox:hover #alltabs-button,#navigator-toolbox:hover #tabview-button,#navigator-toolbox:hover #new-tab-button {display:block!important;}
