css:
@-moz-document domain('facebook.com'){

.home, #facebook{background: #F0F3F9 fixed repeat center top !important;margin-top: -10px !important; }

html, body, #nonfooter, #booklet, .UIFullPage_Container, .profile{ margin-top: 10px; background: url("http://www.tkd.net/images/white.jpg") #ffffff fixed repeat center top!important; !important; }

#headNav {border: 0px !important; background-color:  rgba(59,89,152,.5) !important;}

.jewel {height: 31px !important; margin: 0px !important; width: 23px !important; border: none !important; margin-right: 1px !important;}

.hovercard .stage {background: fixed repeat left center, rgba(240,243,249,.7) !important;}

#pageHead {background: url("http://oi52.tinypic.com/2kg489.jpg") fixed repeat-x top right !important; height: 35px; position: fixed !important; width: 976px !important; top: -10px !important;left: 0 !important; right: 0 !important;margin: 0 auto !important;}

#content {margin-top: 43px !important;}

#headNav {margin-left: 176px !important;}

#navSearch {margin-top: 3px !important;}

#pageHead {padding-left: 6px; background: rgb(59,89,152); !important; z-index: 999999 !important; }


#inputtext {margin-top: -16px !important; background: rgb(59,89,152) !important;}

#leftCol{margin-top:2px; background: transparent !important; background-repeat: no-repeat !important ;}

a, .UIActionButton_Text {text-shadow: none !important;}

.UIComposer_InputArea * {text size: 2px; text-shadow: none !important;}

#profile_name{text-shadow: white 0px 0px 0px, white 1px 1px 1px;}
.gigaboxx_thread_hidden_messages, .uiMenu, .itemAnchor, .gigaboxx_thread_branch_message  {filter:alpha(opacity=8);  -moz-opacity:0.8;  -khtml-opacity: 0.8;  opacity: 0.8;}

.fbNubFlyoutTitlebar, .uiTypeaheadView, .chat_window, .chat_header, .bottom_tray {background: rgb(59,89,152)  fixed repeat left center !important;} 

 .rNubContainer {background: rgba(233,233,233) repeat-x 0px 0px !important;
}

}
