css:
/* ==Techmeme - Simple & Clean with Google News Style== */
@-moz-document domain("techmeme.com"){

body {
background: #fff !important;
}

a {
color: #11C !important;
text-decoration: none !important;
background: none !important;
}
a:hover {
color: #11C !important;
text-decoration: underline !important;
}

.nmpad {
padding: 0.5em 0 !important;
}

.padl {
padding-left: 0 !important;
}

.pagecont {
margin: 0 auto !important;
max-width: 90% !important;
border: none !important;
}

.mainpad, .bcp {
background-image: none !important;
}

.mainpad {
width: 69% !important;
padding-right: 0 !important;
}

.rpan {
width: 30% !important;
float: right !important;
}

.bcp div[align="right"] {
margin: -15px 0 0 0 !important;
padding: 0 !important;
float: right !important;
position: absolute !important;
right: 32.4%;
}

input[name="q"] {
padding: 4px 7px 0 3px !important;
height: 26px !important;
width: 240px !important;
border: 1px solid #CCC !important;
border-bottom-color: #999 !important;
border-right: none !important;
vertical-align: top !important;
font-size: 18px !important;
line-height: 26px !important;
}
input[name="q"]:focus {
border: 2px solid #6694E3 !important;
outline: none !important;
height: 24px !important;
width: 237px !important;
}

input[value="Search"] {
cursor: pointer !important;
height: 32px !important;
border: 1px solid #CCC !important;
border-bottom-color: #999 !important;
font-size: 18px !important;
padding: 4px 10px !important;
margin-left: -4px !important;
background: -webkit-gradient(linear,0% 40%,0% 70%,from(#F9F9F9),to(#E3E3E3)) !important;
border: 1px solid #BBB !important;
border-top-color: #CCC !important;
border-bottom-color: #A0A0A0 !important;
}
input[value="Search"]:hover {
border-color: #939393 !important;
}
input[value="Search"]:focus, input[value="Search"]:active {
background: -webkit-gradient(linear,0% 40%,0% 70%,from(#EEE),to(#F9F9F9)) !important;
border-color: #444 !important;
}

.rnhang {
margin-right: 0 !important;
padding-left: 1em !important;
background: #EBEFF9 !important;
color: #000 !important;
}
#preflink {
margin: -22px 0 0 -180px !important;
float: left !important;
}
#preflink a {
color: #4272DB !important;
}

.rnhd1, .rnhd3 {
display: none !important;
}
.rnhdbak {
letter-spacing: 0 !important;
padding: 4px !important;
background: #EBEFF9 !important;
}
.rnhd2 {
color: #000 !important;
font-style: normal !important;
background-image: none !important;
}

.nornbody {
padding: 0 !important;
}
.clus {
border: 2px solid transparent !important;
border-bottom: 1px solid #C9D7F1 !important;
padding: 4px 8px 4px 4px !important;
}
.clus:hover {
border: 2px solid #C9D7F1 !important;
padding-top: 5px !important;
padding-bottom: 3px !important;
margin-top: -1px !important;
}

.clus cite {
color: #767676 !important;
}
.clus cite a {
font-weight: normal !important;
color: #4272DB !important;
padding-right: 2px !important;
}
.clus .item {
padding: 0.1em 0 !important;
}
.sharebutton {
height: 16px !important;
width: 0px !important;
padding-right: 16px !important;
background: transparent url(data:image/png;base64,%3D) 100% 3px !important;
}

/* share button */
.bdlight, .bddark, .shareboxcontent {
border-radius: 3px !important;
-webkit-border-radius: 3px !important;
-moz-border-radius: 3px !important;
}
.shareboxcontent {
background: #EBEFF9 !important;
}
.bdlight a {
color: #4272DB !important;
}
td.shareonhead, td.permalinkhead {
background: #228822 !important;
}
img[src="/img/twitter_icon16.gif"] {
min-width: 0px !important;
width: 0px !important;
padding-right: 16px !important;
background: transparent url(data:image/x-icon;base64,%3D%3D) 100% !important;
}

.clus {
min-height: 100px !important;
}

.clus .L1 a, .clus .L2 a, .clus .L3 a, .clus .L4 a, .clus .L5 a, .clus .L6 a, .clus .L7 a, .clus .L8 a, .clus .L9 a, .clus .L10 a {
text-decoration: underline !important;
}

span.mls a {
color: #282 !important;
}
.drhed, .mls{
color: #767676 !important;
}
.drhed {
margin-left: 16px !important;
}
.show {
margin: 3px 0 0 3px !important;
display: block !important;
border: none !important;
}
.show a {
font-size: 0px !important;
height: 0 !important;
width: 0 !important;
margin: 0 !important;
padding: 0 10px 10px 0 !important;
color: transparent !important;
}
.show a[href*="true"] {
background: transparent url(data:image/png;base64,%3D) 100% !important;
}
.show a[href*="false"] {
background: transparent url(data:image/png;base64,%3D) 100% !important;
}
.show a:hover {
text-decoration: none !important;
}

.lnkr {
margin-left: 16px !important;
}
.lnkr a, .lnkr cite a {
color: #282 !important;
}
.lnkr cite, .lnkr cite a {
font-weight: normal !important;
}
.shr {
margin-left: 16px !important;
}
.shr a {
color: #282 !important;
font-weight: normal !important;
border: none !important;
}

.relhed {
margin-top: 3px !important;
}
.relhed, .relitems {
margin-left: 16px !important;
border-left: 6px solid #EBEFF9 !important;
}
.relhed .drhed, .relitems .item, .relitems .heditem {
margin-left: 4px !important;
}
.relitems .heditem {
padding: 3px 0 !important;
}

img.ill, img.hill {
padding: 0 !important;
margin: 0.5em 0 0.5em 1em !important;
max-width: 84px !important;
max-height: 84px !important;
-webkit-box-shadow: 1px 1px 2px rgba(0,0,0,0.1) !important;
}

span.recent, span.new {
color: #ff0000 !important;
font-style: normal !important;

}
span.recent:before, span.new:before {
content:url(data:image/png;base64,%3D%3D) !important;
padding-right: 2px !important;
}


/* Sidebar */
.padlr {
padding: 0 0 !important;
}
.col0rn .rnbody {
background: transparent !important;
padding: 0 !important;
}
.heditem {
padding: 4px !important;
}
.heditem .ago {
font-style: normal !important;
}
.heditem cite {
font-weight: normal !important;
color: #767676 !important;
}
.heditem a.nfdl {
font-weight: normal !important;
text-decoration: underline !important;
}
.heditem a[href*="#a"] {
font-weight: normal !important;
color: #282 !important;
}
.heditem a[href*="#a"]:after {
content: " Â»" !important;
}
#show_more_new a, #more_new a[href*="hmn()"], #more_new a[href="http://www.techmeme.com/river"] {
font-weight: normal !important;
color: #282 !important;
padding: 4px !important;
}
.rnftbak {
background-image: none!important;
}
.col1rn .rnbody {
background: transparent !important;
padding: 4px !important;
}
.snh {
padding: 4px !important;
}
.snh a {
font-weight: normal !important;
}
form[name="arbr"] {
padding: 4px !important;
}

/* More Items & Earlier Items */
.halfcol {
width: 49% !important;
float: right !important;
}

.clearfloats + .halfcol {
float: left !important;
margin-right: 12px !important;
}

/* xpromo */
.xpromo {
margin: 2em 0 !important;
background: #EBEFF9 !important;
border-top: 1px solid #C9D7F1 !important;
}

/* Link Search */
div[id*="cts"] {
margin-left: -13px !important;
}
div[id*="cts"] .drhed:before {
content:url(data:image/png;base64,%3D%3D) !important;
padding-right: 2px !important;
}

/* About */
.aboutrn .rnbody {
background: #FFC !important;
padding: 4px !important;
}

/* Preferences */
#prefbox {
border: 1px solid #C9D7F1 !important;
background: #EBEFF9 !important;
}

/* Sponsor */
.rsp, .sponrn, .sp {
display: none !important;
}

/* Techmeme River */
.bcp {
padding: 0 !important;
}
.pagecont > div[style="float:right;"] {
margin-top: -1em !important;
}
.daybody {
padding: 0 !important;
margin-bottom: 1em !important;
}
.daybody .heditem {
padding: ;
}
.daybody .heditem:hover {
background: #FFC !important;
}
.daybody .heditem .rtime {
padding: 4px 10px 2px 2px !important;
}
.blinks {
margin: 2em 0 !important;
background: #EBEFF9 !important;
border-top: 1px solid #C9D7F1 !important;
}

/* Techmeme Leaderboard */
.pagecont .bcp {
padding: 0 !important;
margin-bottom: 2em !important;
}
.bcp h1 {
margin: 0.5em 0 0 0 !important;
}
.bcp .col0rn .rnbody {
padding: 4px !important;
}
.bcp .maincol {
background: none !important;
width: 69% !important;
padding: 0 12px 0 0 !important;
}
.bcp .rightcol {
width: 30% !important;
float: right !important;
}
.bcp .maincol table {
width: 100% !important;
border-bottom:1px solid #EBEFF9 !important;
}
.bcp .maincol table tr.lbh {
background: #EBEFF9 !important;
font-style: normal !important;
color: #000 !important;
}
.bcp .maincol table tr.lbh td {
padding: 0.35em 1em !important;
}
.bcp .maincol table tr.lbh td.rlbh {
background: none !important;
}
.bcp .maincol table tr td {
padding: 0.35em 1em !important;
border: none !important;
border-bottom: 1px solid #EBEFF9 !important;
}
.bcp .maincol table tr:hover td {
background: #FFC !important;
}
.bcp .maincol table tr.lbh:hover td {
background: none !important;
}

/* Search */
.bcp .pagetop {
padding: 0 0 0.5em 0 !important;
}
.bcp .pagetop input[name="q"], .bcp .pagetop input[name="q"]:focus {
height: 32px !important;
width: 390px !important;
}
td.searchcell form {
margin: 2em 0 0 208px !important;
}
#advlink {
margin-left: -60px !important;
padding: 55px 0 0 0 !important;
}
.linkplaceholder {
margin-left: 60px !important;
}
.col0rn .rnhdrslt {
background: #EBEFF9 !important;
padding: 4px !important;
letter-spacing: 0 !important;
text-transform: none !important;
}
.rnhd2 b {
color: #282 !important;
}
.bcp .maincol .item, .bcp .maincol .ii {
padding: 4px 8px 4px 4px !important;
}
.bcp .maincol .ii {
padding-bottom: 8px !important;
border-bottom: 1px solid #EBEFF9 !important;
}
.bcp .maincol .ii + br {
display: none !important;
}
.bcp .maincol .item cite {
color: #767676 !important;
font-weight: normal !important;
}
.bcp .maincol .itemdate, .bcp .maincol .incontext {
color: #282 !important;
font-weight: normal !important;
}
.bcp .maincol .prevnext {
font-size: 18px !important;
}
.bcp .maincol .prevnext .go, .bcp .maincol .prevnext .go:hover, .bcp .maincol .prevnext .stay, .bcp .maincol .prevnext .stay:hover {
border: none !important;
}
.bcp .maincol div[align="center"] form {
display: none !important;
}
#advancedbox .rnhdbak, #advancedbox .rnftbaktop {
display: none !important;
}
#advancedbox .rnbodyadvbox {
padding: 1em 1em 1.5em 1em !important;
background: #EBEFF9 !important;
border-top: 1px solid #C9D7F1 !important;
border-bottom: 1px solid #C9D7F1 !important;
}
.bcp .maincol .ii .L1 a {
text-decoration: underline !important;
}
.bcp .maincol .item cite a {
color: #4272DB !important;
}
}
